INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('en', 'route_lines', '<img alt=\"\" class=\"content-block\" src=\"images/316/route-2.jpg\" />\r\n<table class=\"table tour-table-rounded\">\r\n	<tbody>\r\n		<tr>\r\n			<th style=\"width:8%;\">Days</th>\r\n			<th style=\"width:14%;\">Waypoint</th>\r\n			<th style=\"width:8%;\">Time</th>\r\n			<th>Subjects of itinerary</th>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 1</td>\r\n			<td rowspan=\"2\" style=\"text-align: center;\">Tashkent</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p>Arrival Tashkent, accommodation at hotel. Car Rental procedures.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Lunch.</strong><br />\r\n			<strong><em>Sightseeing tour in Tashkent:</em></strong> Kukeldash Madrassah, Kaffal Shashi Mausoleum, Barak Khan Madrassah, Mosque on Khasty Imam Street, Visit to Chor-Su – oriental market (handicraft bazaar).<br />\r\n			<strong>Dinner.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 2</td>\r\n			<td rowspan=\"2\" style=\"text-align: center;\">Tashkent –<br />\r\n			Sentab</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Breakfast.</strong><br />\r\n			Drive to Sentab village 310 km. Arrival and accommodation at homestay.<br />\r\n			<strong>Lunch. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p>Sightseeing of the village<br />\r\n			<strong>Dinner (included).</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 3</td>\r\n			<td rowspan=\"2\" style=\"text-align: center;\">Sentab -<br />\r\n			Bukhara</td>\r\n			<td>&nbsp;</td>\r\n			<td>\r\n			<p><strong>Breakfast.</strong><br />\r\n			Short trekking in Nurata Mountain Reserve.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n			<td>\r\n			<p><strong>Lunch pick nick (</strong><strong>included). </strong><br />\r\n			Drive to Bukhara via Nurata town. Visit ruins of Alexander the Great fortress and Chashma Mosque. En route visit petroglyphs.<br />\r\n			Arrival to Bukhara in the evening.<br />\r\n			<strong>Distance 270 km</strong><br />\r\n			<strong>Dinner.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 4</td>\r\n			<td rowspan=\"2\" style=\"text-align: center;\">Bukhara</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Breakfast.</strong><br />\r\n			<strong><em>Sightseeing tour in Bukhara:</em></strong> Poi-Kalyan Ensemble including Minaret (12c) Mosque (15c), functioning Madrassah Miri-Arab (16 c), Emir-Alim-Khan Madrassah (14c), Taqi (trading domes), Kukeldash Madrassah (16c), Nadir Divanbegi (17c), Magok-i-Attari Mosque(16c), Lyab-i-Hauz (17c),</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Lunch.</strong><br />\r\n			<strong><em>Sightseeing tour in Bukhara</em></strong>: Visit to Registan, Ark Fortress (16 c) and Bolo-Hauz complex (18 c). Ulug Beg (15c), Abdul Aziz (17c) Madrassahs; Chor-Minor Mausoleums. Ismael Samani mausoleum (9-10c)-vault of Bukhara rulers dynasty, Chashma-Ayub Mausoleums (14 c)<br />\r\n			<strong>Dinner. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 5</td>\r\n			<td rowspan=\"2\" style=\"text-align: center;\">Bukhara -<br />\r\n			Baysun</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Breakfast </strong><br />\r\n			Drive to Baysun.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Lunch.</strong><br />\r\n			Arrival to Baysun, accommodation at homestay. Sightseeing of Baysun, market and workshops.<br />\r\n			<strong>Distance 340 km</strong><br />\r\n			<strong>Dinner (included)</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 6</td>\r\n			<td rowspan=\"2\" style=\"text-align: center;\">Baysun -<br />\r\n			Termez</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong><br />\r\n			Sightseeing of Baysun surroundings. Drive to Termez.&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Lunch. </strong>&nbsp;<br />\r\n			Arrival and accommodation.<br />\r\n			<strong><em>Sightseeing tour of Termez:</em></strong> Kokildor-Ota Khanaka, Fortress Kirk-Kiz kala – a summer residence of Samani rulers, Sultan Saodat Ensemble, and Zurmala – Buddha\'s stupa. Continue to Buddhist archaeological sites of Fayaz-tepe and Mausoleum of Al-Hakim at-Termezi.<br />\r\n			<strong>Dinner.</strong><br />\r\n			<strong>Distance 170 km</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 7</td>\r\n			<td rowspan=\"2\" style=\"text-align: center;\">Termez –<br />\r\n			Shahrisabz – Samarkand</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Breakfast.</strong><br />\r\n			Drive to Samarkand via Shahrisabz.<br />\r\n			<strong><em>Sightseeing tour of Shahrisabz:</em></strong> Ak -Sarai Palace (14-15c); Dorut Tilovat: Kok - Gumbaz Mosque (15 c);</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Lunch.</strong><br />\r\n			<strong><em>Sightseeing tour of Shahrisabz:</em></strong> Dorus Siadat: the Jekhangir Mausoleum (14c), Tamerlane\'s vault (15c).<br />\r\n			Continue to Samarkand. Arrival and accommodation at hotel.<br />\r\n			<strong>Distance 360 km</strong><br />\r\n			<strong>Dinner.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 8</td>\r\n			<td rowspan=\"2\" style=\"text-align: center;\">Samarkand</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong><br />\r\n			<strong><em>Sightseeing tour in Samarkand:</em></strong> Gur-Emir Mausoleum (15c), Registan Ensemble (including Ulugbek (15c), Shir Dor (17c), Tillya –Kari Madrassahs (17c), Bibi Khanum Mosque. Visit to Samarkand bazaar.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Lunch.</strong><br />\r\n			<strong><em>Sightseeing tour in Samarkand:</em></strong> Shah-i-Zinda necropolis (14-15c), “Stars above