INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('it', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n   <tbody>\r\n      <tr>\r\n         <th style=\"width:8%;\">Giorni</th>\r\n         <th style=\"width:14%;\">Tappe</th>\r\n         <th>Soggetti dell’itinerario</th>\r\n      </tr>\r\n      <tr>\r\n         <td>GIORNO 1</td>\r\n         <td>\r\n         <h3>Tashkent - Chimgan/Charvak</h3>\r\n         </td>\r\n         <td>\r\n         <p>Partenza da Tashkent per i monti Chimgan, arrivo e tempo libero libero.</p>\r\n\r\n         <p><strong>Pranzo </strong></p>\r\n\r\n         <p>Tempo libero a disposizione. Inoltre possibile: prendere una via cavo, equitazione, trekking.<br />\r\n         Trasferimento a Charvak, arrivo e sistemazione.<br />\r\n         <strong>Cena. </strong></p>\r\n         </td>\r\n      </tr>\r\n      <tr>\r\n         <td>GIORNO 2</td>\r\n         <td>\r\n         <h3>Chimgan/Charvak - Tashkent</h3>\r\n         </td>\r\n         <td>\r\n         <p><strong>Colazione </strong><br />\r\n         Tempo libero a disposizione, nuoto.</p>\r\n\r\n         <p><strong>Pranzo </strong><br />\r\n         Rientro a Tashkent.<br />\r\n         <strong>Cena </strong></p>\r\n         </td>\r\n      </tr>\r\n   </tbody>\r\n</table>\r\n', '16', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('en', 'route_lines', '                           <table class=\"table tour-table-rounded\">\r\n                                 <tr>\r\n                                     <th style=\"width:8%;\">Days</th>\r\n                                     <th style=\"width:14%;\">Waypoint</th>\r\n                                     <th>Subjects of itinerary</th>\r\n                                 </tr>\r\n<tr>\r\n<td>DAY 1</td>\r\n<td>\r\n<h3>Tashkent - Samarkand</h3>\r\n</td>\r\n<td valign=\"top\">\r\n<p>Transfer to Train Station. Depart Tashkent at 07:00 by \"Registan\" train. Arrival to Samarkand, at 11.30 accommodation in hotel.</p>\r\n<p><strong>Lunch</strong><br /> <strong><em>Sightseeing tour in Samarkand:</em></strong> <a class=\"in_text\" title=\"Gur-Emir Mausoleum\" href=\"[[~776]]\">Gur-Emir Mausoleum</a> (15c), <a class=\"in_text\" title=\"Registan Ensemble\" href=\"[[~697]]\">Registan Ensemble</a> (including Ulugbek (15c), <a class=\"in_text\" title=\"Shir Dor\" href=\"[[~765]]\">Shir Dor</a> (17c), <a class=\"in_text\" title=\"Tillya–Kari Madrassahs\" href=\"[[~768]]\">Tillya–Kari Madrassahs</a> (17c), <a class=\"in_text\" title=\"Bibi Khanum Mosque\" href=\"[[~775]]\">Bibi Khanum Mosque</a>. Visit to Samarkand bazaar.<br /> <strong>Dinner </strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>DAY 2</td>\r\n<td>\r\n<h3>Samarqand - Tashkent</h3>\r\n</td>\r\n<td valign=\"top\">\r\n<p><strong>Breakfast </strong><br /> <strong><em>Sightseeing tour in Samarqand:</em></strong> Mausoleum of Imam Al-Bukhoriy, <a class=\"in_text\" title=\"Shah-i-Zinda necropolis\" href=\"[[~764]]\">Shah-i-Zinda necropolis</a> (14-15c), “Stars above Samarqand”: tour to <a class=\"in_text\" title=\"Ulugbek observatory\" href=\"[[~754]]\">Ulugbek observatory</a> (15c),</p>\r\n<p><strong>Lunch</strong><br /> Free at leisure time. <br /> Transfer to Samarkand Train Station, train \"Registan\" to Tashkent at 17.00 arrival at 20.30.</p>\r\n<p><strong><em>End of our service…</em></strong></p>\r\n</td>\r\n</tr>\r\n</table>', '17', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('de', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n			<th style=\"width:10%;\">TAGE</td>\r\n			<th style=\"width:14%;\">REISEROUTE</td>\r\n			<th style=\"width:8%;\">ZEIT</td>\r\n			<th>THEMEN DER REISEROUTE</td>\r\n		</tr>\r\n<tr>\r\n<td rowspan=\"2\">1. TAG</td>\r\n<td rowspan=\"2\">\r\n<h3>Taschkent - Samarkand</h3>\r\n</td>\r\n<td>\r\n<p>Morgen</p>\r\n</td>\r\n<td>\r\n<p>Transfer zum Bahnhof. Abreise aus Taschkent um 07:00 mit \"Registan\" Zug. Ankunft in Samarkand um 11.30, Unterkunft im Hotel.</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p>NachmitTAG</p>\r\n</td>\r\n<td>\r\n<p><strong>Mittagessen</strong><br /> <strong><em>Stadtbesichtigung in Samarkand:</em></strong> Gur-Emir Mausoleum (15 Jhd.), Registan Ensemble (einschließlich Ulugbek (15 Jhd.), Shir Dor (17 Jhd.), Tillya–Kari Madrassahs (17 Jhd.), Bibi Khanum Moschee. Besuch zum Samarkand Basar.<br /> <strong>Abendessen </strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"2\">2. TAG</td>\r\n<td rowspan=\"2\">\r\n<h3>Samarkand - Taschkent</h3>\r\n</td>\r\n<td>\r\n<p>Morgen</p>\r\n</td>\r\n<td>\r\n<p><strong>Frühstück</strong><br /> <strong><em>Stadtbesichtigung in Samarkand:</em></strong> Mausoleum von Imam Al-Bukhoriy, Shah-i-Zinda Nekropole (14-15 Jhd.), \"Sterne über Samarkand\": Fahrt zum Ulugbek Observatorium (15 Jhd.),</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p>NachmitTAG</p>\r\n</td>\r\n<td>\r\n<p><strong>Mittagessen</strong><br /> Zeit zur freien Verfügung. <br /> Transfer zum Samarkand Bahnhof, Fahrt mit dem Zug \"Registan\" nach Taschkent um 17.00. Ankunft um 20.30.</p>\r\n<p><strong><em>Ende unserer Leistungen…</em></strong></p>\r\n</td>\r\n</tr>\r\n</table>', '17', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('es', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n			<th style=\"width:10%;\"><strong>DÍAS</strong></th>\r\n			<th style=\"width:10%;\"><strong>TRAYECTO</strong></th>\r\n			<th style=\"width:14%;\"><strong>HORA</strong></th>\r\n			<th><strong>ITINERARIO</strong></th>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DÍA 1</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Tashkent - Samarkanda</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>AM</strong></p>\r\n			</td>\r\n			<td>\r\n			<p>Traslado a la estación de Tren. Salida a Samarkanda con el tren “Reguistan” a las 07.00. Llegada a Samarkanda, a las 11.30</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>PM</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>Comida</strong><br />\r\n			<strong><em>Visitas en Samarkanda:</em></strong> Mausoleo Gur-Emir (XV), El complejo de la Plaza de Registan: Madrazas Ulugbek (XV), Sher Dor (XVII), Tillya –Kari (XVII); Mezquita Bibi Khanum ( dedicado a la esposa de Tamerlán), Mercado Siyab (Mercado de especies, frutas secas, dulces, panes y etc..<br />\r\n			<strong>Cena</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DÍA 2</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Samarqanda - Tashkent</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>AM</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>Desayuno</strong><br />\r\n			<strong><em>Visitas en Samarqanda:</em></strong> Mausoleo de Imam Al-Bukhoriy, necrópolis Shah-i-Zinda (s.14-15), \"Estrellas sobre Samarkanda\": visita al observatorio de Ulugbek (s.15)</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>PM</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>Comida</strong><br />\r\n			Tiempo libre.<br />\r\n			Traslado a la estación de tren de Samarkanda, tomar el tren “Reguistán” a las 17.00. Llegada a Tashkent a las 20.30</p>\r\n\r\n			<p><strong><em>Fin de nuestros servicios…</em></strong></p>\r\n			</td>\r\n		</tr>\r\n</table>				', '17', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('fr', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n			<th style=\"width:10%;\">JOURS</td>\r\n			<th style=\"width:14%;\">TRAJET</td>\r\n			<th>ITINERAIRE</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">JOUR 1</td>\r\n			<td rowspan=\"2\">\r\n			<h3 style=\"text-align: center;\">Tachkent - Samarcande</h3>\r\n			</td>\r\n			<td>\r\n			<p>Transfert à la gare et départ pour Samarcande à 07.00 en train «Afrosiyob». Arrivée à 09.10. Accueil et installation à l\'hôtel. Temps libre.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Déjeuner.</strong><br />\r\n			<strong><em>Visite de Samarcande: </em></strong> mausolée Gour-Emir (15C), Registan Ensemble (y compris Oulougbek (15C), Shir Dor (de 17c), Tillia -Kari medersas (17c), la mosquée Bibi Khanoum, visite de bazar orientale ‘‘Siab’’.<br />\r\n			<strong>Dîner</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">JOUR 2</td>\r\n			<td rowspan=\"2\">\r\n			<h3 style=\"text-align: center;\">Samarcande - Tachkent</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner.</strong><br />\r\n			<strong><em>Poursuite de la visite de Samarcande:</em></strong> mausolée d’Imam Al Boukhorii, nécropole Shah-i-Zinda (14-15c), \"Etoiles au-dessus Samarcande\" visite de l\'observatoire d’Oulougbek (15c), «Ruines d\'Afrasyab\" réserve archéologique d’état (6 BC-13 AD)</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Déjeuner.</strong><br />\r\n			Temps libre.<br />\r\n			Transfert à la gare et départ pour Tachkent à 17.00. Arrivée à 19.10</p>\r\n\r\n			<p><strong><em>Fin de nos services…</em></strong></p>\r\n			</td>\r\n		</tr>\r\n</table>\r\n\r\n\r\n', '17', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('it', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n   <tbody>\r\n      <tr>\r\n         <th style=\"width:8%;\">Giorni</th>\r\n         <th style=\"width:14%;\">Tappe</th>\r\n         <th>Soggetti dell’itinerario</th>\r\n      </tr>\r\n      <tr>\r\n         <td>GIORNO 1</td>\r\n         <td>\r\n         <h3>Tashkent - Samarcanda</h3>\r\n         </td>\r\n         <td valign=\"top\">\r\n         <p>Trasferimento alla stazione ferroviaria. Parti da Tashkent alle 07:00 con il treno \"Registan\". Arrivo a Samarcanda, alle 11.30 sistemazione in albergo.</p>\r\n\r\n         <p><strong>Pranzo</strong><br />\r\n         <strong><em>Giro turistico a Samarcanda:</em></strong> Mausoleo Gur-Emir (15c), Registan Ensemble (incluso Ulugbek (15c), Shir Dor (17c), Madrasse Tillya–Kari (17c), Moschea Bibi Khanum Visita al bazar di Samarcanda.<br />\r\n         <strong>Cena </strong></p>\r\n         </td>\r\n      </tr>\r\n      <tr>\r\n         <td>GIORNO 2</td>\r\n         <td>\r\n         <h3>Samarcanda - Tashkent</h3>\r\n         </td>\r\n         <td valign=\"top\">\r\n         <p><strong>Colazione. </strong><br />\r\n         <strong><em>Giro turistico a Samarcanda:</em></strong> Mausoleo dell\'Imam Al-Bukhoriy, necropoli di Shah-i-Zinda (14-15c), “Stelle sopra Samarcanda”: visita all\'osservatorio di Ulugbek (15c)</p>\r\n\r\n         <p><strong>Pranzo</strong><br />\r\n         Tempo libero a disposizione.<br />\r\n         Trasferimento alla stazione ferroviaria di Samarcanda, treno \"Registan\" per Tashkent alle 17.00 arrivo alle 20.30.</p>\r\n\r\n         <p><strong><em>Fine dei nostri servizi …</em></strong></p>\r\n         </td>\r\n      </tr>\r\n   </tbody>\r\n</table>\r\n', '17', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('it', 'route_lines', '<table class=\"table tour-table-rounded\" height=\"494\" width=\"835\">\r\n	<tbody>\r\n		<tr>\r\n			<th style=\"width:8%;\">Giorni</th>\r\n			<th style=\"width:14%;\">Tappe</th>\r\n			<th>Soggetti dell’itinerario</th>\r\n		</tr>\r\n		<tr>\r\n			<td>GIORNO 1</td>\r\n			<td>\r\n			<h3>&nbsp;Nukus -<br />\r\n			Altopiano Ustyurt -<br />\r\n			Lago d’Aral</h3>\r\n			</td>\r\n			<td>\r\n			<p>Iniziamo il nostro viaggio avventuroso nel Mar d\'Aral su auto SUV 4x4 (Toyota Land Cruiser) dal tuo hotel a Nukus / aeroporto al tuo arrivo da Tashkent. Lungo il percorso visiterai il complesso architettonico del XIV secolo \"Mizdakhan\" e le rovine della fortezza \"Gaur kala\" di epoca zoroastriana (IV-III a.C.). Faremo diverse soste: al lago Sudochie salire sulla collina che era usata come faro, visitare il villaggio di pescatori abbandonato Ugra.</p>\r\n\r\n			<p><strong>Pranzo nel villaggio di Ugra</strong><br />\r\n			Nel pomeriggio continuare a guidare ai margini dell\'altopiano di Ustyurt. Lungo il percorso visitiamo l\'insediamento di Kubla Ustyurt - una piccola città costruita in epoca sovietica nel mezzo dell\'altopiano di Ustyrt, lì possiamo assaggiare il latte fresco di cammello. Arrivo al mare d\'Aral, sistemazione nel campo di yurte.<br />\r\n			Tempo libero, scopri i dintorni in autonomia, nuotando in mare.<br />\r\n			<strong>Cena fatta sul fuoco.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>GIORNO 2</td>\r\n			<td>\r\n			<h3>Lago d’Aral - Muynak - Nukus</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Colazione.</strong><br />\r\n			Visita la fortezza nascosta di Kurgancha Kala (XII-XIV), centro commerciale fortificato della Grande Via della Seta nel periodo altomedievale e cimitero dei nomadi del XIII secolo. Guidi a Muynak (ex città di pescatori). La strada passerà attraverso il fondo secco del Lago d\'Aral, vedremo e scatteremo foto dei Grandi Canyon dell\'altopiano di Ustyurt. A Muynak visiteremo il cimitero della nave, museo di storia locale.</p>\r\n\r\n			<p><strong>Pranzo a Muynak a casa della famiglia locale.<br />\r\n			Continua a guidare verso Nukus. Arrivo nel tardo pomeriggio.</strong></p>\r\n\r\n			<p><strong><em><strong>Fine dei nostri servizi...</strong></em></strong></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '18', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('de', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n	<tbody>\r\n		<tr>\r\n			<th style=\"width:10%;\">TAGE</th>\r\n			<th style=\"width:14%;\">REISEROUTE</th>\r\n			<th>THEMEN DER REISEROUTE</th>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">1. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Nukus</h3>\r\n			</td>\r\n			<td>\r\n			<p>Ankunft in Nukus. Beförderung und Unterkunft im Hotel.<br />\r\n			Zeit zur freien Verfügung.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen</strong><br />\r\n			<strong><em>Stadtrundfahrt in Nukus: </em></strong>Besuch zum Igor Savitskiy Museum der russischen Avantgarde, zum Stadtbasar und zum Hauptplatz.<br />\r\n			<strong>Selbstgestaltetes Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">2. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Nukus - Aral See</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstück</strong><br />\r\n			Beförderung zum Aralsee um 06.00. Straßenfahrt durch Ustjurt Plateau, unterwegs Besuch zu den Ruinen des antiken Leuchtturms und zu den Süddeutschen See.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen unterwegs</strong><br />\r\n			Anreise zum Aralsee Küstenlinie, Zeltlager und Übernachtung.<strong>&nbsp; </strong>Zeit zur freien Verfügung, Besichtigungstouren und Schwimmen.<br />\r\n			<strong>Abendessen rund ums Feuer</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">3. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Aral See - Moynak - Nukus</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstück</strong><br />\r\n			Transfer nach Mojnak, durch den Boden des getrockneten Meers. Ankunft in Mojnak.<br />\r\n			<strong><em>Stadtrundfahrt in Mojnak:</em></strong> Schiffsfriedhof, Heimatkundemuseum und Stadtrundfahrt.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen unterwegs</strong><br />\r\n			Ankunft in Nukus. Beförderung und Unterkunft im Hotel.<br />\r\n			<strong>Selbstgestaltetes Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '18', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('es', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n			<th style=\"width:10%;\">DÍAS</td>\r\n			<th style=\"width:14%;\">TRAYECTO</td>\r\n			<th>ITINERARIO</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DÍA 1</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Nukus</h3>\r\n			</td>\r\n			<td>\r\n			<p>Llegada a Nukus, traslado y alojamiento en hotel</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Comida. </strong><br />\r\n			<strong><em>Visita en Nukus:</em></strong> Museo de vanguardia rusa de Igor Savitskiy, Mercado de la ciudad, visita por la ciudad.<br />\r\n			<strong>Comida libre.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DÍA 2</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Nukus - Mar Aral</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Desayuno temprano.</strong><br />\r\n			Traslado al Mar de Aral, a las 06.00. Carretera pasará por la meseta Ustyurt y en el camino visitaremos las ruinas de la antigua casa de la luz y el lago Sudochie.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Almuerzo en la ruta. (lunch box)</strong><br />\r\n			Llegada a la costa del mar de Aral, instalar el campamento y alojarse en &nbsp;la tienda de campaña. Tiempo libre: visita por los alrededores, natación, etc.<br />\r\n			<strong>Cena alrededor de la hoguera.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DÍA 3</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Mar Aral - Moynak - Nukus</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Desayuno temprano.</strong><br />\r\n			Traslado a Muynak, por el fondo del mar seco. Llegada a Muynak.<br />\r\n			<strong><em>Recorrido por la ciudad de Muynak:</em></strong> cementerio de barcos, museo de la tradición local y visita de la ciudad</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Almuerzo en la ruta </strong><br />\r\n			Llegada a Nukus y alojamiento en hotel.<br />\r\n			<strong>Dinner on self arrangement.</strong></p>\r\n			</td>\r\n		</tr>\r\n</table>\r\n\r\n\r\n', '18', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('fr', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n	<tbody>\r\n		<tr>\r\n			<th style=\"width:10%;\">JOURS</th>\r\n			<th style=\"width:14%;\">TRAJET</th>\r\n			<th>ITINERAIRE</th>\r\n		</tr>\r\n		<tr>\r\n			<td>JOUR 1</td>\r\n			<td>\r\n			<h3>Noukous</h3>\r\n			</td>\r\n			<td>\r\n			<p>Accueil à l\'aéroport de Noukous. Transfert et installation à l\'hôtel.</p>\r\n\r\n			<p><strong>Déjeuner libre</strong><br />\r\n			<strong><em>Visite de Noukous: </em></strong>la visite du musée d’Etat des Arts Savitsky où on prenne la connaissance de la collection de l’avant-garde russe, l’une des plus grandes collections de beaux-arts du peuple de l’Asie Centrale, place principal, marché de la ville.<br />\r\n			<strong><strong>Dîner libre</strong></strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>JOUR 2</td>\r\n			<td>\r\n			<h3>Noukous –</h3>\r\n\r\n			<h3>la mer d’Aral</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner</strong><br />\r\n			Transfert vers la mer d’Aral&nbsp;à 06.00. La route passe via le plateau d’Ousturte. Sur la route visite des ruines de vielles maison de la lumière et du lac Soudotchie.</p>\r\n\r\n			<p><strong>Déjeuner en route (lunch box)</strong><br />\r\n			Arrivée au bord de la mer d’Aral, on dresse les tentes.Temps libre.</p>\r\n\r\n			<p><strong>Diner</strong> autour du feu.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>JOUR 3</td>\r\n			<td>\r\n			<h3>La mer d’Aral - Mouinak - Noukous</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner de bon matin</strong><br />\r\n			Transfert à Mouinak via le fond séché de la mer d’Aral. Arrivée et visite de Mouinak : le cimetière des navires, le musée local, auto tour en ville.</p>\r\n\r\n			<p><strong>Déjeuner en route</strong><br />\r\n			Arrivée à Noukous et installation à l\'hôtel.</p>\r\n\r\n			<p><strong><strong>&nbsp;<em>Fin de nos services...</em></strong></strong></p>\r\n\r\n			<p><strong><strong><em>&nbsp;</em></strong></strong></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '18', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('ru', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n			<th style=\"width:10%;\">День</td>\r\n			<th style=\"width:14%;\">Маршрут</td>\r\n			<th style=\"width:8%;\">Время</td>\r\n			<th>Программа</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">День 1</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Нукус</h3>\r\n			</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p>Прибытие в Нукус, трансфер и размещение в гостинице.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p>Туристы по-своему усмотрению могут посетить музей им Игоря Савицкого, экспозицию художественных полотен русского авангарда, городской рынок, главная площадь.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">День 2</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Нукус - Аральское море</h3>\r\n			</td>\r\n			<td>АМ</td>\r\n			<td>\r\n			<p><strong>Ранний завтрак.</strong><br />\r\n			Отправление на Аральское море на внедорожнике, путь пролегает по плату Устюрт. Посещение озера Судочи, руины старого маяка и поселения.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>РМ</td>\r\n			<td>\r\n			<p><strong>Обед в дороге.</strong><br />\r\n			Прибытие и размещение на берегу существующего берега Аральского моря, разбивка лагеря и установка палаток.<br />\r\n			<strong>Ужин вокруг костра.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">День 3</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Аральское море – Муйнак – Нукус</h3>\r\n			</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Завтрак</strong><br />\r\n			Отправление в г.Муйнак по высохшему дну Аральского моря мимо газовых и нефтяных вышек. Прибытие в Муйнак, посещение кладбища кораблей, короткая экскурсия по городу.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Обед в дороге.</strong><br />\r\n			Отправление в Нукус. Прибытие и размещение в гостинице.</p>\r\n			</td>\r\n		</tr>\r\n</table>', '18', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('en', 'route_lines', '<table class=\"table tour-table-rounded\" height=\"494\" width=\"835\">\r\n	<tbody>\r\n		<tr>\r\n			<th style=\"width:8%;\">Days</th>\r\n			<th style=\"width:14%;\">Waypoint</th>\r\n			<th>Subjects of itinerary</th>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 1</td>\r\n			<td>\r\n			<h3>&nbsp;Nukus -<br />\r\n			Plateau Ustyurt -<br />\r\n			Aral Sea</h3>\r\n			</td>\r\n			<td>\r\n			<p>We start our Aral Sea adventure trip on SUV 4x4 cars (Toyota Land Cruiser) from your hotel in Nukus / airport upon your arrival from Tashkent. En route you’ll visit the architectural complex of the XIV century “Mizdakhan” and the ruins of “Gaur kala”&nbsp;fortress of Zoroastrian epoch (IV-III B.C.). We’ll have several stops: at the lake Sudochie climb to the hill which was used as Light house, visit abandoned fisherman village Ugra.</p>\r\n\r\n			<p><strong>Lunch</strong><strong> in Ugra village. </strong><br />\r\n			In the afternoon continue driving on the edge of <a class=\"in_text\" href=\"[[~610]]\" title=\"Ustyurt Plateau\">Ustyurt Plateau</a>. En route we visit Kubla Ustyurt settlement – a small city built in Soviet times in the middle of Ustyrt Plateau, there we could taste fresh camel milk. Arrival to Aral Sea seaside, accommodation in yurt camp.<br />\r\n			Free at leisure time, discover the surroundings on self-arrangement, swimming in the sea.<br />\r\n			<strong>Dinner</strong><strong> made on fire</strong><strong>.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 2</td>\r\n			<td>\r\n			<h3>Aral Sea - Muynak - Nukus</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast.</strong><br />\r\n			Visit the hidden fortress of Kurgancha Kala (XII-XIV), fortified trading center of the Great Silk Road in early medieval period and nomad’s cemetery of 13 century. Drive to&nbsp;<a class=\"in_text\" href=\"[[~647]]\" title=\"Muynak\">Muynak</a><span class=\"in_text\"> </span>(former fisherman town).&nbsp;The road will pass via dried bottom of the Aral Sea, we’ll see &amp; take pictures of Great Canyons of &nbsp;<a class=\"in_text\" href=\"[[~610]]\" title=\"Ustyurt Plateau\">Ustyurt Plateau.</a> In Muynak we\'ll visit the cemetery of ship, local history museum.</p>\r\n\r\n			<p><strong>Lunch in Muynak at the local family</strong>\'<strong>s house</strong><strong> </strong><br />\r\n			Continue driving to Nukus. Arrival late afternoon.</p>\r\n\r\n			<p><em><strong>End of our service...</strong></em></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '18', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('en', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n            <th style=\"width:8%;\">Days</th>\r\n            <th style=\"width:14%;\">Waypoint</th>\r\n            <th>Subjects of itinerary</th>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 1</td>\r\n			<td>\r\n			<h3>Almaty</h3>\r\n			</td>\r\n			<td>\r\n			<p>Arrival to Almaty, transfer to hotel and accommodation.<br />\r\n			<strong>Sightseeing tour in Almaty:</strong> Panfilovtzev Park, Zenkov Cathedral, Green Bazaar, Museum of Musical Instruments, Republican Square, Monument of Independence, Central historical Museum,<br />\r\n			Transfer to Medeo skating-rink, the largest sky open skating-rink in the world. Walking tour.</p>\r\n\r\n			<p><strong>Lunch in \"Kazakh Aul\", </strong>restaurant on Medeo with show.<br />\r\n			After lunch transfer to Kok-Tobe mountain where TV-tower is located.<br />\r\n			Transfer to the railway station for the train to Сhimkent at 17.36.<br />\r\n			<strong>Dinner</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 2</td>\r\n			<td>\r\n			<h3>Chimkent</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong><br />\r\n			Arrival to Chimkent at 07.20, transfer to hotel and accommodation. Transfer to The legendary Kazygurt Mount for sightseeing.<br />\r\n			Transfer back to Chimkent for lunch.</p>\r\n\r\n			<p><strong>Lunch.</strong><br />\r\n			Visit to underground mosque.<br />\r\n			Transfer to Turkestan, en route visit Arystan Bab Mausoleum (12th c.), Museum and archeological site of Otrar.<br />\r\n			Arrival to Turkestan. Visit to Mausoleum of Khodja Ahmad Yassavi (14th c.), Archeological &amp;Ethnography Museum, Friday Mosque (19th c.), Semi – Underground Mosque (12th -15th cc.), Mausoleum of Rabiga Sultan Begim (15th c.)<br />\r\n			<strong>Dinner. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 3</td>\r\n			<td>\r\n			<h3>Chimkent - Toretam</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast </strong><br />\r\n			Transfer to Train Station to catch train to Toretam at 07.18.</p>\r\n\r\n			<p><strong>Lunch.</strong><br />\r\n			Arrival to Toretam Train Station at 18.15.<br />\r\n			Transfer to the hotel and accommodation.<br />\r\n			Free time<br />\r\n			<strong>Dinner.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 4</td>\r\n			<td>\r\n			<h3>Toretam - Baikonur</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast. </strong><br />\r\n			Transfer to Baikonur.<br />\r\n			<strong><em>Sightseeing tour of Baikonur</em>:</strong> visit museum of histories of the cosmodrome Baikonur, Bus excursion of city on away visit to: gardens, monuments, Gagarinskaya summerhouse.</p>\r\n\r\n			<p><strong>Lunch.</strong><br />\r\n			Visit International cosmic school<br />\r\n			<strong>Dinner. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 5</td>\r\n			<td>\r\n			<h3>Baikonur</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast </strong><br />\r\n			Transfer to pl. 2 visit memorial lodges of S.P.KOROLEV, and Y.A.GAGARIN, orbital nave \"Buran\". Transfer to pl. 254 reports spaceman Gagarinskiy start pl. 1, transfer on pl.112, erection-test complex pl.112</p>\r\n\r\n			<p><strong>Lunch.</strong><br />\r\n			Visit to Start complex \'Gagarinskiy start.<br />\r\n			Transfer back to city<br />\r\n			<strong>Dinner. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 6</td>\r\n			<td>\r\n			<h3>Baikonur</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast on train.</strong><br />\r\n			Transfer to Kyzyl-Orda Airport by coach(3 h) for flight to Almaty at 11.15.<br />\r\n			Arrival to Almaty at 13.40, transfer and accommodation in hotel.</p>\r\n\r\n			<p><strong>Lunch.</strong><br />\r\n			Free at leisure time.<br />\r\n			Transfer to Almaty International Airport, flight back home.</p>\r\n\r\n			<p><strong><em>End of our services</em> ... </strong></p>\r\n			</td>\r\n		</tr>\r\n\r\n</table>', '19', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('de', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n			<th style=\"width:10%;\">TAGE</td>\r\n			<th style=\"width:14%;\">REISEROUTE</td>\r\n			<th>THEMEN DER REISEROUTE</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">1. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Almaty</h3>\r\n			</td>\r\n			<td>\r\n			<p>Ankunft in Almaty, Beförderung zum Hotel und Unterbringung.<br />\r\n			<strong><em>Stadtbesichtigung in Almaty:</em></strong> Der Panfilowtzew Park, die Senkow Kathedrale, Der grüne Basar, Das Museum der musikalischen Instrumenten, der Republikplatz, das Denkmal der Unabhängigkeit, Das zentrale historische Museum.<br />\r\n			Ausflug zur Medeu-Eisbahn, die gröβte Eisbahn der Welt unter freiem Himmel. Wandertour.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen in \"Kasakh Aul\", </strong>das Restaurant am Medeo mit Show.<br />\r\n			Nach dem Mittagessen werden Sie zum Kok-Tobe Berg befördert, wo sich der Fernsehturm befindet.<br />\r\n			Beförderung zum Bahnhof. Zug nach Tschimkent um 17.36.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">2. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Tschimkent</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck</strong><br />\r\n			Ankuft in Tschimkent um 07.20, Beförderung zum Hotel und Unterbringung. Beförderung zum legendären Kazygurt Berg für Stadtbesichtigung.<br />\r\n			Rückkehr nach Tschimkent für Mittagessen.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen</strong><br />\r\n			Besuch zur unterirdische Moschee.<br />\r\n			Beförderung nach Turkestan, unterwegs besuchen wir das Arystan Bab Mausoleum (12.Jh), das Museum und den archäologischen Platz von Otrar.<br />\r\n			Ankunft in Turkestan. Bezuch zum Mausoleum von Khodja Ahmad Yassavi (14.Jh), zum archäologischen und ethnographischen Museum, zur Freitag Moschee (19Jh), zur Halb-unterirdische Moschee (12.Jh -15.Jh), zum Mausoleum von Rabiga Sultan Begim (15.Jh)<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">3. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Tschimkent - Toretam</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck</strong><br />\r\n			Beförderung zum Bahnhof und Einstieg auf den Toretam Zug um 07.18.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen im Zug</strong><br />\r\n			Ankuft in Toretam Bahnhof um 18.15.<br />\r\n			Beförderung zum Hotel und Unterbringung.<br />\r\n			Zeit zur freien Verfügung.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">4. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Toretam - Baikonur</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast. </strong>&nbsp;<br />\r\n			Beförderung nach Baikonur.<br />\r\n			<strong><em>Stadtbesichtigung in Baikonur:</em></strong> Besuch zum Museum der Geschichte baikonurischen Kosmodrom, Busausflug zur Stadt: Gärten, Denkmäler, das Gagarinskaya Sommerhaus.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen</strong><br />\r\n			Besuch zur Internationale kosmische Schule<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">5. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Baikonur</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck</strong><br />\r\n			Beförderung zur Transfer to pl. 2 visit memorial lodges of S.P.KOROLEV, and Y.A.GAGARIN, orbital nave \"Buran\". Transfer to pl. 254 reports spaceman Gagarinskiy start pl. 1, transfer on pl.112, erection-test complex pl.112</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen</strong><br />\r\n			Besuch zum \"Gagarinskiy Start\" Komplex<br />\r\n			Rückkehr zur Stadt<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">6. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Baikonur</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck im Zug</strong><br />\r\n			Beförderung zum Kyzyl-Orda Flughafen mit dem Bus (3 Std.) und Flug nach Almaty um 11.15.<br />\r\n			Ankunft in Almaty um 13.40, Beförderung und Unterbringung im Hotel.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen</strong><br />\r\n			Zeit zur freien Verfügung.<br />\r\n			Beförderung zum Internationalen Flughafen Almaty.<br />\r\n			<br />\r\n			<strong><em>Ende unserer Leistung …</em></strong></p>\r\n			</td>\r\n		</tr>\r\n</table>', '19', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('es', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n			<th style=\"width:10%;\">DÍAS</td>\r\n			<th style=\"width:14%;\">TRAYECTO</td>\r\n			<th>ITINERARIO</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DÍA 1</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Almaty</h3>\r\n			</td>\r\n			<td>\r\n			<p>LLegada a Almaty, traslado y alojamiento en hotel.<br />\r\n			<strong>Visitas en Almaty: </strong> Parque de Panfilovtzev, la catedral Zenkov, Bazar Verde, Museo de Instrumentos Musicales, Plaza de la Republica, Monumento de la Independencia, Museo Central Histórico.<br />\r\n			Traslado a pista de patinaje Medeo, pista de patinaje más grande al aire libre en el mundo</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Comida en restaurante \"Kazakh Aul\", </strong>con shou.<br />\r\n			Despues de la comida traslado a Kok-Tobe, donde está situada la torre televisiva.<br />\r\n			Traslado a la estación de trenes con destino a Chimkent 17.36.<br />\r\n			<strong>Cena</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DÍA 2</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Chimkent</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Desayuno</strong><br />\r\n			LLegada a Chimkent a las 07.20, traslado y alojamiento en hotel. Visita al legendario monte Kazygurt.<br />\r\n			Traslado a Chimkent para comer.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Comida.</strong><br />\r\n			Visita a mezquita subterranea. Traslado a Turkestan, en el camino visita al mausoleo Arystan Bab (s.12), Museo y sitio arqueologico en Otrar.<br />\r\n			Llegada a Turkestan. Visita al Mausoleo de Khodja Ahmad Yassavi (14th c.), Museo arqueológico y etnográfico, Mesquita de viernes (s.19), mezquita semisubterraneo (SS.12-15), Mausoleo de Rabiga Sultan Begim (s.15)<br />\r\n			<strong>Cena.</strong>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DÍA 3</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Chimkent - Toretam</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Desayuno </strong><br />\r\n			Traslado a la estación de tren para tomar el tren con destino a Toretam a las 07.18</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Comida.</strong><br />\r\n			Llegada a la estación de tren de Toretam a las 18.15.<br />\r\n			Traslado y alojamiento en hotel.<br />\r\n			Tiempo libre.<br />\r\n			<strong>Cena.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DÍA 4</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Toretam - Baikonur</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Desayuno. </strong>&nbsp;<br />\r\n			Traslado a Baikonur.<br />\r\n			<strong><em>Visitas en Baikonur: </em></strong> visita al museo de historia del cosmódromo de Baikanur, excursion en el autobus con visitas a los jardines, monumentos, casa de campo Gagarinskaya.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Comida.</strong><br />\r\n			Visita a la escuela Internacional de cósmos<br />\r\n			<strong>Cena.&nbsp; </strong>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DÍA 5</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Baikonur</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Desayuno </strong><br />\r\n			Traslado pl. 2 visita al monumento conmemorativo a S.P.KOROLEV, y a Y.A.GAGARIN, nave espacial \"Buran\". Traslado a pl. 254 informes de Gagarin sobre el primer lanzamiento pl. 1, traslado a pl.112, complejo pl.112</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Comida.</strong><br />\r\n			Visita al comlejo de lanzamiento de naves Gagarinskiy start.<br />\r\n			<strong>Cena. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DÍA 6</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Baikonur</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Desayuno en el tren. </strong><br />\r\n			Traslado al aeropuerto de Kyzyl-Orda: tomar el vuelo hacia Almaty a las 11.15<br />\r\n			Llegada a Almaty a las 13.40, traslado y alojamiento en hotel</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Comida.</strong><br />\r\n			Tiempo libre.<br />\r\n			Traslado al aeropuerto Internacional de Almaty, vuelo al destino.</p>\r\n\r\n			<p><strong><em>Fin de servicios ... </em></strong></p>\r\n			</td>\r\n		</tr>\r\n</table>', '19', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('fr', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n			<th style=\"width:10%;\">JOURS</td>\r\n			<th style=\"width:14%;\">TRAJET</td>\r\n			<th>ITINERAIRE</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 1</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Almaty</h3>\r\n			</td>\r\n			<td>\r\n			<p>Arrivée à Almaty, transfert à l\'hôtel et l\'hébergement.<br />\r\n			<strong>Visite d\'Almaty:</strong> Panfilovtzev Park,Cathédrale Zenkov, Vert bazar, Musée des Instruments de Musique, Place de Republique, Monument de l\'Indépendance, Musée centrale historique.<br />\r\n			Transfert à Medeo patinoire, la plus grande patinoire à ciel ouvert dans le monde.</p>\r\n\r\n			<p><strong>Déjeuner</strong> dans\"Kazakh Aoul\", restaurant sur Medeo avec spectacle. Après le déjeuner, transfert à la montagne Kok-Tobe où tour de télévision se trouve. Transfert à la gare pour le train à Chimkent à 17.36<br />\r\n			<strong>Dîner</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 2</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Chimkent</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner</strong><br />\r\n			Arrivée à Chimkent à 07.20, transfert à l\'hôtel et l\'hébergement. Transfert à la légendaire Mont Kazygourte, visite.<br />\r\n			Retour à Chimkent pour le déjeuner.</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			Visite de la mosquée souterraine.<br />\r\n			Transfert àTurkestan, en route visite Mausolée Arystan Bab (12 c.), Musée et site archéologique d’Otrar.<br />\r\n			Arrivée au Turkestan. Visite du mausolée de Khodja Ahmad Yassavi(14 c.), Musée de l’Archéologie et de l\'Ethnographie, Mosquée du Vendredi, Semi(19 c.) - de la Mosquée souterrain Khilovat (12 -15cc.), Le mausolée de Sultan Rabiga Begim (15 c.)<br />\r\n			<strong>Dîner</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 3</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Chimkent - Toretam</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner</strong><br />\r\n			Transfert à la gare pour prendre le train à Toretam à 07.18.</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			Arrivée à Toretam à 18h15.<br />\r\n			Transfert à l\'hôtel et l\'hébergement.<br />\r\n			Temps libre<br />\r\n			<strong>Dîner</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 4</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Toretam - Baïkonour</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner</strong><br />\r\n			Transfert à Baïkonour.<br />\r\n			<strong><em>Visite de Baïkonour:</em></strong> visite de musée d\'histoires du cosmodrome de Baïkonour, excursion en bus : les jardins, monuments, Gagarinskaya pavillon.</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			Visite de l\'école internationale cosmique<br />\r\n			<strong>Dîner</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 5</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Baïkonour</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner</strong><br />\r\n			Transfert à pl. 2 visite de loges commémoratives de KOROLEV S.P.et de GAGARIN Y.A., neforbitale \"Bourane\". Transfert àpl. 254 rapports, Gagarinskiy start pl. 1pl.112 -érection test complexe</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			Visite du Start complexe « Gagarinskiy start ».<br />\r\n			Retour à la ville.<br />\r\n			<strong>Dîner </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 6</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Baïkonour</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner</strong><br />\r\n			Transfert à l\'aéroport de Kyzyl-Orda (253 km, 3 h) pour le vol à Almaty à 11.15.<br />\r\n			Arrivée à Almaty à 13.40, transfert et installation à l\'hôtel.</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			Temps libre.<br />\r\n			Transfert à l\'aéroport international d\'Almaty, vol de retour.</p>\r\n\r\n			<p><strong><em>Fin de nos services... </em></strong></p>\r\n			</td>\r\n		</tr>\r\n</table>\r\n\r\n\r\n', '19', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('en', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n            <th style=\"width:8%;\">Days</th>\r\n            <th style=\"width:14%;\">Waypoint</th>\r\n            <th>Subjects of itinerary</th>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 1</td>\r\n			<td>\r\n			<h3>Astana</h3>\r\n			</td>\r\n			<td>\r\n			<p>Arrival to Astana, transfer to hotel and accommodation.</p>\r\n\r\n			<p><strong>Lunch</strong><br />\r\n			<strong><em>Sightseeing tour in Astana:</em></strong> visit of the Monument of Independence \"Bayterek\", museum of First President and Islamic Cultural Centre. Excursion to ethnopark \"Map of Kazakhstan- Atameken\" representing a unique outdoor museum. Visit to entertainment complex- Duman - place for public recreation featuring an oceanarium.<br />\r\n			Visit Khan Shatyr – Khan Shatyry (\"Royal Marquee\") is a giant transparent tent in Astana.<br />\r\n			<strong>Dinner</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 2</td>\r\n			<td>\r\n			<h3>Astana - Alzhir - Almaty</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong><br />\r\n			Transfer to Alzhir (Memorial of wives of Stalin regime prisoners) 30 km from Astana</p>\r\n\r\n			<p><strong>Lunch.</strong><br />\r\n			Transfer back to Astana Airport, flight to Almaty at 15.40 arrival at 17.20. Transfer to hotel and accommodation.<br />\r\n			Evening city tour, include transfer to Kok-Tobe mountain where TV-tower is located.<br />\r\n			visit to Medeo skating-rink, the largest sky open skating-rink in the world-(panorama from the dumb)<br />\r\n			<strong>Dinner with music show. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 3</td>\r\n			<td>\r\n			<h3>Almaty</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast </strong><br />\r\n			Transfer to Charyn Canyon, visit valley of Castles, unusual rock formations.</p>\r\n\r\n			<p><strong>Lunch.</strong><br />\r\n			Transfer back to Almaty, visit to family of silver jewelry maker. See handmade jewelry. Have some master class of making jewelry.<br />\r\n			<strong>Dinner.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 4</td>\r\n			<td>\r\n			<h3>Almaty</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast. </strong><br />\r\n			Drive to Turgen River Gorge . See the Zailisky Alatau mountains. Drive up to the middle part of the Gorge. Hike to the first Turgen waterfall (40-m high), also called Medvezhiy<br />\r\n			Transfer to the trout farm.</p>\r\n\r\n			<p><strong>Lunch.</strong><br />\r\n			Transfer back to Almaty, arrival and free at leisure time.<br />\r\n			<strong>Dinner. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 5</td>\r\n			<td>\r\n			<h3>Almaty - Chimkent</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Early breakfast </strong><br />\r\n			Transfer Domestic Airport, flight to Chimkent 07.40 arrival at 09.10 transfer to hotel and accommodation. Free at leisure time.</p>\r\n\r\n			<p><strong>Lunch.</strong><br />\r\n			Transfer to Kazygurt Mount-the legendary place is in the spurs of the Talass Alatau (western ridge of the Tien Shan mountains). Visit Underground Mosque, Pottery workshop of local ceramists.<br />\r\n			<strong>Dinner. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 6</td>\r\n			<td>\r\n			<h3>Chimkent - Turkestan</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast on train.</strong><br />\r\n			Free at leisure time. Check-out.</p>\r\n\r\n			<p><strong>Lunch.</strong><br />\r\n			Transfer to Turkestan, on the way visit to Arystan Bob Mausoleum (12th c.), Museum and archeological site of Otrar<br />\r\n			Arrival and accommodation to hotel.<br />\r\n			<strong>Dinner. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 7</td>\r\n			<td>\r\n			<h3>Turkestan</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong><br />\r\n			<strong><em>Sightseeing tour in Turkestan:</em></strong> Visit Mausoleum of Khodja Ahmad Yassavi (14th c.), Archeological &amp;Ethnography Museum, Friday Mosque (19th c.), Semi – Underground Mosque (12th -15th cc.), Mausoleum of Rabiga Sultan Begim (15th c.)</p>\r\n\r\n			<p><strong>Lunch. </strong><br />\r\n			Drive to ruins of ancient city Sauran -46 km from Turkestan. Transfer back to Turkestan.<br />\r\n			Dinner.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 8</td>\r\n			<td>\r\n			<h3>Turkestan - Almaty</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast </strong><br />\r\n			Transfer to Chimkent.</p>\r\n\r\n			<p><strong>Lunch</strong><br />\r\n			Arrival to Chimkent domestic airport, flight to Almaty at 15.20. Arrival to Almaty at 17.20</p>\r\n\r\n			<p><strong><em>End of our services ...</em></strong></p>\r\n			</td>\r\n		</tr>\r\n\r\n</table>', '20', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('de', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n			<th style=\"width:10%;\">TAGE</td>\r\n			<th style=\"width:14%;\">REISEROUTE</td>\r\n			<th>THEMEN DER REISEROUTE</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">1. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Astana</h3>\r\n			</td>\r\n			<td>\r\n			<p>Ankunft in Astana, Beförderung zum Hotel und Unterkunft.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen</strong><br />\r\n			<strong><em>Stadtbesichtigung in Astana</em></strong>: Besuch des \"Bayterek\" Unabhängigkeitsdenkmal, des Museums des Ersten Präsidents und des islamischen Kulturzentrums. Ausflug nach ethnischen Park \"Karte von Kasachstan-Atameken\", der ein einzigartiges Freilichtmuseum repräsentiert. Besuch zum Unterhaltungkomlex-Duman – der Ort für öffentliche Erholung mit einem Ozeanarium.<br />\r\n			Besuch zum Khan Shatyr (\"Das Keiserfestzelt \") der riesige transparente Zelt in Astana.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">2. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Astana - Alzhir - Almaty</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck</strong><br />\r\n			Beförderung zum Alzhir (Denkmal für die Opfer politischer Repressionen und des Totalismus), das 30 km von Astana entfernt ist.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen</strong><br />\r\n			Rückkehr nach Astana Flughafen, Flug nach Almaty um 15.40 Ankunft um 17.20. Beförderung zum Hotel und Unterkunft.<br />\r\n			Abendlicher Stadtrundfahrt, einschliβlich Ausflug nach Kok-Tobe Berg, wo sich Fernsehturm befindet.<br />\r\n			Ausflug zur Medeu-Eisbahn, die gröβte Eisbahn der Welt unter freiem Himmel.<br />\r\n			<strong>Abendessen mit Musikshow. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">3. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Almaty</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck </strong><br />\r\n			Fahrt nach Tscharyn Schlucht, Besichtigung des Tals der Burgen und ungewöhnlichen Felsformationen.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen</strong><br />\r\n			Rückkehr, Besuch der Familie von Juwelieren. Handmade Schmucke. Teilnahme an der Meister-Klasse in der Geschmeide.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">4. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Almaty</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck</strong><br />\r\n			Fahrt nach Turgen Fluss Schlucht. Besichtigung des Sailisky Alatau Gebirge. Fahrt nach mittleren Teil der Schlucht. Wanderung zum ersten Turgen Wasserfall (40-m hoch), der auch <em>Medvezhiy </em>genannt wird.<br />\r\n			Transfer zum Forellezucht.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen.</strong><br />\r\n			Rückkehr nach Almaty. Zeit zur freien Verfügung.<br />\r\n			<strong>Abendessen.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">5. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Almaty - Tschimkent</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck </strong><br />\r\n			Transfer zum Inlandsflughafen, Flug nach Tschimkent um 07.40 Ankunft um 09.10. Beförderung zum Hotel und Unterbringung. Zeit zur freien Verfügung.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen.</strong><br />\r\n			Beförderung zum legendären Kazygurt Berg für Besichtigung.<br />\r\n			Transfer zum Berg Kazygurt - der legendäre Ort in den Ausläufern der Talass Alatau (Westgrat des Tien-Shan-Berge). Besuch zur Besuch zur unterirdische Moschee, die Töpferwerkstatt von lokalen Keramiker.<br />\r\n			<strong>Abendessen. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">6. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Tschimkent - Turkestan</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck im Zug</strong><br />\r\n			Zeit zur Freien Verfügung. Abreisen.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen.</strong><br />\r\n			Transfer nach Turkestan, unterwegs Besichtigung des Arystan Bob Mausoleums (12Jh), des Museums und archäologischen Ort von Otrar<br />\r\n			Ankunft und Unterbringung im Hotel.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">7. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Turkestan</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck</strong></p>\r\n			<strong><em>Stadtbesichtigung in Turkestan:</em></strong> Ausflug zum Mausoleum von Khodja Achmad Yassawi (14Jh), zum archäologischen und ethnographischen Museum, zur Freitag Moschee (19Jh), zur &nbsp;Halb–unterirdische Moschee (12Jh -15Jh), zum Mausoleum von Rabiga Sultan Begim (15Jh)</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen.</strong><br />\r\n			Abfahrt zur Gemäuer von historischer Stadt Sauran, die von Turkistan 46 km entfernt ist. Rückkehr nach Turkestan.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">8. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Turkestan - Almaty</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck </strong><br />\r\n			Transfer nach Schimkent.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen</strong><br />\r\n			Ankunft in inländischen Flughafen Schimkent, Flug nach Almaty um 15.20. Ankunft in Almaty um 17.20<br />\r\n			<br />\r\n			<strong><em>Ende unserer Leistung …</em></strong></p>\r\n			</td>\r\n		</tr>\r\n</table>', '20', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('es', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n			<th style=\"width:10%;\">DÍAS</td>\r\n			<th style=\"width:14%;\">TRAYECTO</td>\r\n			<th>ITINERARIO</td>\r\n		</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 1</td>\r\n<td rowspan=\"2\">\r\n<h3>Astana</h3>\r\n</td>\r\n<td>\r\n<p>Llegada a Astana, traslado y alojamiento en hotel.</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida.</strong><br /> <strong><em>Visitas en Astana:</em></strong> Monumento de  Independencia \"Bayterek\", museo del primer Presidente y Centro de Cultura Islámica. Excursión a etnoparque \"Mapa de Kazajstán-Atameken\" que representa un singular museo al aire libre. Visita al complejo de ocio-Duman - lugar para la recreación pública con un oceanario.<br /> Visita Khan Shatyr - Shatyry Khan (\"Real marquesina\") es una carpa gigante transparente en Astana  <br /> <strong>Cena</strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 2</td>\r\n<td rowspan=\"2\">\r\n<h3>Astana - Alzhir - Alamaty</h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno</strong><br /> Traslado al Alzhir (Memorial de las esposas de los presos del régimen de Stalin) a 30 km de Astana </p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida.</strong><br /> Traslado de regreso al aeropuerto de Astana: tomar el vuelo a Almaty a las 15.40 y llegada a las 17.20. Traslado al hotel y alojamiento. Excursión por la tarde: traslado ala montaña Kok Tobe, donde está sitiuada la Torre televisiva, visita a la pista de patinaje Medeo, la pista de patinaje más grande en el mundo al aire libre.  <br /> <strong>Cena con música   </strong> </p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 3</td>\r\n<td rowspan=\"2\">\r\n<h3>Alamty</h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno</strong><br /> Traslado al Cañón Charyn, visita el valle de Castillos, formación extraña de roca.  </p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida.</strong><br /> Traslado a Almaty, visita a la familia de fabricante de joyas de plata. Ver la joyería hecha a mano. Tendrán alguna clase de maestro de hacer la joyería. <br /> <strong>Cena.</strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 4</td>\r\n<td rowspan=\"2\">\r\n<h3>Almaty</h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno. </strong> <br /> Paseo a Desfiladero del Río Turgen. Ver las montañas Zailisky Alatau. Paseo hasta la parte media del Desfiladero. Excursión a la primera cascada Turgen(de 40 ms de alto), también llamada Medvezhiy.<br /> Traslado a la granja de trucha.</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida.</strong><br /> Traslado a Almaty, llegada y tiempo libre <br /> <strong>Cena.  </strong> </p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 5</td>\r\n<td rowspan=\"2\">\r\n<h3>Almaty - Chimkent</h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno temprano</strong><br /> Traslado al aeropuerto doméstico: vuelo a  Chimkent a las 07.40., llegada a las 09.10, traslado y alojamiento en hotel.<br> Tiempo libre  </p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida.</strong><br /> Traslado al Monte de Kazygurt, el lugar legendario que está en los picos del Talass Alatau (la cadena occidental del Tien Shan). Visita la Mezquita Subterránea, el taller de Cerámica de ceramista local. <br /> <strong>Cena. </strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 6</td>\r\n<td rowspan=\"2\">\r\n<h3>Chimkent - Turkestan</h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno en el tren.</strong> <br /> Tiempo libre. Check-out.</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida.</strong><br /> Traslado a Turkestan, en el camino visita al mausoleo Aristan Bob (s. XII), Museo y campo arqueológico en Otrar. <br />Llegada y alojamiento en hotel.<br /> <strong>Cena. </strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 7</td>\r\n<td rowspan=\"2\">\r\n<h3>Turkestan</h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno</strong> <br /> <strong><em>Visitas en Turkestan: </em></strong> Visita al Mausoleo de Khodja Ahmad Yassavi (s14.), Museo arqueológico y etnográfico, Mesquita de Viernes (s.19.), Mezquita semisubterranea(s.12 -15.), Mausoleo de Rabiga Sultan Begim (s.15.)</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida.</strong><br /> Traslado a la ciudad antigua  Sauran, a 46 km de Turkestan. Regreso a Turkestan.  <br /> Cena.</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA  8</td>\r\n<td rowspan=\"2\">\r\n<h3>Turkestan - Almaty</h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno </strong><br /> Traslado a Chimkent.</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida.</strong><br />Llegada al aeropuerto local de Chimkent, vuelo a Almaty a las 15.20. Llegada a Almaty a las 17.20</p>\r\n<p><strong><em>Fin de nuestros servicios ...</em></strong></p>\r\n</td>\r\n</tr>\r\n</table>', '20', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('fr', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n			<th style=\"width:10%;\">JOURS</td>\r\n			<th style=\"width:14%;\">TRAJET</td>\r\n			<th>ITINERAIRE</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 1</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Astana</h3>\r\n			</td>\r\n			<td>\r\n			<p>Arrivée à Astana, transfert à l\'hôtel et l\'hébergement.</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			<strong><em>Visite de la ville d’Astana :</em></strong> visite du Monument de l\'Indépendance \"Bayterek\", musée de Premier Président et le Centre culturel islamique. Excursion à ethno-parque \"Carte de Kazakhstan- Atameken\" représentant un musée unique en plein air. Visite du complexe récréatif Douman - lieu de récréation public avec un océanarium.<br />\r\n			Visite de Khan Shatyry («Royal Marquee\") est une tente géante transparente à Astana.<br />\r\n			<strong>Dîner</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 2</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Astana - Alzhir - Almaty</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner</strong><br />\r\n			Transfert à Alzhir, Musée-Mémorial des victimes de la répression politique du régime Staline, à 30 km d’Astana</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			Transfert à l\'aéroport d\'Astana, vol à Almaty à 15.40 arrivée à 17h20. Transfert à l\'hôtel et l\'hébergement.<br />\r\n			Visite de la ville de Soirée, comprend le transfert à la montagne Kok-Tobe où tour de télévision se trouve.<br />\r\n			Visite de Medeo patinoire, patinoire à ciel ouvert la plus grandeau monde (panorama depuis le muet)<br />\r\n			<strong>Dîner avec spectacle de musique. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 3</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Almaty</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner </strong><br />\r\n			Transfert à Charyn Canyon, visite vallée des Châteaux, des formations rocheuses insolites.</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			Retour à Almaty, visite de la famille fabricant de bijoux en argent faits à main. Un cours de maitre de la fabrication de bijoux.<br />\r\n			<strong>Dîner</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 4</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Almaty</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner</strong><br />\r\n			Transfert à Tourgen River Gorge. Voir les montagnes Zailisky Alatau. Roulez jusqu\'à la partie médiane de la Gorge. Randonnée à la première cascade Tourgen (40 m de haut), également appelé Medvezhiy<br />\r\n			Transfert à l\'élevage de truites.</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			Retour à Almaty. Temps libre.<br />\r\n			<strong>Dîner </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 5</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Almaty - Chimkent</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner matinal</strong><br />\r\n			Transfert à l\'aéroport domestique, vol pour Chimkent à 07.40 arrivée à 09.10 transfert à l\'hôtel et l\'hébergement. Temps libre.</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			Transfert à Mont Kazygourte - la légendaire place est dans les contreforts de la Talass Alataou (de crête ouest des montagnes Tien Shan). Visite de la mosquée souterraine, atelier de poterie de céramistes locaux.<br />\r\n			<strong>Dîner</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 6</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Chimkent - Turkestan</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner</strong><br />\r\n			Temps libre.</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			Transfert à Turkestan, sur la route visite de Mausolée Bob Arystan (12 c.), Musée et site archéologique d’Otrar<br />\r\n			Arrivée et installation à l\'hôtel.<br />\r\n			<strong>Dîner </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 7</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Turkestan</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner</strong><br />\r\n			<strong><em>Visite de Turkestan : </em></strong> le mausolée de Khodja Ahmad Yassavi (. 14 c), le Musée d’Archéologie et d\'Ethnographie, Mosquée du Vendredi (19 c), de la Mosquée souterrain Khilovat (. 12 -15 cc), le mausolée de Rabiga Sultan Begim (15 c.)</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			Transfert à Saouran - les ruines de l\'ancienne ville à 46 km de Turkestan. Visite.<br />\r\n			Retour à Turkestan.<br />\r\n			Dîner.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 8</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Turkestan - Almaty</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner </strong><br />\r\n			Transfert à Chimkent.</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			Arrivée à l\'aéroport domestique de Chimkent, vol à Almaty à 15.20. Arrivée à Almaty à 17.20</p>\r\n\r\n			<p><strong><em>Fin de nos services...</em></strong></p>\r\n			</td>\r\n		</tr>\r\n</table>\r\n\r\n\r\n', '20', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('en', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n            <th style=\"width:8%;\">Days</th>\r\n            <th style=\"width:14%;\">Waypoint</th>\r\n            <th>Subjects of itinerary</th>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 1</td>\r\n			<td>\r\n			<h3>Almaty</h3>\r\n			</td>\r\n			<td>\r\n			<p>Arrival to Almaty, transfer to hotel and accommodation.<br />\r\n			<strong><em>Sightseeing tour in Almaty:</em></strong> Visit to 28 Panfilovzev Park, Zenkov Cathedral, War Memorial, Museum of Musical Instruments, Glory Alley, Republic Square, and Central Mosque. Transfer outside of Almaty to Medeo stadium, walking tour in Medeo.</p>\r\n\r\n			<p><strong>Lunch</strong><br />\r\n			Transfer back to Almaty, visit to Kok-Tobe using cable-route, walking tour on Kok-Tobe.<br />\r\n			<strong>Dinner</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 2</td>\r\n			<td>\r\n			<h3>Almaty - Lake Issyk - Almaty</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong><br />\r\n			Transfer to lake Issyk on the way visiting Issyk kurgans, where “Golden Man” was founded.</p>\r\n\r\n			<p><strong>Lunch en route.</strong><br />\r\n			Transfer back to Almaty, on the visit wine factory, degustation of wine.<br />\r\n			<strong>Dinner. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 3</td>\r\n			<td>\r\n			<h3>Almaty - Chimkent</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast </strong><br />\r\n			Transfer to family of silver jewelry maker. See handmade jewelry. Have some master class of making jewelry.</p>\r\n\r\n			<p><strong>Lunch.</strong><br />\r\n			Transfer to Big Almaty Lake, visit to falcon farm \"Golden Eagle show\".<br />\r\n			Transfer to Train Station, train to Chimkent at 17.36.<br />\r\n			<strong>Dinner.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 4</td>\r\n			<td>\r\n			<h3>Chimkent</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast on train. </strong><br />\r\n			<strong>Arrival to Chimkent, transfer to hotel and accommodation. Transfer to </strong>The legendary Kazygurt Mount for sightseeing.<br />\r\n			Transfer back to Chimkent for lunch.</p>\r\n\r\n			<p><strong>Lunch.</strong><br />\r\n			Visit to underground mosque.<br />\r\n			<strong>Dinner. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 5</td>\r\n			<td>\r\n			<h3>Chimkent - Turkistan</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast </strong><br />\r\n			Transfer to Turkistan, arrival and visit to Ahmad Yassawiy Mausoleum.</p>\r\n\r\n			<p><strong>Lunch.</strong><br />\r\n			Continue of city sightseeing.<br />\r\n			<strong>Early dinner. </strong><br />\r\n			Transfer to Train Station, train to Aral Sea at 17.10.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 6</td>\r\n			<td>\r\n			<h3>Aral Sea</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast on train.</strong><br />\r\n			Arrival to Aral train station, transfer to Tastubek village on the way to village visit canyons and salty dersert- the former bottom of sea. Arrival to village and accommodation in fisherman families.</p>\r\n\r\n			<p><strong>Lunch.</strong><br />\r\n			Tasting shubat, possible camel riding. Dombra show and swimming in Aral sea.<br />\r\n			<strong>Dinner. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 7</td>\r\n			<td>\r\n			<h3>Aral Sea</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong><br />\r\n			Trasfer back to Aral. Walking tour - visiting of former Port see Ships Museum,visiting ethnical Museum ( history of city)</p>\r\n\r\n			<p><strong>Lunch. </strong><br />\r\n			Visit to Bazaar. Possible to visit fish factory \"Cambala Balyk\"<br />\r\n			<strong>Dinner</strong>.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 8</td>\r\n			<td>\r\n			<h3>Aral Sea - Almaty</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast </strong><br />\r\n			Free at leisure time</p>\r\n\r\n			<p><strong>Lunch</strong><br />\r\n			Transfer to Train Station, train to Almaty at 16.30<br />\r\n			<strong>Dinner. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 9</td>\r\n			<td>\r\n			<h3>Almaty</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong><br />\r\n			Train journey</p>\r\n\r\n			<p><strong>Lunch.</strong><br />\r\n			Arrival to Almaty Train Station at 23.20, transfer to hotel and accommodation.<br />\r\n			<strong>Dinner.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 10</td>\r\n			<td>\r\n			<h3>Almaty</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast.</strong><br />\r\n			Transfer to International Airport for departure.</p>\r\n\r\n			<p><strong><em>End of our service …</em></strong></p>\r\n			</td>\r\n		</tr>\r\n\r\n</table>', '21', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('de', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n			<th style=\"width:10%;\">TAGE</td>\r\n			<th style=\"width:14%;\">REISEROUTE</td>\r\n			<th>THEMEN DER REISEROUTE</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">1. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Almaty</h3>\r\n			</td>\r\n			<td>\r\n			<p>Ankunft in Almaty, Beförderung zum Hotel und Unterbringung.<br />\r\n			<strong><em>Stadtbesichtigung in Almaty:</em></strong> Der Panfilowtzew Park, die Senkow Kathedrale, das Krieg-Denkmal, Das Museum der musikalischen Instrumenten, die Ehrengasse, der Republikplatz, die Zentrale Moschee.<br />\r\n			Beförderung zum Medeo Stadium, das sich auβerhalb von Almaty befindet, Wandertour in Medeo.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen</strong><br />\r\n			Beförderung nach Almaty, Besuch zur Kok-Tobe mit Seilbahn, Wandertour nach Kok-Tobe.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">2. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Almaty - Issyk See- Almaty</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck </strong><br />\r\n			Beförderung zum Issyk See, unterwegs Besuch zum Issyk Kurgans, wo \"Der goldene Mann\" gegründet war.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen unterwegs</strong><br />\r\n			Rückkehr nach Almaty, Besuch zur Weinfabrik mit Weinprobe.<br />\r\n			<strong>Abendessen. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">3. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Almaty - Tschimkent</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck </strong><br />\r\n			Besuch der Familie von Juwelieren. Handmade Schmucke. Teilnahme an der Meister-Klasse in der Geschmeide.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen.</strong><br />\r\n			Beförderung zum Groβen Almaty See, Besuch zum Falkenbauernhof \"Golden Eagle show\".<br />\r\n			Beförderung zum Bahnhof, Zug nach Tschimkent um 17.36.<br />\r\n			<strong>Abendessen.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">4. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Tschimkent</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck im Zug</strong><br />\r\n			Ankunft in Tschimkent, Beförderung zum Hotel Unterbringung. Beförderung zum legendären Kazygurt Berg für Besichtigung.<br />\r\n			Rückkehr nach Tschimkent für Mittagessen.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen</strong><br />\r\n			Besuch zur unterirdische Moschee.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">5. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Tschimkent - Turkistan</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck </strong><br />\r\n			Beförderung nach Turkistan, Ankuft und Besuch zum Mausoleum von Ahmad Yassawiy.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen</strong><br />\r\n			Weitere Stadtbesichtigung.<br />\r\n			<strong>Frühes Abendessen</strong><br />\r\n			Beförderung zum Bahnhof, Zug zum Aral See um 17.10.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">6. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Aral Sea</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck </strong><br />\r\n			Ankunft auf dem Aral Bahnhof, Beförderung zum Tastubek Dorf Transfer. Unterwegs die Schluchten und die salzige Wüste besuchen. Ankunft in Dorf und Unterbringung bei Fischers Familie.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen</strong><br />\r\n			Shubat probieren, eventuelles Kamelreiten. Dombra Show und in Aral See schwimmen.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">7. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Aral Sea</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck</strong><br />\r\n			Rückkehr nach Aral. Wandertour – Der ehemalige Hafen, das Schiffenmuseum und ethnische Museum (Geschichte der Stadt)</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen</strong><br />\r\n			Besuch zum Basar. Eventueller Besuch zur \"Cambala Balyk\".<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">8. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Aral Sea - Almaty</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck </strong><br />\r\n			Zeit zur freien Verfügung.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen</strong><br />\r\n			Beförderung zum Bahnhof, Zug nach Almaty um 16.30<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">9. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Almaty</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck</strong></p>\r\n			Bahnfahrt</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen</strong><br />\r\n			Ankunft im Almaty Bahnhof un 23.20, Beförderung zum Hotel und Unterbringung.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>10. TAG</td>\r\n			<td>\r\n			<h3>Almaty</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck</strong><br />\r\n			Beförderung zum Internationalen Flughafen Almaty.<br />\r\n			<br />\r\n			<strong><em>Ende unserer Leistung …</em></strong></p>\r\n			</td>\r\n		</tr>\r\n</table>', '21', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('es', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n			<th style=\"width:10%;\">DÍAS</td>\r\n			<th style=\"width:14%;\">TRAYECTO</td>\r\n			<th>ITINERARIO</td>\r\n		</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 1</td>\r\n<td rowspan=\"2\">\r\n<h3>Almaty</h3>\r\n</td>\r\n<td>\r\n<p>Llegada a Almaty, traslado y alojamiento en hotel  <br /> <strong><em>Visitas en Almaty:</em></strong> Visita al parque Panfilovzev 28, Catedral Zenkov, Memorial de Guerra, Museo de Instrumentos Musicales, Alleya de Gloria, Plaza de la  República, Mezquita Central . Traslado a las afueras de Almaty, al estadio Medeo, Caminata en Medeo.</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida</strong><br /> Traslado a Almaty, excursion en telesillas y caminata en Kok-Tobe <br /> <strong>Cena </strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 2</td>\r\n<td rowspan=\"2\">\r\n<h3>Alamaty - Lake Issyk - Almaty</h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno</strong><br /> Traslado al lago Issyk, en el camino visita a Issyk kurgans, donde  fue fundada \"Golden Man\".</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida en la ruta.</strong><br /> Traslado a Almaty, visita a la bodega de vino, degustación de vino.<br /><strong>Cena.</strong> </p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 3</td>\r\n<td rowspan=\"2\">\r\n<h3>Alamty - Chimkent</h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno.</strong><br /> Visita a la familia de fabricante de joyas de plata. Ver la joyería hecha a mano. Tendrán alguna clase de maestro de hacer la joyería.</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Lunch.</strong><br /> Traslado al lago Grande de Almaty, visit to falcon farm \"Golden Eagle show\".<br />Traslado a la estación de Tren, tomar el tren a Chimkent a las 17.36. <br /> <strong>Cena.</strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 4</td>\r\n<td rowspan=\"2\">\r\n<h3>Chimkent</h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno en el tren.</strong> <br /> Llegada a Chimkent, traslado y alojamiento en hotel . Visita al famoso Monte Kazygurt.\r\n <br />Traslado a Chimkent para comer.</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida.</strong><br /> Visita a la mezquita subterranea.<br /> <strong>Cena.</strong> </p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 5</td>\r\n<td rowspan=\"2\">\r\n<h3>Chimkent - Turkistan</h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno</strong><br /> Traslado a Turkistan, llegada y visita al mausoleo de  Ahmad Yassawiy.  </p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida.</strong><br /> Continuación de la visita de la ciudad. <br /> <strong>Cena temprana.  </strong><br /> Traslado a la Estación de Tren, tomar el tren hacia el mar Aral a las 17.10.</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 6</td>\r\n<td rowspan=\"2\">\r\n<h3>Aral Sea</h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno en el tren. </strong> <br /> Llegada a la estación de tren del mar de  Aral, traslado al pueblo Tastubek, en el camino visita a canyones y desierto de sal que se formó por la sequía del mar- and salty dersert- the former bottom of sea. Llegada al pueblo y alojamiento en las casas de los pescadores.</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida.</strong><br /> Tasting shubat, possible camel riding. Dombra show y  nadar en el mar de Aral. <br /> <strong>Cena. </strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 7</td>\r\n<td rowspan=\"2\">\r\n<h3>Aral Sea</h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno</strong> <br /> Traslado al mar de Aral. Caminata, visitas la Puerto, museo de barcos, museo étnico (historia de la ciudad) </p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida.</strong><br /> Visita al Bazaar. Posiblemente visita a la planta de pescado \"Cambala Balyk\"<br /> <strong>Cena.</strong>.</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 8</td>\r\n<td rowspan=\"2\">\r\n<h3>Aral Sea - Almaty</h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno</strong><br /> Tiempo libre</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida</strong><br /> Traslado a la Estación de tren, tomar el tren hacia Almaty a las 16.30 <br /> <strong>Cena. </strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 9</td>\r\n<td rowspan=\"2\">\r\n<h3>Almaty</h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno.</strong><br /> Viaje en tren</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida.</strong><br /> Llegada a la estación de tren de Almaty a las 23.20, traslado y alojamiento en hotel.<br /> <strong>Cena. </strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>DÍA 10</td>\r\n<td>\r\n<h3>Almaty</h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno.</strong> <br /> Traslado al aeropuerto internacional para tomar el vuelo hacia el destino. <br /> <strong><em>Fin de nuestros servicios …</em></strong></p>\r\n</td>\r\n</tr>\r\n</table>', '21', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('fr', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n			<th style=\"width:10%;\">JOURS</td>\r\n			<th style=\"width:14%;\">TRAJET</td>\r\n			<th>ITINERAIRE</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 1</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Almaty</h3>\r\n			</td>\r\n			<td>\r\n			<p>Arrivée à Almaty, transfert à l\'hôtel et l\'hébergement<br />\r\n			<strong><em>Visite d’Almaty : </em></strong> Visite de 28 Panfilovzev Park, Cathédrale Zenkov, Mémorial de Guerre, Musée des Instruments de Musique, l’Allée de Gloire, Place de la République, et Mosquée Centrale. Transfert au stade Medeo, visite à pied dans Medeo.</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			Retour à Almaty, visite de Kok-Tobe, la câble de route.<br />\r\n			<strong>Dîner</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 2</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Alamaty - Lac Issyk - Almaty</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner</strong><br />\r\n			Transfert au lac Issyk sur la route visite des kourganes Issyk, où \"Homme d’or\" a été trouvée.</p>\r\n\r\n			<p><strong>Déjeuner en route</strong><br />\r\n			Retour à Almaty, visite de l\'usine de vin, dégustation de vins.<br />\r\n			<strong>Dîner </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 3</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Almaty - Chimkent</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner </strong><br />\r\n			Transfert à la famille fabricant de bijoux en argent faits à main. Un cours de maitre de la fabrication de bijoux.</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			Transfert à Grand lac d’Almaty, visite du ferme faucon \"Spectacle aigle d’or\".<br />\r\n			Transfert à la gare, train pour Chimkent à 17.36.<br />\r\n			<strong>Dîner</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 4</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Chimkent</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner à bord du train</strong><br />\r\n			Arrivée à Chimkent, transfert à l\'hôtel et l\'hébergement. Transfert à la légendaire Mont Kazygourte pour faire du trek.<br />\r\n			Retour à Chimkent.</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			Visite de la mosquée souterraine.<br />\r\n			<strong>Dîner </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 5</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Chimkent - Turkestan</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner </strong><br />\r\n			Transfert à Turkestan, arrivée et visite de Mausolée d’Akhmad Yassawiy</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			Continue de la visite de la ville.<br />\r\n			<strong>Dîner tôt</strong><br />\r\n			Transfert à la gare, train pour la mer d\'Aral à 17.10.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 6</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Mer d’Aral</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner à bord du train</strong><br />\r\n			Arrivée à la gare d\'Aralsk, transfert au village Tastoubek sur la route visite des canyons et désert salé - l\'ancien fond de la mer. Arrivée au village et l\'hébergement en famille de pêcheur.</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			Dégustation de Shoubat, promenade à dos de chameau possiblement. Spectacle Dombra et bain dans la mer d\'Aral.<br />\r\n			<strong>Dîner </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 7</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Mer d’Aral</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner</strong><br />\r\n			Retour à Aralsk. Visite à pied - de l\'ancien Port, Musée de navires, Musée ethnique (histoire de la ville)</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			Visite Bazaar. Possibilité de visiter l\'usine de poisson \"Cambala Balyk\"<br />\r\n			<strong>Dîner</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 8</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Mer d’Aral - Almaty</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner </strong><br />\r\n			Temps libre.</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			Transfert à la gare, le train pour Almaty à 16.30<br />\r\n			<strong>Dîner à bord du train </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 9</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Almaty</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner</strong><br />\r\n			Voyage en train.</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			Arrivée à la gare d’Almaty à 23h20, transfert à l\'hôtel et l\'hébergement.<br />\r\n			<strong>Dîner.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 10</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Almaty</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner</strong><br />\r\n			Transfert à l\'aéroport pour le départ. Envol.</p>\r\n\r\n			<p><strong><em>Fin de nos services...</em></strong></p>\r\n			</td>\r\n		</tr>\r\n</table>\r\n\r\n\r\n', '21', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('en', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n            <th style=\"width:8%;\">Days</th>\r\n            <th style=\"width:14%;\">Waypoint</th>\r\n            <th>Subjects of itinerary</th>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 1</td>\r\n			<td>\r\n			<h3>Bishkek -<br />\r\n			Ala-Archa - Bishkek</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong><br />\r\n			Transfer to Ala-Archa national natural park (40 km) from Bishkek for a sightseeing. Ala Archa National Park is one of the few places of the habitat of the rare snow leopard.</p>\r\n\r\n			<p><strong>Barbeque Lunch.</strong><br />\r\n			Transfer back to Bishkek for city sightseeing visiting: Manas monument, State History Museum, Ala Too Square, Independence monument, Parliament House.<br />\r\n			<strong>Dinner. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 2</td>\r\n			<td>\r\n			<h3>Bishkek - Issyk-kul</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast </strong><br />\r\n			Transfer to Issyk-Kul, on the way visit to Burana Tower (11-12c). Also visit local museum telling about that civilization, collection of Balbals, Turkic ancient tombs.</p>\r\n\r\n			<p><strong>Lunch.</strong><br />\r\n			Continue drive to the Issyk-Kul Lake, the second largest alpine lake in the world after Lake Titicaca in South America. Upon arrival, possible sweeming, hiking, sightseeing. Accommodation in hotel.<br />\r\n			<strong>Dinner.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 3</td>\r\n			<td>\r\n			<h3>Issyk-Kul - Kochkor village - Son Kul Lake</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong><br />\r\n			Transfer to Son-Kul Lake, On the way visit Kochkor village to watch local people make felt, various carpets (called shyrdaks), embroidering, spinning, and other handicraft stuff. Visit a handicraft museum and a shop, finishing with small Christmas tree decorations.</p>\r\n\r\n			<p><strong>Lunch.</strong><br />\r\n			Arrival to Son Kul Lake, accommodation in yurts, Sightseeing of surroundings.<br />\r\n			<strong>Dinner. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 4</td>\r\n			<td>\r\n			<h3>Son-Kul Lake - Chon Kemin</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast. </strong><br />\r\n			Transfer to Chon Kemin, The valley has a rich variety of vegetation, and animal life, and boasts both a Zoological preserve. Upon arrival accommodation in guest house.</p>\r\n\r\n			<p><strong>Lunch.</strong><br />\r\n			Visit to mausoleum dedicated to Shabidan Baitir, a 19th century chieftain of the Sary Bagysh tribe who ruled the valley and is highly regarded as a national hero. Hiking the George.<br />\r\n			<strong>Dinner. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 5</td>\r\n			<td>\r\n			<h3>Chon Kemin - Bishkek</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast </strong><br />\r\n			Transfer to Bishkek. Our way goes through tight Boom Gorge where boisterous Chu River, which is so much loved by rafters, passes.</p>\r\n\r\n			<p><strong>Lunch.</strong><br />\r\n			Arrival and accommodation in hotel. Visit to Victory square.<br />\r\n			Free at leisure time, shopping.<br />\r\n			<strong>Dinner. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 6</td>\r\n			<td>\r\n			<h3>Bishkek</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast. </strong><br />\r\n			Transfer to \"Manas\" Int Airport, flight back home.</p>\r\n\r\n			<p><strong><em>End of our services ...</em></strong></p>\r\n			</td>\r\n		</tr>\r\n</table>', '22', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('de', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n			<th style=\"width:10%;\">TAGE</td>\r\n			<th style=\"width:14%;\">REISEROUTE</td>\r\n			<th>THEMEN DER REISEROUTE</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">1. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Bischkek -<br />\r\n			Ala-Artscha - Bischkek</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck</strong><br />\r\n			Transfer zum Ala-Artscha nationale Naturpark, der 40 km von Bischkek entfernt ist. Ala Artscha Naturpark ist einer der Orte, wo die seltene Schneeleoparden bewohnen.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Barbeque für Mittagessen</strong><br />\r\n			Rückkehr nach Bischkek für Stadtbesichtigung: Manas Denkmal, das Staatliche Geschichtsmuseum, Ala Too Platz, Monument der Unabgängigkeit, Parlament Haus.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">2. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Bischkek - Issyk-kul</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck </strong><br />\r\n			Transfer zum Issyk-Kul See, unterwegs Besichtigung des Burana Turms (11-12Jh), lokales Museum, wo man über damaliger Zivillisation, die Balbals Kollektion und alte Turkische Grӓber lernen kann.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen</strong><br />\r\n			Weiterfahrt zum Issyk-Kul See, der zweitgrößte alpine See in die Welt zu, nachdem Titicaca See in Südamerika. Nach der Ankunft kann man schwimmen, wandern und Besichtigungen haben. Unterbringung im Hotel.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">3. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Issyk-Kul - Kotschkor Dorf - Son Kul See</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck</strong><br />\r\n			Transfer zum Son-Kul See, unterwegs Besuch zum Kotschkor Dorf um zu beobachten, wie die Leute den Filz, verschiedene Teppiche (shyrdaks) weben, sticken, spinnen und andere handwerkliche Arbeit machen. Zum Handwerkmuseum und ins Geschäft gehen, am Ende ein kleines Tannenbaum dekorieren.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen</strong><br />\r\n			Ankunft in Son Kul See, Unterbringung in den Jurten, Besichtigung der Umgebung.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">4. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Son-Kul See - Tschon Kemin</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck</strong><br />\r\n			Transfer nach Tschon Kemin. Das Tal ist reich für sein Vegetation und Tierwelt und bietet sowohl ein Tierschutzgebiet. Nach der Ankunft, Unterbringung im Gästehaus.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen</strong><br />\r\n			Ausflug ins Shabidan Baitir Mausoleum, der Häuptling der Sary Bagysh Stamm aus dem 19. Jahrhundert, der das Tal beherrschte und war hoch wie ein Volksheld angesehen. Wandern auf der Schlucht.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">5. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Tschon Kemin - Bischkek</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck </strong><br />\r\n			Transfer nach Bischkek. Unser Weg führt durch der enge Boom-Schlucht, wo ausgelassener Chu-Fluss flieβt.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen</strong><br />\r\n			Ankunft und Unterbringung im Hotel. Ausflug zum Siegeplatz.<br />\r\n			Erholen, Einkaufen.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>6. TAG</td>\r\n			<td>\r\n			<h3>Bischkek</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck</strong><br />\r\n			Transfer zum \"Manas\" Internationalen Flughafen, Heimreise.<br />\r\n			&nbsp;<br />\r\n			<strong><em>Ende unserer Leistung …</em></strong></p>\r\n			</td>\r\n		</tr>\r\n</table>', '22', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('es', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n			<th style=\"width:10%;\">DÍAS</td>\r\n			<th style=\"width:14%;\">TRAYECTO</td>\r\n			<th>ITINERARIO</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DÍA 1</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Bishkek -<br />\r\n			Ala-Archa - Bishkek</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Desayuno</strong><br />\r\n			Traslado al parque nacional de Naturaleza Ala-Archa,(a 40 km de Bishkek para hacer excursión). El Parque Nacional Ala Archa es uno de los pocos lugares de habitat del leopard de nieve.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Comida (carne asada).</strong><br />\r\n			Traslado a Bishkek para vistar la ciudad: monumento de Manas, Museo Estatal de Historia, Plaza Ala Too, Monumento de la Independencia, Sede del Parlamento.<br />\r\n			<strong>Cena.</strong>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DÍA 2</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Bishkek - Issyk-kul</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Desayuno </strong><br />\r\n			Traslado a Issyk-Kul, en el camino visita la Torre Burana (s.11-12). También visita al museo local sobre la civilización, colección de balbals, antiguas tumbas de los turcos.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Comida.</strong><br />\r\n			Continuación de la visita hacia el lago Issyk-Kul, que es el segundo lago más grande en la montaña en el mundo después del lago Titicaca, en America del Sur. Llegada al lago: bañarse, montar en caballo,excursions por los alrededores (opcional). Alojamiento en hotel.<br />\r\n			<strong>Dinner.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DÍA 3</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Issyk-Kul - Kochkor village - Son Kul Lake</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Desayuno</strong><br />\r\n			Traslado al lago Son-Kul, en el camino visita al pueblo KochKor para ver como hacen las alfombras (llamadas shyrdaks), trabajaos de bordado y tejido y otros trabajos hechos a mano. Visita al museo y tienda de artesanía, decoraciones pequeñas del árbol de Navidad.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Comida.</strong><br />\r\n			Llegada al lago Son Kul, alojamiento en yurtas.<br />\r\n			Excursión por los alrededores.<br />\r\n			<strong>Cena.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DÍA 4</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Son-Kul Lake - Chon Kemin</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Desayuno. </strong>&nbsp;<br />\r\n			Traslado a Chon Kemin, la valle con su rica flora y fauna, también con su reserva zoological<br />\r\n			Llegada y alojamiento en casa de huespedes</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Comida.</strong><br />\r\n			Visita al mausoleo Shabidan Baitir, jefe del tribú Sary Bagysh en el siglo 19, quien gobernó la valle y considerado como heroe nacional. Senderismo por el desfiladero.<br />\r\n			<strong>Cena.</strong>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DÍA 5</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Chon Kemin - Bishkek&nbsp;</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Desayuno </strong><br />\r\n			Traslado a Bishkek. Nuestro camino pasa por el estrecho desfiladero donde corre revoltoso río Chu, que es el lugar preferido de viajeros de extremo.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Comida.</strong><br />\r\n			Llegada y alojamiento en hotel. Visita a la plaza de Victoria.<br />\r\n			<strong>Cena.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DÍA 6</td>\r\n			<td>\r\n			<h3>Bishkek</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Comida. </strong><br />\r\n			Traslado al aeropuerto Internacional de \"Manas\", vuelo al destino final...<br />\r\n			<br />\r\n			<strong><em>Fin de nuestros servicios ...</em></strong><strong> </strong></p>\r\n			</td>\r\n		</tr>\r\n</table>', '22', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('fr', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n			<th style=\"width:10%;\">JOURS</td>\r\n			<th style=\"width:14%;\">TRAJET</td>\r\n			<th>ITINERAIRE</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 1</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Bichkek - Ala-Archa - Bichkek</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner</strong><br />\r\n			Transfert à parc naturel national d’Ala-Archa à 40 km de Bichkek pour une visite touristique. Parc national d\'Ala-Archa est l\'un des rares endroits de l\'habitat de la panthère de neige rare.</p>\r\n\r\n			<p><strong>Déjeuner-barbecue</strong><br />\r\n			Retour à Bichkek pour visiter la ville : monument de Manas, Musée d’État d’Histoire, place d’Ala-Too, monument de l\'Indépendance, la Chambre de Parlement.<br />\r\n			<strong>Dîner </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 2</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Bichkek - Issyk-koul</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner </strong><br />\r\n			Transfert à Issyk-Koul, sur la route visite à la Tour Bourana (11-12c), musée local racontant de cette civilisation, collection de Balbals, anciennes tombes turques.</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			Continuation en voiture du lac Issyk-Koul, le deuxième plus grand lac alpin dans le monde après le lac Titicaca en Amérique du Sud. À l\'arrivée, possible bain, randonnées, visites. Hébergement à l\'hôtel.<br />\r\n			<strong>Dîner</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 3</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Issyk-Koul – village Kochkor – lac Son Koul</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner</strong><br />\r\n			Transfert à Son-Koul, en route visite du village Kochkor de regarder les gens locaux qui font feutre, divers tapis (appelés shyrdaks), la broderie, la filature, et d\'autres choses de l\'artisanat. Visite d’un musée de l\'artisanat et une boutique, la finition avec de petites décorations d\'arbres de Noël.</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			Arrivée à Son Koul, l\'hébergement dans des yourtes, promenade aux alentours.<br />\r\n			<strong>Dîner. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 4</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Lac Son-Koul - Chon Kemin</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner</strong><br />\r\n			Transfert à Chon Kemin, la vallée a une riche variété de la végétation et la vie animale, et possède à la fois un zoologique préserver. Arrivée et installation dans la maison d\'hôtes.</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			Visite au mausolée dédié à Shabidan Baitir, un chef de la tribu Sary Bagysh du 19ème siècle qui a gouverné la vallée et est hautement considéré comme un héros national. Randonnée sur le George.<br />\r\n			<strong>Dîner </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 5</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Chon Kemin - Bichkek</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner </strong><br />\r\n			Transfert à Bichkek à travers Gorge Boom serré où bruyante rivière Chu, qui est tellement aimé par des chevrons.</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			Arrivée et installation à l\'hôtel. Visite de la place de la Victoire.<br />\r\n			Temps libre, shopping.<br />\r\n			<strong>Dîner </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 6</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Bichkek</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner</strong><br />\r\n			Transfert à l’aéroport international de \"Manas\", vol de retour.</p>\r\n\r\n			<p><strong><em>Fin de nos services ...</em></strong></p>\r\n			</td>\r\n		</tr>\r\n</table>\r\n', '22', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('en', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n            <th style=\"width:8%;\">Days</th>\r\n            <th style=\"width:14%;\">Waypoint</th>\r\n            <th>Subjects of itinerary</th>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 1</td>\r\n			<td>\r\n			<h3>Bishkek</h3>\r\n			</td>\r\n			<td>\r\n			<p>Arrival to Bishkek, transfer to hotel and accommodation.<br />\r\n			<strong><em>Sightseeing tour in Bishkek:</em></strong> visit Manas Monument, the main national hero of the Kyrgyz people. State History Museum and walking tour around Ala Too Square to watch ceremony of changing of the Guard of Honor, Independence Monument, Old Square, Parliament House.</p>\r\n\r\n			<p><strong>Lunch </strong><br />\r\n			Transfer to the Victory Square to finish the city tour by a short excursion there.<br />\r\n			<strong>Dinner</strong><strong> </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 2</td>\r\n			<td>\r\n			<h3>Bishkek -<br />\r\n			Ala-Archa - Bishkek</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong><br />\r\n			Transfer to Ala-Archa national natural park (40 km) from Bishkek for a sightseeing. Ala Archa National Park is one of the few places of the habitat of the rare snow leopard.</p>\r\n\r\n			<p><strong>Barbeque Lunch.</strong><br />\r\n			On the way we make several stops near a cemetery for taking pictures and to visit the grave of a national Kyrgyz hero – Baytik Baatyr, his mausoleum.<br />\r\n			Transfer back to Bishkek.<br />\r\n			<strong>Dinner. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 3</td>\r\n			<td>\r\n			<h3>Bishkek – Issyk-kul</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast </strong><br />\r\n			Transfer to Issyk-Kul, on the way visit to Burana Tower (11-12c). Also visit local museum telling about that civilization, collection of Balbals, Turkic ancient tombs.</p>\r\n\r\n			<p><strong>Lunch.</strong><br />\r\n			Continue drive to the Issyk-Kul Lake, the second largest alpine lake in the world after Lake Titicaca in South America. Upon arrival, possible sweeming, hiking, sightseeing. Accommodation in hotel.<br />\r\n			<strong>Dinner.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 4</td>\r\n			<td>\r\n			<h3>Issyk-Kul - Karakol</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast. </strong><br />\r\n			Transfer to Cholpon-Ata petroglyphs site, the temple in the open air of Scythians and Hung suns civilizations dated back 9-8 centuries BC till 3-4 centuries AD. Optionally possible boat cruise.</p>\r\n\r\n			<p><strong>Lunch.</strong><br />\r\n			Transfer to Karakol, on the way visit museum of Przhevalski, the outstanding Russian explorer of Asian continent.<br />\r\n			Upon arrival accommodation in hotel.<br />\r\n			Visit to Dungan mosque in Chinese style, old Russian Orthodox Cathedral made of wood without any nails.<br />\r\n			<strong>Dinner. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 5</td>\r\n			<td>\r\n			<h3>Karakol - Djety Oguz goerge - Kochkor Village</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast </strong><br />\r\n			Transfer to Kochgor Village on the visit Djety Oguz George, This picturesque gorge is named so because of the red rocky sand formations covered with forests of Tien Shan spruces. We hike up the main gorge along the winding Djety Oguz mountain river. On the way you visit some yurts of the local nomadic families.</p>\r\n\r\n			<p><strong>Lunch with show in local family house.</strong><br />\r\n			Arrival and accommodation in village. In Kochkor you will watch how local people make felt, various carpets, embroidering and spinning, and other handicraft stuff.<br />\r\n			<strong>Dinner. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 6</td>\r\n			<td>\r\n			<h3>Kochkor - Bishkek</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast.</strong><br />\r\n			<em>Transfer to Bishkek,</em> way goes through tight Boom Gorge where boisterous Chu River<em>passes.</em></p>\r\n\r\n			<p><strong>Lunch.</strong><br />\r\n			Arrival to Bishkek, accommodation in hotel, free at leisure time.<br />\r\n			<strong>Dinner.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 7</td>\r\n			<td>\r\n			<h3>Bishkek</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast. </strong><br />\r\n			Transfer to \"Manas\" Int Airport, flight back home.</p>\r\n\r\n			<p><strong><em>End of our services ...</em></strong></p>\r\n			</td>\r\n		</tr>\r\n</table>', '23', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('de', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n			<th style=\"width:10%;\">TAGE</td>\r\n			<th style=\"width:14%;\">REISEROUTE</td>\r\n			<th>THEMEN DER REISEROUTE</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">1. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Bischkek</h3>\r\n			</td>\r\n			<td>\r\n			<p>Ankunft in Bischkek, Transfer ins Hotel und Unterbringung.<br />\r\n			<strong><em>Stadtbesichtigung in Bischkek:</em></strong> Ausflug zum Manas Denkmal, der haupte Nationalhelden des kirgisischen Volkes. Staatliches Geschichtsmuseum und Wandertour rund um den Ala Too Platz um die Zeremonie der Veränderungen des Ehrenspaliers, das Monument der Unabgängigkeit, den Alten Platz, das Parlament Haus zu beobachten.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen </strong><br />\r\n			Transfer zum Siegesplatz um die Stadtrundfahrt durch einen kurzen Ausflug dorthin zu beenden.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">2. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Bischkek -<br />\r\n			Ala-Artscha - Bischkek</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck </strong><br />\r\n			Transfer zum Ala-Artscha nationale Naturpark, der 40 km von Bischkek entfernt ist. Ala Artscha Naturpark ist einer der Orte, wo die seltene Schneeleoparden bewohnen.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Barbeque für Mittagessen</strong><br />\r\n			Auf dem Weg machen wir mehrere Haltestellen in der Nähe eines Friedhofs um Fotos aufzunehmen und und besichtigen wir das Mausoleum eines nationalen kirgisischen Helden - Baytik Baatyr.<br />\r\n			<strong>Abendessen. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">3. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Bischkek – Issyk-kul</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck &nbsp;</strong><br />\r\n			Transfer zum Issyk-Kul See, unterwegs Besichtigung des Burana Turms (11-12Jh), lokales Museum, wo man über damaliger Zivillisation, die Balbals Kollektion und alte Turkische Gräber lernen kann.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen.</strong><br />\r\n			Weiterfahrt zum Issyk-Kul See, der zweitgrößte alpine See in die Welt zu, nachdem Titicaca See in Südamerika. Nach der Ankunft kann man schwimmen, wandern und Besichtigungen haben. Unterbringung im Hotel.&nbsp;<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">4. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Issyk-Kul - Karakol</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck </strong><br />\r\n			Transfer zum Tscholpon-Ata Petroglyphen Ort, der Tempel an frischen Luft der Skythen und Hung Sonnenzivilisationen des 9-8 Jh. v. Chr. bis 3-4 Jahrhundert n. Chr. Bootsfahrt ist möglich.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen</strong><br />\r\n			Transfer zum Karakol, unterwegs besichtigen wir Przhevalski Museum des hervorragenden russischen Entdecker der asiatischen Kontinent.<br />\r\n			Unterbringung im Hotel nach der Ankunft .<br />\r\n			Ausflug zur Dungan Moschee in chinesischer Art, zur alte russisch-orthodoxe Kathedrale, die aus Holz und ohne Nägel gebaut wurde.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">5. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Karakol – Djety Oguz Schluchte – Kotschkor Dorf</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck &nbsp;</strong><br />\r\n			Transfer zum Kochgor Dorf und Besichtigung von Djety Oguz Schlucht. Diese malerische Schlucht hat solchen Namen wegen der roten Felsen und mit Wäldern bedeckten Sandformationen bekommen. Wir klettern auf die Hauptschlucht entlang des gewundenen Djety Oguz Gebirgsfluss. Unterwegs besuchen wir einige Jurten der lokalen nomadischen Familien.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen mit Show im lokalen Familienhaus</strong><br />\r\n			Anreise und Unterbringung im Dorf. In Kotschkor können Sie beobachten, wie die Leute den Filz, verschiedene Teppiche (shyrdaks) weben, sticken, spinnen und andere handwerkliche Arbeit machen.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">6. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Kotschkor - Bischkek</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck </strong><br />\r\n			Transfer nach Bischkek. Unser Weg führt durch der enge Boom-Schlucht, wo ausgelassener Chu-Fluss flieβt.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen</strong><br />\r\n			Ankunft in Bischkek, Unterbringung im Hotel. Erholen.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>7. TAG</td>\r\n			<td>\r\n			<h3>Bischkek</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck </strong><br />\r\n			Transfer zum \"Manas\" Internationalen Flughafen, Heimreise.<br />\r\n			<br />\r\n			<strong><em>Ende unserer Leistung …</em></strong></p>\r\n			</td>\r\n		</tr>\r\n</table>', '23', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('es', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n			<th style=\"width:10%;\">DÍAS</td>\r\n			<th style=\"width:14%;\">TRAYECTO</td>\r\n			<th>ITINERARIO</td>\r\n		</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 1</td>\r\n<td rowspan=\"2\">\r\n<h3>Bishkek</h3>\r\n</td>\r\n<td>\r\n<p>LLegada a Bishkek,  traslado y alojamiento en hotel . <br /> <strong><em>Visitas en Bishkek:</em></strong> visita al Monumento de Manas, heroe nacional del pueblo kirguiz, Museo Estatal de historia y caminata por los alrededores de la plaza de Ala Too para ver la ceremonia de cambio de la Guardia de Honor, monumento de la Independencia, Plaza Vieja, Sede del Parlamento.</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida </strong><br /> Traslado a la plaza de Victoria, acabar las visitas en la ciudad con una excursion breve allí. <br /> <strong>Cena</strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 2</td>\r\n<td rowspan=\"2\">\r\n<h3>Bishkek -<br />Ala-Archa - Bishkek</h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno</strong><br /> Traslado a Ala-Archa para recorrer el parque nacional de  Naturaleza (a 40 km de Bishkek). El Parque Nacional Ala Archa es uno de los pocos lugares de hábito del leopardo de nieve. </p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Barbeque Lunch. Comida asado</strong><br /> En el camino hacemos un parada breve cerca de cementerio para tomar fotos y visitar la tumba y mausoleo del héroe nacional kirguiz, Baytik Baatyr.  <br /> Traslado a Bishkek<br /> <strong>Cena. </strong> </p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 3</td>\r\n<td rowspan=\"2\">\r\n<h3>Bishkek – Issyk-kul </h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno </strong><br /> Traslado a Issyk-Kul, en el camino visita al torre Burana (s.11-12). También visita al museo local sobre la civilización, colección de Balbals, antiguas tumbas turcas. </p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida.</strong><br /> Continuación de la visita hacia el lago Issyk-Kul, que es el segundo lago más grande en la montaña en el mundo después del lago Titicaca, en America del Sur. Llegada al lago: bañarse, montar en caballo,excursiones por los alrededores (opcional). Alojamiento en hotel   <br /> <strong>Cena.</strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 4</td>\r\n<td rowspan=\"2\">\r\n<h3>Issyk-Kul - Karakol</h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno. </strong> <br /> Traslado a Cholpon-Ata, lugar de petroglifos, los templos al aire libre de la civilizaciones escitas y soles de Hung que remontan a los siglos 9-8 hasta los siglos 4-3 antes de Cristo.</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida.</strong><br /> Traslado a Karakol, en el camino visita al museo de Przhevalski, explorador ruso en el continente asiatico.<br /> Llegada y alojamiento en hotel.\r\nVisita a la mesquita Dungan  en estilo chino, vieja Iglesia Ortodoxa Rusa de madera hecha sin usar ningun clavo.<br /> <strong>Cena.  </strong> </p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 5</td>\r\n<td rowspan=\"2\">\r\n<h3> Karakol - Djety Oguz goerge - Kochkor Village </h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno </strong><br /> Traslado al pueblo Kochgor Village, visita al desfiladero Djety Oguz George,  pintoresco desfiladero se llama así debido a las formaciones de rocas de arena roja cubiertas de bosque de abetos de Tian Shan. Caminamos hasta la garganta principal a lo largo del serpenteante río de montaña Djety Oguz. En el camino visitamos algunas yurtas de las familias nómadas locales. </p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida con show en una casa de familia local.</strong><br /> Llegada y alojamiento en el pueblo. En Kochkor puede ver hacer la gente local alfombras, bordados, tejidos y otros trabajos hechos a mano.  <br /> <strong>Cena. </strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 6</td>\r\n<td rowspan=\"2\">\r\n<h3>Kochkor - Bishkek</h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno.</strong> <br /> <em>Traslado a Bishkek,</em> el camino pasa por  el desfiladero,donde corre revoltoso río Chu.</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida.</strong><br /> Llegada a Bishkek, alojamiento en hotel, tiempo libre. <br /> <strong>Cena.</strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>DAY 7</td>\r\n<td>\r\n<h3>Bishkek</h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno. </strong><br /> Traslado al aeropuerto internacional \"Manas\" , vuelo al destino...  <br /> <br /> <strong><em>Fin de nuestros servicios ...</em></strong><strong> </strong></p>\r\n</td>\r\n</tr>\r\n\r\n</table>', '23', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('fr', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n			<th style=\"width:10%;\">JOURS</td>\r\n			<th style=\"width:14%;\">TRAJET</td>\r\n			<th>ITINERAIRE</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 1</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Bichkek</h3>\r\n			</td>\r\n			<td>\r\n			<p>Arrivée à Bichkek, transfert à l\'hôtel et l\'hébergement.<br />\r\n			<strong><em>Visite de Bichkek :</em></strong> monument de Manas, le principal héros national du peuple kirghize, musée d’État d’Histoire, promenade autour de la place d’Ala-Too de regarder la cérémonie de changement de la garde d\'honneur, Monument de l\'Indépendance, Place de la Vieille, Maison de Parlement.</p>\r\n\r\n			<p><strong>Déjeuner </strong><br />\r\n			Transfert à la Place de la Victoire, pour terminer la visite de la ville par une courte excursion.<br />\r\n			<strong>Dîner</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 2</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Bichkek -<br />\r\n			Ala-Archa - Bichkek</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner</strong><br />\r\n			Transfert à parc naturel national d’Ala-Archa à 40 km de Bichkek pour une visite touristique. Parc national d’Ala-Archa est l\'un des endroits rares de l\'habitat de la panthère de neige rare.</p>\r\n\r\n			<p><strong>Déjeuner barbecue</strong><br />\r\n			Sur la route, nous faisons plusieurs arrêts à proximité d\'un cimetière pour prendre des photos et visiter la tombe d\'un héros national kirghize - Baytik Baatyr, son mausolée.<br />\r\n			Retour à Bichkek.<br />\r\n			<strong>Dîner </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 3</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Bichkek - Issyk-Koul</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner </strong><br />\r\n			Transfert à Issyk-Koul, sur la route visite à la Tour Bourana (11-12c), le musée local racontant de cette civilisation, collection de Balbals, anciennes tombes turques.</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			Continuation en voiture du lac Issyk-Koul, le deuxième plus grand lac alpin dans le monde après le lac Titicaca en Amérique du Sud. À l\'arrivée, possible bain, randonnées, visites. Hébergement à l\'hôtel.<br />\r\n			<strong>Dîner</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 4</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Issyk-Koul - Karakol</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner</strong><br />\r\n			Transfert à Cholpon-Ata site de pétroglyphes, le temple de la civilisation des Scythes et Hung à l\'air libre remontent 9-8 siècles avant JC jusqu\'à 3-4 siècles de notre ère. En option possible croisière en bateau.</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			Transfert à Karakol, sur la route visite du musée de Prezhevalski, l\'explorateur russe exceptionnelle de continent asiatique.<br />\r\n			L\'arrivée et installation dans l\'hôtel.<br />\r\n			Visite de la mosquée Dungan dans un style chinois, la cathédrale orthodoxe russe ancienne en bois sans clous.<br />\r\n			<strong>Dîner </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 5</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Karakol – Gorge Djety Oghouz – village Kochkor</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner </strong><br />\r\n			Transfert à village Kochgor pour la visite de George Djety Oghouz. Cette gorge pittoresque est nommé comme ça parce que des formations rocheuses de sable rouges couvertes de forêts de sapins Tien Shan. Nous remontons la gorge principale le long de l\'enroulement Djety Oghouz rivière de montagne. Sur le chemin vous visitez certaines yourtes des familles nomades locales.</p>\r\n\r\n			<p><strong>Déjeuner avec spectacle dans la maison familiale locale.</strong><br />\r\n			Arrivée et installation dans le village. Dans Kochkor vous pourrez regarder comment la population locale fait feutre, divers tapis, la broderie et la filature, et d\'autres choses de l\'artisanat.<br />\r\n			<strong>Dîner </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 6</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Kochkor - Bichkek</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner</strong><br />\r\n			Transfert à Bichkek, la route passe par le Gorge serré Boom où bruyantes rivière Chou coule.</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			Arrivée à Bichkek, l\'hébergement à l\'hôtel. Temps libre.<br />\r\n			<strong>Dîner</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\">JOUR 7</td>\r\n			<td>\r\n			<h3 style=\"text-align: center;\">Bichkek</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner</strong><br />\r\n			Transfert à l’aéroport international de \"Manas\", vol de retour.</p>\r\n\r\n			<p><strong><em>Fin de nos services ...</em></strong></p>\r\n			</td>\r\n		</tr>\r\n</table>\r\n\r\n\r\n', '23', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('en', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n            <th style=\"width:8%;\">Days</th>\r\n            <th style=\"width:14%;\">Waypoint</th>\r\n            <th>Subjects of itinerary</th>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 1</td>\r\n			<td>\r\n			<h3>Bishkek</h3>\r\n			</td>\r\n			<td>\r\n			<p>Arrival to Bishkek, transfer to hotel and accommodation.<br />\r\n			<strong><em>Sightseeing tour in Bishkek:</em></strong> visit Manas Monument, the main national hero of the Kyrgyz people. State History Museum and walking tour around Ala Too Square to watch ceremony of changing of the Guard of Honor, Independence Monument, Old Square, Parliament House.</p>\r\n\r\n			<p><strong>Lunch </strong><br />\r\n			Transfer to the Victory Square to finish the city tour by a short excursion there.<br />\r\n			<strong>Dinner</strong><strong> </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 2</td>\r\n			<td>\r\n			<h3>Bishkek -<br />\r\n			Ala-Archa - Bishkek</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong><br />\r\n			Transfer to Ala-Archa national natural park (40 km) from Bishkek for a sightseeing. Ala Archa National Park is one of the few places of the habitat of the rare snow leopard.</p>\r\n\r\n			<p><strong>Barbeque Lunch.</strong><br />\r\n			On the way we make several stops near a cemetery for taking pictures and to visit the grave of a national Kyrgyz hero – Baytik Baatyr, his mausoleum.<br />\r\n			Transfer back to Bishkek.<br />\r\n			<strong>Dinner. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 3</td>\r\n			<td>\r\n			<h3>Bishkek - Issyk-kul</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast </strong><br />\r\n			Transfer to Issyk-Kul, on the way visit to Burana Tower (11-12c). Also visit local museum telling about that civilization, collection of Balbals, Turkic ancient tombs.</p>\r\n\r\n			<p><strong>Lunch.</strong><br />\r\n			Continue drive to the Issyk-Kul Lake, the second largest alpine lake in the world after Lake Titicaca in South America. Upon arrival, possible swimming, hiking, sightseeing. Accommodation in hotel.<br />\r\n			<strong>Dinner.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 4</td>\r\n			<td>\r\n			<h3>Issyk-Kul - Kochkor Villgae - Son Kul Lake</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast. </strong><br />\r\n			Transfer Son Kul Lake, on the stopover at Kochkor Village. watch how local people make felt, various carpets (called shyrdaks), embroidering, spinning, and other handicraft stuff. Visit to handicraft museum and shop.</p>\r\n\r\n			<p><strong>Lunch.</strong><br />\r\n			Arrival to Son Kul Lake, accommodation in yurts, Sightseeing of surroundings.<br />\r\n			<strong>Dinner. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 5</td>\r\n			<td>\r\n			<h3>Son Kul Lake</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast </strong><br />\r\n			Free at leisure day, you have some free time to explore the area and enjoy the peaceful and picturesque landscapes.</p>\r\n\r\n			<p><strong>Lunch </strong><br />\r\n			Free at leisure day, you have some free time to explore the area and enjoy the peaceful and picturesque landscapes.<strong> </strong><br />\r\n			<strong>Dinner. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 6</td>\r\n			<td>\r\n			<h3>Son Kul Lake - Tash-Rabat</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast.</strong><br />\r\n			Transfer to Tash-Rabat over Dolon Pass down to Naryn Town. Arrival to Tash-Rabat and accommodation in Yurts.</p>\r\n\r\n			<p><strong>Lunch.</strong><br />\r\n			Tash Rabat caravanserai is a carefully restored stone building that once housed an inn on the Great Silk Road.<br />\r\n			<strong>Dinner.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 7</td>\r\n			<td>\r\n			<h3>Tash-Rabat - Kashgar</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast. </strong><br />\r\n			Transfer to Kyrgyz-Chinese border. Passing the border.</p>\r\n\r\n			<p><strong><em>End of our services ...</em></strong></p>\r\n			</td>\r\n		</tr>\r\n</table>\r\n', '24', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('de', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n			<th style=\"width:10%;\">TAGE</td>\r\n			<th style=\"width:14%;\">REISEROUTE</td>\r\n			<th>THEMEN DER REISEROUTE</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">1. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Bischkek</h3>\r\n			</td>\r\n			<td>\r\n			<p>Ankunft in Bischkek, transfer ins Hotel und Unterbringung.<br />\r\n			<strong><em>Sightseeing Tour in Bischkek:</em></strong> Ausflug zum Manas Denkmal, der haupte Nationalhelden des kirgisischen Volkes. Staatliches Geschichtsmuseum und Wandertour rund um den Ala Too Platz um die Zeremonie der Veränderungen des Ehrenspaliers, das Monument der Unabgängigkeit, den Alten Platz, das Parlament Haus zu beobachten.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen</strong><br />\r\n			Transfer zum Siegesplatz um die Stadtrundfahrt durch einen kurzen Ausflug dorthin zu beenden.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">2. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Bischkek -<br />\r\n			Ala-Artscha - Bischkek</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck </strong><br />\r\n			Transfer zum Ala-Artscha nationale Naturpark, der 40 km von Bischkek entfernt ist. Ala Artscha Naturpark ist einer der Orte, wo die seltene Schneeleoparden bewohnen. &nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Barbeque für Mittagessen</strong><br />\r\n			Auf dem Weg machen wir mehrere Haltestellen in der Nähe eines Friedhofs um Fotos aufzunehmen und und besichtigen wir das Mausoleum eines nationalen kirgisischen Helden - Baytik Baatyr.<br />\r\n			Rückkehr nach Bischkek.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">3. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Bischkek - Issyk-kul</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck &nbsp;</strong><br />\r\n			Transfer zum Issyk-Kul See, unterwegs Besichtigung des Burana Turms (11-12Jh), lokales Museum, wo man über damaliger Zivillisation, die Balbals Kollektion und alte Turkische Gräber lernen kann.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen</strong><br />\r\n			Weiterfahrt zum Issyk-Kul See, der zweitgrößte alpine See in die Welt zu, nachdem Titicaca See in Südamerika. Nach der Ankunft kann man schwimmen, wander und Stadtbesichtigung. Unterbringung im Hotel.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">4. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Issyk-Kul - Kotschkor Dorf - Son Kul See</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck </strong><br />\r\n			Transfer zum Son-Kul See, unterwegs Besuch zum Kotschkor Dorf um zu beobachten, wie die Leute den Filz, verschiedene Teppiche (shyrdaks) weben, sticken, spinnen und andere handwerkliche Arbeit machen. Zum Handwerkmuseum und ins Geschäft gehen.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen.</strong><br />\r\n			Ankunft in Son Kul See, Unterbringung in den Jurten, Besichtigung der Umgebung.<br />\r\n			<strong>Abendessen.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">5. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Son Kul See</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck</strong><br />\r\n			Zeit zur freien Verfügung. Sie können die Zeit zur Erforschung des Geländes widmen und den ruhigen und malerischen Landschaften genieβen.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen </strong><br />\r\n			Zeit zur freien Verfügung. Sie können die Zeit zur Erforschung des Geländes widmen und den ruhigen und malerischen Landschaften genieβen.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">6. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Son Kul See - Tasch-Rabat</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck </strong><br />\r\n			Transfer zum Tasch-Rabat durch Dolon Pass nach Naryn Stadt. Ankunft in Tasch-Rabat und Unterbringung in Jurten.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen</strong><br />\r\n			Tasch Rabat Karawansarai ist ein sorgfältiges restauriertes Gebäude aus Stein, das einst als Gasthaus auf der Großen Seidenstraße diente.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>7. TAG</td>\r\n			<td>\r\n			<h3>Tasch-Rabat - Kaschgar</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck </strong><br />\r\n			Transfer zur Kirgisischen-chinesischen Grenze. Die Grenze überqueren.&nbsp;<br />\r\n			<br />\r\n			<strong><em>Ende unserer Leistung …</em></strong></p>\r\n			</td>\r\n		</tr>\r\n</table>', '24', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('es', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n			<th style=\"width:10%;\">DÍAS</td>\r\n			<th style=\"width:14%;\">TRAYECTO</td>\r\n			<th>ITINERARIO</td>\r\n		</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 1</td>\r\n<td rowspan=\"2\">\r\n<h3>Bishkek</h3>\r\n</td>\r\n<td>\r\n<p>Llegada a Bishkek, traslado al hotel y alojamiento. <br /> <strong><em>Visitas en Bishkek:</em></strong> visita al Monumento de Manas, heroe nacional del pueblo kirguiz, Museo Estatal de historia y caminata por los alrededores de la plaza de Ala Too para ver la ceremonia de cambio de la Guardia de Honor, monumento de la Independencia, Plaza Vieja, Sede del Parlamento.</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida </strong><br /> Traslado a la plaza de Victoria, acabar las visitas en la ciudad con una excursion breve allí.<br /> <strong>Cena </strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 2</td>\r\n<td rowspan=\"2\">\r\n<h3>Bishkek -<br />Ala-Archa - Bishkek</h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno</strong><br /> Traslado a Ala-Archa para recorrer el parque nacional de  Naturaleza (a 40 km de Bishkek). El Parque Nacional Ala Archa es uno de los pocos lugares de hábito del leopardo de nieve. (one of the few places of the habitat of the rare snow leopard.) </p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida (carne asado)</strong><br /> En el camino hacemos un parada breve cerca de cementerio para tomar fotos y visitar la tumba y mausoleo del héroe nacional kirguiz, Baytik Baatyr. Traslado a Bishkek  <br /> <strong>Cena. </strong> </p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 3</td>\r\n<td rowspan=\"2\">\r\n<h3>Bishkek - Issyk-kul  </h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno </strong><br /> Traslado a Issyk-Kul, en el camino visita al torre Burana (s.11-12). También visita al museo local sobre la civilización, colección de Balbals, antiguas tumbas turcas.</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida.</strong><br /> Continuación de la visita hacia el lago Issyk-Kul, que es el segundo lago más grande en la montaña en el mundo después del lago Titicaca, en America del Sur. Llegada al lago: bañarse, montar en caballo, excursiones por los alrededores (opcional). Alojamiento en hotel.  <br /> <strong>Cena.</strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 4</td>\r\n<td rowspan=\"2\">\r\n<h3>Issyk-Kul - Kochkor Villgae - Son Kul Lake</h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno. </strong> <br /> Traslado al lago Son Kul, hacemos una parada en el pueblo Kochkor donde vemos la gente local hacer alfombras (llamadas shyrdaks), bordados, tejidos y otros trabajos hechos a mano. Visita al museo de Artesanía y tienda.</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida.</strong><br /> Llegada al lago Son Kul, alojamiento en yurtas. Visita a los alrededores. <br /> <strong>Cena.</strong> </p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 5</td>\r\n<td rowspan=\"2\">\r\n<h3>Son Kul Lake</h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno </strong><br /> Tiempo libre para explorer la zona y disfrutar del paisaje precioso.</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida </strong><br /> Tiempo libre para explorer la zona y disfrutar del paisaje precioso.<br /> <strong>Cena.</strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 6</td>\r\n<td rowspan=\"2\">\r\n<h3>Son Kul Lake - Tash-Rabat</h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno.</strong> <br /> Traslado a  Tash-Rabat sobre Dolon para llegar a Naryn. Llegada a Tash-Rabat y alojamiento en Yurtas.</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida.</strong><br /> Tash Rabat caravansarai es un edificio de piedra cuidadosamente restaurada que una vez albergó una posada en la Ruta de la Seda.<br /> <strong>Cena.</strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>DÍA 7</td>\r\n<td>\r\n<h3>Tash-Rabat - Kashgar</h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno. </strong><br /> Traslado a la frontera Kyrguizistán-China.  Pasar la frontera.    <br /> <br /> <strong><em>Fin de nuestros servicios ...</em></strong></p>\r\n</td>\r\n</tr>\r\n\r\n</table>', '24', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('fr', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n			<th style=\"width:10%;\">JOURS</td>\r\n			<th style=\"width:14%;\">TRAJET</td>\r\n			<th>ITINERAIRE</td>\r\n		</tr>\r\n		<tr>\r\n			<td>JOUR 1</td>\r\n			<td>\r\n			<h3>Bichkek</h3>\r\n			</td>\r\n			<td>\r\n			<p>Arrivée à Bichkek, transfert à l\'hôtel et l\'hébergement.<br />\r\n			<strong><em>Visite de Bichkek :</em></strong> monument de Manas, le principal héros national du peuple kirghize, musée d’État d’Histoire, promenade autour de la place d’Ala-Too de regarder la cérémonie de changement de la garde d\'honneur, Monument de l\'Indépendance, Place de la Vieille, Maison de Parlement.</p>\r\n\r\n			<p><strong>Déjeuner </strong><br />\r\n			Transfert à la Place de la Victoire, pour terminer la visite de la ville par une courte excursion.<br />\r\n			<strong>Dîner</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>JOUR 2</td>\r\n			<td>\r\n			<h3>Bichkek -<br />\r\n			Ala-Archa - Bichkek</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner</strong><br />\r\n			Transfert à parc naturel national d’Ala-Archa à 40 km de Bichkek pour une visite touristique. Parc national d’Ala Archa est l\'un des endroits rares de l\'habitat de la panthère de neige rare.</p>\r\n\r\n			<p><strong>Déjeuner barbecue</strong><br />\r\n			Sur la route, nous faisons plusieurs arrêts à proximité d\'un cimetière pour prendre des photos et visiter la tombe d\'un héros national kirghize - Baytik Baatyr, son mausolée.<br />\r\n			Retour à Bichkek.<br />\r\n			<strong>Dîner </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>JOUR 3</td>\r\n			<td>\r\n			<h3>Bichkek - Issyk-Koul</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner </strong><br />\r\n			Transfert à Issyk-Koul, sur la route visite à la Tour Burana (11-12c), le musée local racontant de cette civilisation, collection de Balbals, anciennes tombes turques.</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			Continuation en voiture du lac Issyk-Koul, le deuxième plus grand lac alpin dans le monde après le lac Titicaca en Amérique du Sud. À l\'arrivée, possible bain, randonnées, visites. Hébergement à l\'hôtel.<br />\r\n			<strong>Dîner</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>JOUR 4</td>\r\n			<td>\r\n			<h3>Issyk-Koul - village Kochkor - lac Son Koul</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner</strong><br />\r\n			Transfert à Son-Koul, en route visiter le village Kochkor de regarder les gens locaux font feutre, divers tapis (appelés shyrdaks), la broderie, la filature, et d\'autres choses de l\'artisanat. Visite d’un musée de l\'artisanat et une boutique, la finition avec de petites décorations d\'arbres de Noël.</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			Arrivée à Son Koul, l\'hébergement dans des yourtes, promenade aux alentours.<br />\r\n			<strong>Dîner </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>JOUR 5</td>\r\n			<td>\r\n			<h3>lac Son Koul</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner </strong><br />\r\n			Temps libre pour explorer la région et profiter des paysages paisibles et pittoresques.</p>\r\n\r\n			<p><strong>Déjeuner </strong><br />\r\n			Temps libre pour explorer la région et profiter des paysages paisibles et pittoresques.<br />\r\n			<strong>Dîner </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>JOUR 6</td>\r\n			<td>\r\n			<h3>lac Son Koul - Tach-Rabat</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner</strong><br />\r\n			Transfert à Tach-Rabat via passage Dolonet la ville de Naryn.<br />\r\n			Arrivée à Tach-Rabatet l\'hébergement dans les yourtes.</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			Tach-Rabat caravansérail est un bâtiment en pierre soigneusement restaurée qui abritait autrefois une auberge sur la Grande Route de la Soie.<br />\r\n			<strong>Dîner</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>JOUR 7</td>\r\n			<td>\r\n			<h3>Tach-Rabat - Kachgar</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner</strong><br />\r\n			Transfert vers la frontière kirghize-chinois. Passage de la frontière.</p>\r\n\r\n			<p><strong><em>Fin de nos services ...</em></strong></p>\r\n			</td>\r\n		</tr>\r\n</table>\r\n\r\n\r\n', '24', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('en', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n            <th style=\"width:8%;\">Days</th>\r\n            <th style=\"width:14%;\">Waypoint</th>\r\n            <th>Subjects of itinerary</th>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 1</td>\r\n			<td>\r\n			<h3>Ashgabat</h3>\r\n			</td>\r\n			<td>\r\n			<p>Arrival to Ashgabat, transfer to hotel and accommodation.<br />\r\n			<strong><em>Sightseeing tour outside of Ashgabat:</em></strong> Transfer to Old Nisa (UNESCO World Heritage Site), the earliest of Parthian Empire capitals, Kipchak village to visit a complex of the biggest mosque in Turkmenistan and the family mausoleum of the first President.</p>\r\n\r\n			<p><strong>Lunch</strong><br />\r\n			<strong><em>Sightseeing tour of Ashgabat:</em></strong> Park of independence, Neutrality Square famous for its Arch of Neutrality topped by a rotating gold-plated statue of the first President of Turkmenistan, Earthquake Memorial, National Museum of History and Ethnography<br />\r\n			<strong>Dinner</strong><strong> </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 2</td>\r\n			<td>\r\n			<h3>Ashgabat - Mary</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong><br />\r\n			Transfer to Mary on the way visit city of Bagabad, featuring the citadel of an important Timurid-period centre and the ruins of the Seyit Jemaleddin mosque.</p>\r\n\r\n			<p><strong>Lunch.</strong><br />\r\n			Arrival to Mary, and accommodation in hotel. Sightseeing tour of Mary, the capital of the present-day Mary region and the centre of the Murghab oasis, an archaeological “Klondike” of Turkmenistan. <strong> </strong><br />\r\n			<strong>Dinner. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 3</td>\r\n			<td>\r\n			<h3>Gonur Depe (Margush)</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast </strong><br />\r\n			<em>Transfer to Gunur Depe </em>capital of Margush country in the past, arrival to important archaeological site, the centre of a remarkable Bronze Age civilisation, discovered in 1972</p>\r\n\r\n			<p><strong>Lunch en route.</strong><br />\r\n			<em>Transfer back to Mary, sightseeing of the city: </em>visit local History Museum.<br />\r\n			<strong>Dinner.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 4</td>\r\n			<td>\r\n			<h3>Mary - Ashgabat</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast. </strong><br />\r\n			Transfer to Ashgabat, on the visit to Bayram-Ali archeological park (under Unesco), also visit VI-IX cc. AD forts of big and little Kyz-Kalas, the splendid XII c. mausoleums of Sultan Sanjar and Muhammed ibn-Zeyd</p>\r\n\r\n			<p><strong>Lunch.</strong><br />\r\n			Visit to ruins of Abiverd, once an important trading town Arrival to Ashgabat and accommodation in hotel<br />\r\n			<strong>Dinner. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 5</td>\r\n			<td>\r\n			<h3>Ashgabat - Dashoguz - Ashgabat</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast </strong><br />\r\n			Transfer to the airport for the flight to Dashoguz. On arrival, transfer to Kunya-Urgench for sightseeing. In Kunya-Urgench (UNESCO World Heritage Site), visit mausoleum of Turabek Hanum</p>\r\n\r\n			<p><strong>Lunch.</strong><br />\r\n			Visit to mausoleum of Sultan Tekesh, minaret of Kutlug-Timur, mausoleum of Il-Arslan and a mysterious mound of Kirk-Mullah. In the afternoon, return to Dashoguz for the flight to Ashgabat.<br />\r\n			<strong>Dinner. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 6</td>\r\n			<td>\r\n			<h3>Ashgabat</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast.</strong><br />\r\n			<em>Transfer to the Ashgabat International Airport, flight back home.</em></p>\r\n\r\n			<p><strong><em>End of our services ... </em></strong></p>\r\n			</td>\r\n		</tr>\r\n</table>', '25', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('de', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n			<th style=\"width:10%;\">TAGE</td>\r\n			<th style=\"width:14%;\">REISEROUTE</td>\r\n			<th>THEMEN DER REISEROUTE</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">1. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Aschgabat</h3>\r\n			</td>\r\n			<td>\r\n			<p>Anreise zu Aschgabat, Ankunft ins Hotel und Unterbringung.<br />\r\n			<strong><em>Stadtrundfahrt außer Aschgabat</em></strong>: Anreise zu Altem Nisa (UNESCO-Welterbe-Seite), die frühste von Parthischen Reich-Hauptstädte, Dorf Kiptschak, um größte Moschee in Turkmenistan und das Familienmausoleum des ersten Präsidenten zu besuchen.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen</strong><br />\r\n			<strong><em>Stadtrundfahrt von Aschgabat:</em></strong> Park der Unabhängigkeit, Platz der Neutralität, der wegen seines Bogens der Neutralität berühmt ist, durch eine rotierende vergoldete Bildsäule des ersten Präsidenten Turkmenistans, Erdbeben-Denkmal, Nationales Museum der Geschichte und Völkerbeschreibung.<br />\r\n			<strong>Abendessen.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">2. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Aschgabat - Mary</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstück</strong><br />\r\n			Während der Beförderung nach Mary Besuch der Stadt Bagadad, dir Zitadelle eines wichtigen Timurid-Periode-Zentrums und der Ruinen des Seyit Jemaleddin Moschee.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagesen</strong><br />\r\n			Anreise zu Mary und Unterbringung im Hotel.<br />\r\n			<strong><em>Stadtrundfahrt von Mary</em></strong>, die Hauptstadt des heutigen Gebiets von Mary und das Zentrum der Murghab Oase, ein archäologischer \"Klondike\" Turkmenistans.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">3. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Gonur Depe (Margusсh)</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstück</strong><br />\r\n			Beförderung nach Gonur Depe, Hauptstadt des Margusch Landes in der Vergangenheit, Ankunft zum wichtigen archäologischen Ort, dem Zentrum einer bemerkenswerten Bronsezeit-Zivilisation, entdeckt 1972.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen unterwegs</strong><br />\r\n			Beförderung nach Mary, Besichtigung von Sehenswürdigkeiten der Stadt: Besuch des lokalen Geschichtsmuseums.<br />\r\n			<strong>Abendessen.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">4. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Mary - Aschgabat</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstück</strong><br />\r\n			Beförderung nach Aschgabat, während des Besuchs Bayram-Ali, archäologischer Park (unter UNESCO). Besuch im VI-IX Jh. a. Chr. Toren von großem und kleinem Kyz-Kalas, herrlichen Mausoleen von Sultan Sanjar und Muhammed ibn-Zeyd.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen</strong><br />\r\n			Besuch in Ruinen von Abiverd, einmal eine wichtige Handelsstadt. Anreise zu Aschgabat und Unterbringung im Hotel.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">5. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Aschgabat - Daschoguz - Aschgabat</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstück</strong><br />\r\n			Beförderung nach Flughafen für den Flug zu Daschoguz. Nach der Ankunft, Beförderung nach Kunja Urgentsch für die Besichtigung von Sehenswürdigkeiten. In Kunja Urgentsch (UNESCO-Welterbe-Ort), Besuch vom Mausoleum von Turabek Hanum.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen</strong><br />\r\n			Besuch im Mausoleum von Sultan Tekesh, Minarett von Kutlug-Timur, Mausoleum von Il-Arslan und einem mysteriösen Erdhügel des Kirk-Mullas. Am Nachmittag &nbsp;zurück zu Daschoguz für den Flug nach Aschgabat.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>6. TAG</td>\r\n			<td>\r\n			<h3>Aschgabat</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstück</strong><br />\r\n			Beförderung nach Aschgabat Internationalen Flughafen, Flug zurück heim.<br />\r\n			<br />\r\n			<strong>Ende unserer Leistungen …</strong></p>\r\n			</td>\r\n		</tr>\r\n</table>', '25', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('es', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n			<th style=\"width:10%;\">DÍAS</td>\r\n			<th style=\"width:14%;\">TRAYECTO</td>\r\n			<th>ITINERARIO</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DÍA 1</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Ashgabat</h3>\r\n			</td>\r\n			<td>\r\n			<p>Llegada a Ashgabat, traslado y alojamiento en hotel.<br />\r\n			<strong><em>Excursión por las afueras de Ashgabat:</em></strong> Traslado a Antigua Nisa (Patrimonio de la Humanidad declarado por Unesco), la capital del imperio Parfiana, Visita al pueblo Kipchak para ver la mesquita más grande de Turkmenistán y el mausoleo familiar del primer Presidente de la República.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Comida</strong><br />\r\n			<strong><em>Visitas en Ashgabat:</em></strong> Parque de la Independencia, Plaza de Neutralidad famoso con su Arco de Neutralidad donde se erige el estatua giratorio de oro plateado del primer presidente de Turkmenistán, Memorial de Terremoto, Museo Nacional de Historia y Etnografía.<br />\r\n			<strong>Cena.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DÍA 2</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Ashgabat - Mary</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Desayuno</strong><br />\r\n			Traslado a Mary, en el camino visita a la ciudad de Bagabad, on the way visit city of Bagabad, importatne ciudadela del período timurida, ruinas de la mezquita Seyit Jamaliddin.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Comida.</strong><br />\r\n			Llegada a Mary y alojamiento en hotel. Visitas en Mary, la capital de la region de Mary y el centro del oasis de Murgab, \"Klondike\" arqueológico de Turkmenistán.<br />\r\n			<strong>Cena.</strong>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DÍA 3</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Gonur Depe (Margush)</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Desayuno </strong><br />\r\n			<em>Traslado a Gunur Depe</em> capital del estado Margush en el pasado, llegada al importante sitio arqueológico, centro de la civilización de la Edad de Bronce, descubierta en 1972.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Comida en la ruta.</strong><br />\r\n			<em>Traslado de regreso a Mary, visitas en la ciudad : </em> visita al museo local de Historia.<br />\r\n			<strong>Cena.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DÍA 4</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Mary - Ashgabat</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Desayuno. </strong>&nbsp;<br />\r\n			Traslado a Ashgabat, visita al parque arqueológico Bayram-Ali (bajo la protección de Unesco), también visita a las fortalezas Kyz-Kalas (Grande y Pequeña(VI-IX AC), espléndidas mausoleos del siglo XII: Sulton Sanjar y Muhammad ibn Zeyd.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Comida.</strong><br />\r\n			Visita a las ruinas Abiverd, una de las importantes ciudades comerciales. Llegada a Ashgabat y alojamiento en hotel<br />\r\n			<strong>Cena.</strong>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DÍA 5</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Ashgabat - Dashoguz - Ashgabat</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Desayuno </strong><br />\r\n			Traslado al aeropuerto para tomar el vuelo a Dashoguz. Llegada y traslado a Kunya-Urgench (Patrimonio de la Humanidad por UNESCO), visita al mausoleuo de Turabek Hanum</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Comida.</strong><br />\r\n			Visita al mausoleuo de Sultan Tekesh, minarete Kutlug-Timur, mausoleo de Arslan-II y misterioso montículo Kirk-Mullah. Por la tarde, retorno a Dashoguz para tomar el vuelo Ashgabat.<br />\r\n			<strong>Cena.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DÍA 6</td>\r\n			<td>\r\n			<h3>Ashgabat</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Desayuno.</strong><br />\r\n			<em>Traslado al Aeropuerto Internacional de Ashgabat , vuelo al destino final.</em></p>\r\n\r\n			<p><strong><em>Fin de nuestros servicios ...</em></strong></p>\r\n			</td>\r\n		</tr>\r\n\r\n</table>\r\n', '25', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('fr', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n			<th style=\"width:10%;\">JOURS</td>\r\n			<th style=\"width:14%;\">TRAJET</td>\r\n			<th>ITINERAIRE</td>\r\n		</tr>\r\n		<tr>\r\n			<td>JOUR 1</td>\r\n			<td>\r\n			<h3>Achgabat</h3>\r\n			</td>\r\n			<td>\r\n			<p>Arrivée à Achgabat, transfert à l\'hôtel et l\'hébergement.<br />\r\n			<strong><em>Visite de l\'extérieur d’Achgabat :</em></strong> l\'ancienne Nisa (site du patrimoine mondial de l\'UNESCO), la première capitale de l’empire de Parthes, village Kipchak avec le complexe de la plus grande mosquée au Turkménistan et le mausolée familial du premier président.</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			<strong><em>Visite d\'Achgabat:</em></strong> Parc de l\'indépendance, Place de la Neutralité, célèbre pour son Arche de la Neutralité surmontée d\'une statue dorée de rotation du premier Président du Turkménistan, Mémorialde Tremblement de terre, Musée national d\'histoire et d\'ethnographie<br />\r\n			<strong>Dîner</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>JOUR 2</td>\r\n			<td>\r\n			<h3>Achgabat - Mary</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner</strong><br />\r\n			Transfert à Mary sur la route, visite de la ville Bagabad, avec sa citadelle, d\'un important centre de la période de Timuride et les ruines de la mosquée Seyit Jemaleddin.</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			<strong><em>Visite de Mary :</em></strong> la capitale de la région de Mary actuelle et le centre de l\'oasis de Murghob, un «Pactole» archéologique du Turkménistan.<br />\r\n			<strong>Dîner </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>JOUR 3</td>\r\n			<td>\r\n			<h3>Gonour Depe (Margiane)</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner </strong><br />\r\n			Transfert à Gonour Depe la capitale de Margiane pays dans le passé, l\'arrivée au site archéologique importante, le centre d’une remarquable civilisation du bronze, découvert en 1972.</p>\r\n\r\n			<p><strong>Déjeuner en route</strong><br />\r\n			Retour à Mary. Visite du musée d\'histoire locale.<br />\r\n			<strong>Dîner</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>JOUR 4</td>\r\n			<td>\r\n			<h3>Mary - Achgabat</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner</strong><br />\r\n			Transfert à Achgabat, sur la route visite du parc archéologique Bayram-Ali (en vertu de l\'Unesco), également de grande et petite forteresse de Kyz-Kalas VI-IX s. après J-C, les splendides mausolées de Sultan Sanjar et Muhammed ibn-Zeyd de XII s.</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			Visite des ruines d’Abiverd, une fois une ville commerciale importante<br />\r\n			Arrivée à Achgabat et l\'hébergement à l\'hôtel<br />\r\n			<strong>Dîner </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>JOUR 5</td>\r\n			<td>\r\n			<h3>Achgabat - Dachogouz - Achgabat</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner </strong><br />\r\n			Transfert à l\'aéroport pour le vol à Dachogouz.<br />\r\n			Arrivée et transfert à Kounya-Ourguentch.<br />\r\n			<strong><em>Visite de Kounya Ourguentch :</em></strong> Kounya-Ourguentch (site du patrimoine mondial de l\'UNESCO), le mausolée de Tourabek Khanoum</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			<strong><em>Poursuite de la visite de Kounya Ourguentch :</em></strong> le mausolée du Sultan Tekech, minaret de Koutloug-Timour, le mausolée d’Il-Arslan et un monticule mystérieuse de Kirk-mollakh.<br />\r\n			Dans l\'après-midi, retour à Dachogouz pour le vol à destination d’Achgabat.<br />\r\n			<strong>Dîner </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>JOUR 6</td>\r\n			<td>\r\n			<h3>Achgabat</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner</strong><br />\r\n			Transfert à l\'aéroport international d’Achgabat, vol de retour.</p>\r\n\r\n			<p><strong><em>Fin de nos services ... </em></strong></p>\r\n			</td>\r\n		</tr>\r\n</table>', '25', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('en', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n            <th style=\"width:8%;\">Days</th>\r\n            <th style=\"width:14%;\">Waypoint</th>\r\n            <th>Subjects of itinerary</th>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 1</td>\r\n			<td>\r\n			<h3>Ashgabat</h3>\r\n			</td>\r\n			<td>\r\n			<p>Arrival to Ashgabat, transfer to hotel and accommodation.</p>\r\n\r\n			<p><strong>Lunch</strong><br />\r\n			<strong><em>Sightseeing tour outside of Ashgabat:</em></strong> Transfer to Old Nisa (UNESCO World Heritage Site), the earliest of Parthian Empire capitals.<br />\r\n			Transfer back to Ashgabat, visit to Park of independence, National Museum of History and Ethnography.<br />\r\n			<strong>Dinner</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 2</td>\r\n			<td>\r\n			<h3>Ashgabat - Mary</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong><br />\r\n			Morning flight to Mary. Arrival to Mary, transfer towards Bayram-Ali, the contemporary re-incarnation Ancient Merv. One of the major archaeological sites of Central Asia. Visit to VI-IX cc. AD forts of Big and Little Kyz-Kalas, mausoleums of Sultan Sanjar and Muhammed ibn-Zeyd XII c.,</p>\r\n\r\n			<p><strong>Lunch.</strong><br />\r\n			Transfer back to Mary, sightseeing tour: visit to local History Museum.<br />\r\n			<strong>Dinner. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 3</td>\r\n			<td>\r\n			<h3>Mary - Ashgabat</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast </strong><br />\r\n			Transfer to Ashgabat, on the way stop at Kaahka village, sightseeing tour of Nadir-Shah fortress (18c).</p>\r\n\r\n			<p><strong>Lunch.</strong><br />\r\n			Arrival to Ashgabat and accommodation in hotel.<br />\r\n			<strong>Dinner.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 4</td>\r\n			<td>\r\n			<h3>Ashgabat - Bokurdak - Darvaza</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast. </strong><br />\r\n			Transfer to Bakurdak village visit to local families and see traditional activities: embroidery, pichme (traditional bakery), baking bread and cooking ishlekli (meat pies). See making felt if in season and milking camels.</p>\r\n\r\n			<p><strong>Lunch in local family.</strong><br />\r\n			Transfer to Darvaza Flaming Crater, which located in desert, upon arrival sightseeing of surrounding and the flaming crater. Accommodation in tents.<br />\r\n			<strong>Dinner around the fireplace. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 5</td>\r\n			<td>\r\n			<h3>Darvaza - Dashoguz</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast </strong><br />\r\n			Transfer to Dashoguz, on the visit to Izmukshir, an ancient stronghold and the mausoleum of Abulkasim az-Zamakhshari.</p>\r\n\r\n			<p><strong>Lunch.</strong><br />\r\n			Arrival to Dashoguz, visit to ceramic work shop. Accommodation in hotel.<br />\r\n			<strong>Dinner. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 6</td>\r\n			<td>\r\n			<h3>Dashoguz - Kunya Urgench - Ashgabat</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast.</strong><br />\r\n			Transfer to Kunya Urgench.<br />\r\n			<strong><em>Sightseeing tour in Kunya Urgench:</em></strong> visit to mausoleum of Turabek Hanum, minaret of Kutlug Timur, mausoleum of Sultan Tekesh,</p>\r\n\r\n			<p><strong>Lunch.</strong><br />\r\n			<strong><em>Sightseeing tour in Kunya Urgench:</em></strong> visit to Kyrk-Mollah mound, the fabled al-Mamun’s Academy of Sciences; and mausoleum of Il-Arslan.<br />\r\n			Transfer to Dashoguz Domestic airport, flight to Ashgabat. Arrival to Ashgabat, transfer and accommodation in hotel.<br />\r\n			<strong>Dinner. </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 7</td>\r\n			<td>\r\n			<h3>Ashgabat</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong><br />\r\n			<strong><em>Sightseeing tour of Ashgabat:</em></strong> visit to Talkuchka Sunday market, transfer to Kipchak village to visit biggest mosque in Turkmenistan and the family mausoleum of the first President, visit Geok-Depe to see pure-blood Akhal-Tekke horses at Arkadash stud farm,</p>\r\n\r\n			<p><strong>Lunch. </strong><br />\r\n			<strong><em>Sightseeing tour of Ashgabat:</em></strong> visit to Tekke stronghold Russian-Turkmen battle field in 19c., Kov-Ata cave a unique hot-water underground lake.<br />\r\n			Dinner.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 8</td>\r\n			<td>\r\n			<h3>Ashgabat</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast </strong><br />\r\n			Transfer to Ashgabat International Airport, flight back home.</p>\r\n\r\n			<p><strong><em>End of our services ...</em></strong></p>\r\n			</td>\r\n		</tr>\r\n</table>', '26', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('de', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n			<th style=\"width:10%;\">TAGE</td>\r\n			<th style=\"width:14%;\">REISEROUTE</td>\r\n			<th>THEMEN DER REISEROUTE</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">1. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3><em>Aschgabat</em></h3>\r\n			</td>\r\n			<td>\r\n			<p>Anreise zu Aschgabat, Ankunft ins Hotel und Unterbringung</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen</strong><br />\r\n			<strong><em>Stadtrundfahrt außer Aschgabat</em></strong>: Anreise zu Altem Nisa (UNESCO-Welterbe-Seite), die frühste von Parthischen Reich-Hauptstädte.<br />\r\n			Beförderung zurück nach Aschgabat, Besuch des Parkes der Unabhängigkeit, Nationales Museums der Geschichte und Völkerbeschreibung.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">2. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Aschgabat - Mary</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstück</strong><br />\r\n			Morgenflug nach Mary. Anreise nach Mary, Anreise zu Bayram-Ali, die zeitgenössische Reinkarnation Alter Merv. Eine der archäologischen Hauptseiten Zentralasiens. Besuch im VI-IX Jh. a. Chr. Toren von großem und kleinem Kyz-Kalas, Mausoleen von Sultan Sanjar und Muhammed ibn-Zeyd XII Jh.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen</strong><br />\r\n			Beförderung zurück nach Mary, Stadtrundfahrt: Besuch des lokales Geschichtsmuseum.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">3. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Mary - Aschgabat</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstück</strong><br />\r\n			Beförderung nach Aschgabat, Zwischenaufenthalt im Dorf Kaahka, Stadtrundfahrt der Festung des Nadir-Schahs (18 Jh.)</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen</strong><br />\r\n			Anreise zu Aschgabat und Unterbringung im Hotel.<br />\r\n			<strong>Abendessen.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">4. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Aschgabat - Bokurdak - Darwasa</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstuck</strong><br />\r\n			Beförderung nach Dorf Bakurdak, Besuch bei lokalen Familien und Kenntnis mit den traditionellen Tätigkeiten: Stickerei, pichme (traditionelle Bäckerei), Brot backen und ishlekli (Fleisch-Kuchen) kochen. Dazu noch Milchmelken (hängt von der Saison ab).</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen in der lokalen Familie</strong><br />\r\n			Beförderung nach Darwasa brennenden Krater, der sich in der Wüste befindet, vor dieser Anreise - Besichtigung von Sehenswürdigkeiten der Umgebung und des brennenden Kraters. Unterbringung in Zelten.<br />\r\n			Abendessen vor dem Lagerfeuer.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">5. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Darwasa - Daschogus</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstück</strong><br />\r\n			Beförderung nach Daschogus während des Besuchs in Ismuschir, eine alte Zitadelle und das Mausoleum von Abulkasim as-Zamakhschari.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen</strong><br />\r\n			Anreise zu Daschogus, Besuch bei der keramischen Werkstatt. Unterbringung im Hotel.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">6. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Daschogus - Kunya Urgench - Aschgabat</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstück</strong><br />\r\n			Beförderung nach Kunja Urgentsch.<br />\r\n			Stadtrundfahrt in Kunja Urgentsch: Besuch des Mausoleum von Turabek Hanum, Minarett von Kutlug Timur, Mausoleum von Sultan Tekesch.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen.</strong><br />\r\n			<strong><em>Stadtrundfahrt in Kunja Urgentsch</em></strong>: Besuch des Kyrk-Mollah Erdhügels, der Akademie der Wissenschaften von sagenhaften al-Mamun; und Mausoleum von Il-Arslan.<br />\r\n			Beförderung nach Daschogus Innenflughafen, Flug nach Aschgabat. Anreise zu Aschgabat, Anreise ins Hotel und Unterbringung.<br />\r\n			<strong>Abendessen.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">7. TAG</td>\r\n			<td rowspan=\"2\">\r\n			<h3>Aschgabat</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstück.</strong><br />\r\n			<strong><em>Stadtrundfahrt von Aschgabat:</em></strong> Besuch beim Talkuchka Sonntag Markt, Beförderung nach Dorf Kiptschak, um größte Moschee in Turkmenistan und das Familienmausoleum des ersten Präsidenten zu besuchen, Besuch bei Geok-Depe, um reinrassige Akhal-Tekke Pferde am Arkadasch Gestüt zu sehen.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Mittagessen.</strong><br />\r\n			<strong><em>Stadtrundfahrt von Ashgabat:</em></strong> Besuchen bei&nbsp; Tekke Zitadelle Russisch-turkmenisches Kampffeld in 19 Jh., Kov-Ata Höhle&nbsp; ein&nbsp; einzigartiger unterirdische Warmwassersee.<br />\r\n			<strong>Abendessen.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>8. TAG</td>\r\n			<td>\r\n			<h3>Aschgabat</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstück.</strong><br />\r\n			Beförderung nach Aschgabat Internationalen Flughafen, Flug zurück heim.<br />\r\n			<br />\r\n			<strong>Ende unserer Leistungen … </strong></p>\r\n			</td>\r\n		</tr>\r\n</table>', '26', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('es', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n			<th style=\"width:10%;\">DÍAS</td>\r\n			<th style=\"width:14%;\">TRAYECTO</td>\r\n			<th>ITINERARIO</td>\r\n		</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 1</td>\r\n<td rowspan=\"2\">\r\n<h3><em>Ashgabat</em></h3>\r\n</td>\r\n<td>\r\n<p>Llegada a Ashgabat, traslado al hotel alojamiento</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida</strong><br /> <strong><em>Visitas por los alrededores de  Ashgabat: </em></strong> Traslado a la vieja Nisa  (declarado por la UNESCO Patrimonio de la Humanidad), la capital del antigua estado Parfiana. Traslado a Ashgabat, visita al parque de la independencia, Museo nacional de Historia y Etnografía. <br /> <strong>Cena </strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 2</td>\r\n<td rowspan=\"2\">\r\n<h3>Ashgabat - Mary</h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno</strong><br /> Vuelo temprano a Mary. Llegada a Mary, traslado a Bayram-Ali, reincarnación contemporanea de la Antigua Merv. Uno de los principals sitios arqueológicos de Asia Central. Visita a las fortalezas Grande y Pequeña de Kyz-Kalas (s.VI-IX AC) mausoleos de Sultan Sanjar y  Muhammed ibn-Zeyd (s. XII )</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida.</strong><br /> Traslado de retorno a Mary, visita al museo local de Historia <br /> <strong>Cena. </strong> </p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 3</td>\r\n<td rowspan=\"2\">\r\n<h3>Mary - Ashgabat</h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno </strong><br /> Llegada a Ashgabat, en el camino parada en  Kaahka: visita a fortaleza Nadir-Shah (s.18)</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida.</strong><br /> Llegada a Ashgabat y alojamiento en hotel.  <br /> <strong>Cena.</strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 4</td>\r\n<td rowspan=\"2\">\r\n<h3>Ashgabat - Bokurdak - Darvaza</h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno. </strong> <br /> Traslado al pueblo Bakurdak: visita a las familias locales y ver actividades tradicionales: bordados, pichme,  (hornear el pan tradicional), preparar ishlekli (tarta de carne).Ver hacer tejidos de la lana de camello y ordeñar el camello. </p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida en la familia local.</strong><br /> Traslado a Darvaza, Crater de fuego que está situada en el desierto, despues de la llegada visita por los alrededores del crater. Alojamiento en tiendas de campaña.  <br /> <strong>Cena alrededor de la hoguera.</strong> </p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 5</td>\r\n<td rowspan=\"2\">\r\n<h3>Darvaza - Dashoguz </h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno </strong><br /> Traslado a Dashoguz, visita a la Antigua Fortaleza Izmukshir y mausoleo de Abulkasim az-Zamakhshari. </p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida.</strong><br /> Llegada a Dashoguz, visita a la tienda de céramica. Alojamiento en hotel.  <br /> <strong>Cena. </strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 6</td>\r\n<td rowspan=\"2\">\r\n<h3>Dashoguz - Kunya Urgench - Ashgabat</h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno.</strong> <br /> Traslado a Kunya Urgench.  <br /> <strong><em>Visitas en Kunya Urgench: </em></strong> visita al mausoleo de Turabek Hanum, minarete de Kutlug Timur, mausoleo de Sultan Tekesh,</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida.</strong><br /> <strong><em>Visitas en Kunya Urgench: </em></strong> visita al montículo de Kyrk-Mollah , the fabled al-Mamun’s Academy of Sciences; and mausoleum of Il-Arslan.<br /> Traslado al aeropuerto local de Dashoguz para tomar el vuelo con destino a Ashgabat. Llegada a Ashgabat, traslado y alojamiento en hotel.  <br /> <strong>Cena. </strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"2\">DÍA 7</td>\r\n<td rowspan=\"2\">\r\n<h3>Ashgabat</h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno</strong> <br /> <strong><em>Visitas en Ashgabat:</em></strong> visita al Mercado de domingo de  Talkuchka, traslado al pueblo de Kipchak para visitar la mezquita más grande de  Turkmenistan y el mausoleo familiar del primer   Presidente, visita Geok-Depe para ver los caballos de sangre pura de raza Akhal-Tekke en la granja de caballos de Arkadash.</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>Comida. </strong><br /> <strong><em>Visitas en Ashgabat:</em></strong> visita a Tekke, campo de batalla entre Rusia-Turkmenistán en el siglo 19., La cueva Kov-Ata, lago subterraneo con agua calente  <br />Cena.</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>DÍA  8</td>\r\n<td>\r\n<h3>Ashgabat</h3>\r\n</td>\r\n<td>\r\n<p><strong>Desayuno </strong><br /> Traslado al Aeropuerto Internacional de Ashgabat, vuelo al destino final.</p>\r\n<p><strong><em>Fin de nuestros servicios ...</em></strong></p>\r\n</td>\r\n</tr>\r\n\r\n</table>', '26', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('fr', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n		<tr>\r\n			<th style=\"width:10%;\">JOURS</td>\r\n			<th style=\"width:14%;\">TRAJET</td>\r\n			<th>ITINERAIRE</td>\r\n		</tr>\r\n		<tr>\r\n			<td>JOUR 1</td>\r\n			<td>\r\n			<h3>Achgabat</h3>\r\n			</td>\r\n			<td>\r\n			<p>Arrivée à Achgabat, transfert et l\'hébergement à l\'hôtel.</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			<strong><em>Visite à l\'extérieur d’Achgabat :</em></strong> l\'ancienne Nisa (site du patrimoine mondial de l\'UNESCO), la première des capitales Parthes.<br />\r\n			Retour à Achgabat. Visite du parc de l\'indépendance, le Musée national d\'histoire et d\'ethnographie.<br />\r\n			<strong>Dîner</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>JOUR 2</td>\r\n			<td>\r\n			<h3>Achgabat - Mary</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner</strong><br />\r\n			Le vol matinal à Mary. Arrivée et transfert à Bayram-Ali, la réincarnation contemporaine de l’Ancienne Merv, l\'un des principaux sites archéologiques de l\'Asie Centrale, de grande et petite forteresse de Kyz-Kalas VI-IX s. après J-C, les splendides mausolées de Sultan Sanjar et Muhammed-ibn-Zeyd de XII s.</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			Retour à Mary.<br />\r\n			<strong><em>Visite de Mary :</em></strong> le musée d\'histoire locale.<br />\r\n			<strong>Dîner </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>JOUR 3</td>\r\n			<td>\r\n			<h3>Mary - Achgabat</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner </strong><br />\r\n			Transfert à Achgabat, sur la route, arrêt au village de Kakhka, visite de Nadir Shah-forteresse (18s).</p>\r\n\r\n			<p><strong>Déjeuner en route</strong><br />\r\n			Arrivée à Achgabat et l\'hébergement à l\'hôtel.<br />\r\n			<strong>Dîner</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>JOUR 4</td>\r\n			<td>\r\n			<h3>Achgabat - Bokourdak - Darvaza</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner</strong><br />\r\n			Transfert à Bakourdak village.Visite des familles locales et les activités traditionnelles: la broderie, pichme (de boulangerie traditionnelle), la cuisson du pain et ishkekli de cuisson (des pâtés à la viande), fabrication du feutre si en saison et chamelles laitières.</p>\r\n\r\n			<p><strong>Déjeuner dans une famille locale</strong><br />\r\n			Transfert à Darvaza cratère flambant, qui est situé dans le désert.<br />\r\n			Arrivée et visite d’environ du cratère embrasant. Hébergement sous tentes.<br />\r\n			<strong>Dîner autour de la cheminée </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>JOUR 5</td>\r\n			<td>\r\n			<h3>Darvaza - Dachogouz</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner </strong><br />\r\n			Transfert à Dachoguoz, sur la route visite d’Izmoukchir, une ancienne forteresse et le mausolée d’Abulkasim-az-Zamakhcharii.</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			Arrivée à Dachogouz, visite d’atelier de céramique. Hébergement à l\'hôtel.<br />\r\n			<strong>Dîner </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>JOUR 6</td>\r\n			<td>\r\n			<h3>Dachogouz - Kunya Urgench - Achgabat</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner</strong><br />\r\n			Transfert à Kounya Ourguentch.<br />\r\n			<strong><em>Visite de Kounya Ourguentch :</em></strong> mausolée de Tourabek Khanoum, minaret de Koutloug Timour, le mausolée de Soultan Tekech.</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			<strong><em>Poursuite de la visite de <strong><em>Kounya Ourguentch :</em></strong> </em></strong>monticulede Kyrk-Mollah, l\'Académie des sciencesdu légendaire al-Mamoun ; le mausolée de Il-Arslan.<br />\r\n			Transfert à l\'aéroport domestique de Dachogouz, vol à destination d’Achgabat.<br />\r\n			Arrivée à Achgabat, transfert et installation à l\'hôtel.<br />\r\n			<strong><strong>Dîner </strong></strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>JOUR 7</td>\r\n			<td>\r\n			<h3>Achgabat</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner</strong><br />\r\n			<strong><em>Visite d’Achgabat :</em></strong> marché de dimanche Talkouchka, transfert au village de Kipchak pour visiter la plus grande mosquée au Turkménistan et le mausolée familial du premier président, Geok-Depe pour voir les chevauxpur-sangd’Akhal-Tekke à Arkadash haras</p>\r\n\r\n			<p><strong>Déjeuner</strong><br />\r\n			<strong><em>Poursuite de la visite d\'Achgabat : </em></strong>fief de Tekke champ de bataille russe-turkmène en 19s, grotte Kov-Ata un lac souterrain d\'eau chaude unique.<br />\r\n			<strong>Dîner</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>JOUR 8</td>\r\n			<td>\r\n			<h3>Achgabat</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Petit déjeuner </strong><br />\r\n			Transfert à l\'aéroport international d’Achgabat, vol de retour.</p>\r\n\r\n			<p><strong><em>Fin de nos services ...</em></strong></p>\r\n			</td>\r\n		</tr>\r\n</table>\r\n\r\n\r\n', '26', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('it', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n	<tbody>\r\n		<tr>\r\n			<th style=\"width:8%;\">Giorni</th>\r\n			<th style=\"width:14%;\">Tappe</th>\r\n			<th style=\"width:8%;\">Time</th>\r\n			<th>Soggetti dell’itinerario</th>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">GIORNO 1</td>\r\n			<td rowspan=\"2\">Tashkent</td>\r\n			<td>AM</td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p>Arrivo a Tashkent. Trasferimento e sistemazione in albergo.<br />\r\n			<strong>Cena.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">GIORNO 2</td>\r\n			<td rowspan=\"2\">Tashkent – Urgench - Khiva</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Colazione.</strong><br />\r\n			<em>Giro turistico a Tashkent:</em> La parte vecchia della città di Tashkent.<br />\r\n			Visita al complesso Khast Imam, composto dalla Madrasa Barak Khan, dalla Moschea Till Sheikh - il deposito del famoso Corano Osman e dai capelli d\'oro del profeta Maometto; La Moschea Juma di Khoja Ahrar Vali.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Pranzo.</strong><br />\r\n			Guidi a Parkent in 45 km ad est da Tashkent. Lì visiteremo campi e giardini di uva, inoltre visiteremo il castello di viti Hamkor situato vicino al villaggio di Namdanak.<br />\r\n			Ritorno a Tashkent. Tempo libero.<br />\r\n			<strong>Cena.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">GIORNO 3</td>\r\n			<td rowspan=\"2\">Khiva</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Colazione.</strong><br />\r\n			Trasferimento in aeroporto, partenza per Urgench alle 07:25 volo HY-011. Arrivo a Urgench alle 08:55.<br />\r\n			Visita alla Urgench State University, incontro con docenti universitari, discussione sui temi della bonifica, eliminazione dei sali del suolo, prospettive di sviluppo agricolo in Karakalpakstan e nella regione di Khorezm.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Pranzo.</strong><br />\r\n			Una visita alla fabbrica di succhi di Mitra, alla produzione di formaggio e latticini Tillo Domor, e sulla strada per Khiva, è possibile ispezionare vigneti e risaie.<br />\r\n			Trasferimento a Khiva, sistemazione in albergo.<br />\r\n			<strong>Cena.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">GIORNO 4</td>\r\n			<td rowspan=\"2\">Khiva</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Colazione.</strong><br />\r\n			<em>Giro turistico a Khiva:</em> fortezza Kunya-Ark, incluso Kurinish Khana (17c); Mohammed-Amin-Khan (19c) Madrasa, minareto Islam Khodja; Complesso Palvan-Kari, complesso Abd Al Bobo (18c), Rafanek Madrassah, Mohammed Rakim Khan (19c), Amir-Tur Madrassah, Mausoleo Sho-I-Kalandar Bobo.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Pranzo.</strong><br />\r\n			<em>Giro turistico a Khiva:</em> Mazary Sharif Madrassah, Kheivak well (10c), Uch Avlioli (\"tre santi\") Mausoleo, Moschea Juma (18c), Kutluq-Murad Inaq (19c) Madrassah, Mausoleo Pakhlavan Mahmoud, Tash Hauli (\"cortile di pietra ”) Palazzo, Alla-Kuli-Khan (18c), Arabo Mohammed Khan (18c) Madrasse.<br />\r\n			<strong>Cena con spettacolo di danza Khorezmian</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">GIORNO 5</td>\r\n			<td rowspan=\"2\">Khiva - Bukhara</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Colazione.</strong><br />\r\n			Trasferimento a Bukhara attraverso il deserto di Kyzyl-Kum, breve sosta sulle rive del fiume Amu Darya. La durata del viaggio è di 7-8 ore.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Pranzo al sacco lungo la strada.</strong><br />\r\n			Sulla strada per Bukhara, faremo un salto nella città di Gazli per vedere come vengono allevate le pecore di Astrakhan, inoltre visiteremo il laboratorio per la lavorazione della lana di Astrakhan (Karakol) e della pelle di pecora.<br />\r\n			Arrivo a Bukhara, trasferimento in hotel, sistemazione. Arrivo a Bukhara, trasferimento in albergo, sistemazione.<br />\r\n			<strong>Cena.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">GIORNO 6\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td rowspan=\"2\">Bukhara</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Colazione.</strong><br />\r\n			<em>Giro turistico a Bukhara:</em> Visita al Registan, alla Fortezza dell\'Arca (16 sec) e al complesso Bolo-Hauz (18 sec). Ulug Beg (15c), Abdul Aziz (17c) Madrasse; Chor-Mausolei Minori. Mausoleo di Ismael Samani (9-10c)-volta della dinastia dei sovrani di Bukhara, Mausolei Chashma-Ayub (14 c)</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Pranzo.</strong><br />\r\n			<em>Giro turistico a Bukhara:</em> Poi-Kalyan Ensemble che include Minareto (12c) Moschea (15c), Madrassah Miri-Arab funzionante (16c), Emir-Alim-Khan Madrassah (14c), Taqi (cupole commerciali), Kukeldash Madrassah (16c) , Nadir Divanbegi (17c), Moschea Magok-i-Attari (16c), Lyab-i-Hauz (17c).<br />\r\n			<strong>Cena con spettacolo folkloristico.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">GIORNO 7\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td rowspan=\"2\">Bukhara – Nurata – Uhum</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Colazione.</strong><br />\r\n			Un viaggio nelle vicinanze della città di Kagan, dove si trova la pittoresca area protetta, rifugio di animali selvatici come mufloni e gazzelle.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Pranzo.</strong><br />\r\n			Trasferimento in direzione di Nurata attraverso il lago Tadakul, al villaggio di Ukhum. Sistemazione in una pensione.<br />\r\n			<strong>Cena.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">GIORNO 8\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td rowspan=\"2\">Uhum - Samarcanda</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Colazione.</strong><br />\r\n			Un viaggio nelle vicinanze della città di Kagan, dove si trova la pittoresca area protetta, rifugio di animali selvatici come mufloni e gazzelle.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Pranzo.</strong><br />\r\n			Trasferimento in direzione di Nurata attraverso il lago Tadakul, al villaggio di Ukhum. Sistemazione in una pensione.<br />\r\n			<strong>Cena.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">GIORNO 9\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td rowspan=\"2\">Samarcanda</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Colazione.</strong><br />\r\n			<em>Giro turistico a Samarcanda:</em> Mausoleo Gur-Emir (15c), Registan Ensemble (incluso Ulugbek (15c), Shir Dor (17c), Tillya –Kari Madrassah (17c) Moschea Bibi Khanum, Visita al bazar di Samarcanda.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Pranzo.</strong><br />\r\n			<em>Giro turistico a Samarcanda:</em> Necropoli di Shah-i-Zinda (14-15c) “Stelle sopra Samarcanda”: visita all\'osservatorio di Ulugbek (15c), Riserva archeologica statale “Rovine di Afrosiab” (6 aC-13 dC).<br />\r\n			<strong>Cena.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">GIORNO 10\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td rowspan=\"2\">Samarcanda – Shakhrisabz - Samarcanda</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Colazione.</strong><br />\r\n			Una visita ai giardini di Amir Temur vicino a Samarcanda, nel giardino di Baland visiteremo le piantagioni di fichi (fichi). Successivamente, visiteremo l\'agricoltura nella regione di Tailak, esamineremo le piantagioni di vigneti, patate, pomodori e mais, vedremo l\'impianto di lavorazione del pomodoro Tonny e esamineremo anche i bidoni dello stabilimento di Bagizag`an. Successivamente ci dirigiamo a Shakhrisabz.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Pranzo.</strong><br />\r\n			<em>Giro turistico a Shakhrisabz:</em> Palazzo Ak-Saray (secoli XIV-XV); Dorut Tilovat: Moschea Kok - Gumbaz (XV secolo); Dorus Siadat: Mausoleo di Jahongir (14c), volta di Tamerlano (XV secolo).<br />\r\n			Dopo un tour a Shakhrisabz verso Kitab, visiteremo il villaggio di Varganza, famoso per le sue piantagioni di melograni.<br />\r\n			Rientro a Samarcanda, trasferimento in hotel.<br />\r\n			<strong>Cena.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">GIORNO 11\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td rowspan=\"2\">Samarcanda - Tashkent</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Colazione.</strong><br />\r\n			Rientro a Tashkent..</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Pranzo in viaggio</strong><br />\r\n			Sulla strada per Tashkent in Chinaz, visiteremo un allevamento di cavalli e bovini.<br />\r\n			Cena di arrivederci</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>GIORNO 12\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td>\r\n			<p><em>Tashkent</em></p>\r\n\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Colazione.</strong><br />\r\n			Trasferimento all’aeroporto, volo di rientro.<br />\r\n			<em>Fine dei nostril servizi...</em></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em>• Tutte le escursioni sono condotte utilizzando un\'audioguida</em></strong><br />\r\n<strong><em>• Wi-Fi gratuito disponibile durante tutto il tour</em></strong></p>\r\n', '29', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('ru', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n	<tbody>\r\n		<tr>\r\n			<th style=\"width:10%;\">День</th>\r\n			<th style=\"width:14%;\">Маршрут</th>\r\n			<th style=\"width:8%;\">Время</th>\r\n			<th>Программа</th>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">ДЕНЬ 1</td>\r\n			<td rowspan=\"2\">Ташкент</td>\r\n			<td>AM</td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p>Прибытие в Ташкент. Трансфер в гостиницу, размещение.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">ДЕНЬ 2</td>\r\n			<td rowspan=\"2\">Ташкент – Ургенч – Хива</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Завтрак<br />\r\n			<em>Экскурсия по городу:</em></strong> Медресе Кукельдаш, мавзолей Каффала Шаши, медресе Барак Чан, мечеть на улице Част Имама, посещение восточного рынка Чорсу(осмотр рядов фруктовой и плодоовощной и прочей фермерской продукции).</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Обед</strong><br />\r\n			Поездка в Паркент, осмотр виноградных насаждений различных столовых и винных сортов. Возвращение в Ташкент, трансфер в гостиницу.<br />\r\n			<strong>Ужин</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">ДЕНЬ 3</td>\r\n			<td rowspan=\"2\">Хива</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Завтрак</strong><br />\r\n			Трансфер в аэропорт, вылет в Ургенч в 07.25 рейсом HY-011. Прибытие в Ургенч в 08:55.<br />\r\n			Посещение Ургенчского Государственного Университета, встреча с профессурой университета, обсуждение вопросов мелиорации, устранение почвенных солей, перспектив сельскохозяйственного развития в Каракалпакстане и в Хорезмской области.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Обед</strong><br />\r\n			Посещение фабрики по производству соков «Mitra», производства сыро-молочной продукции «Tillo Domor», по пути в Хиву возможно осмотреть виноградные насаждения, а также рисовые поля.<br />\r\n			Трансфер в Хиву, размещение в гостинице.<br />\r\n			<strong>Ужин</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">ДЕНЬ 4</td>\r\n			<td rowspan=\"2\">Хива</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Завтрак</strong><br />\r\n			<em><strong>Экскурсия по городу: </strong></em>крепость Куня-Арк, в том числе Куринская хана (17 век); медресе Мухаммед-Амин-Хан (19 век), минарет Ислам Ходжа; комплекс Палван-Кари, комплекс Абд аль Бобо (18 век), медресе Рафанек, мохаммед Раким Чан (19 век), медресе Амир-Тур, мавзолей Шо-и-Каландар Бобо.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Обед<br />\r\n			<em>Экскурсия по городу:</em></strong> Медресе Шариф Масари, фонтан Хейвак (10 век), мавзолей Утч Авлиоли («Три святых»), мечеть Дшума (18 век), медресе Кутлук-Мурад инак (19 век), мавзолей Пахлавана Махмуда, таш хаули («Таш хаули») Штайнхоф ») Дворец, Алла-Кули-Хан (18 век), Араб Мохаммед Хан (18 век), медресе.<br />\r\n			<strong>Ужин</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">ДЕНЬ 5</td>\r\n			<td rowspan=\"2\">Хива - Бухара</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Завтрак</strong><br />\r\n			Переезд в Бухару через пустыню Кызыл-Кум, короткая остановка у берега реки Амударья. Продолжительность переезда 8-10 часов.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>В пути остановка на обед-пикник.</strong><br />\r\n			По пути в Бухару мы заедем в город Газли, чтобы посмотреть на как разводят каракулевых овец, также посетим цех по переработке каракулевой шерсти и овечьей кожи.<br />\r\n			Прибытие в Бухару, трансфер в гостиницу, размещение.<br />\r\n			<strong>Ужин</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">ДЕНЬ 6</td>\r\n			<td rowspan=\"2\">Бухара</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Завтрак</strong><br />\r\n			Экскурсия по городу: Ансамбль Пои-Калон, включающий минарет (12 век), мечеть (15 век), действующее медресе Мири-Араб (16 век), медресе Эмир-Алим-Хан (14 век), Таки (игольные купола), медресе Кукельдаш (16 век) ), Надир Диванбеги (17 век), Мечеть Магок-и-Аттари (16 век), Ляб-и-Хауз (17 век).</p>\r\n\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Обед</strong><br />\r\n			Экскурсия по городу: Посещение Регистана, крепости Арк (16 век) и комплекса Боло-Чаус (18 век), Улугбека (15 век), Абдул Азиза (17 век), мавзолеев Исмаила Самани (9-10 век) - Могила правителей Бухары - Династия, Мавзолеи Чащма-Аюба (14 век).<br />\r\n			<strong>Ужин</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">ДЕНЬ 7</td>\r\n			<td rowspan=\"2\">Бухара - Нурата - Ухум</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Завтрак</strong><br />\r\n			Поездка в окрестности города Каган, где расположена живописная заповедная зона - прибежище диких зверей как муфлоны и газели.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Обед</strong><br />\r\n			Далее переезд в направление Нураты мимо озера Тадакуль, в селение Ухум. Размещение в гостевом доме.<br />\r\n			<strong>Ужин</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">ДЕНЬ 8</td>\r\n			<td rowspan=\"2\">Ухум – Самарканд</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Завтрак</strong><br />\r\n			Перед тем как направиться в Самарканд, по пути через 25 км мы посетим крупнейшую в Узбекистане ферму верблюдов, а также ферму овец. Здесь возможно попробовать настоящее верблюжье молоко. Говорят, что верблюжье молоко способной излечить у человека многие заболевания печени.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Обед</strong><br />\r\n			Далее мы направляемся в Самарканд, по прибытии в Самарканд, размещение в гостинице.<br />\r\n			<strong>Ужин</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">ДЕНЬ 9</td>\r\n			<td rowspan=\"2\">Самарканд</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Завтрак<br />\r\n			<em>Экскурсия по городу: </em></strong>Мавзолей Гур-Эмир (15 век), Ансамбль Регистан (в том числе Улугбек (15 век), Шир Дор (17 век), Тилля - Кари Медрессен (17 век), мечеть Биби Чанум. Посещение базара в Самарканде.</p>\r\n\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Обед</strong><br />\r\n			Экскурсия по городу<strong><em>:</em></strong> Шах-и-Синда Город мертвых (XIV-XV века), «Звезды над Самаркандом»: экскурсия в Улугбекскую обсерваторию (XV век), бумажная фабрика<br />\r\n			<strong>Ужин</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">ДЕНЬ 10</td>\r\n			<td rowspan=\"2\">Самарканд - Шахрисабз - Самарканд</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Завтрак</strong><br />\r\n			Посещение под Самаркандом сады Амира Темура, в саду Баланд мы посетим плантации фиговых деревьев (инжир). Далее мы посетим в районе Тайлак агро-фермерское хозяйство, рассмотреть плантации виноградников, картофеля, томатов и кукурузы, увидеть фабрику по переработке томатов «Tonny», а также заглянуть в закрома завода Bagizag`an. Далее направляемся в Шахрисабз.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Обед</strong><br />\r\n			Экскурсия по городу: Дворец Ак-Сарай (XIV-XV века); Дорут Тиловат: Мечеть Кок - Гумбаз (15 век); Дорус Сиадат: Мавзолей Жахонгир (14c), хранилище Тамерлана (15-го века).<br />\r\n			После экскурсии в Шахрисабзе по направлении в Китаб мы посетим деревню Варганза, знаменитую своими плантациями граната.<br />\r\n			Возращение в Самарканд, трансфер в гостиницу.<br />\r\n			<strong>Ужин</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">ДЕНЬ 11</td>\r\n			<td rowspan=\"2\">Ташкент</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Завтрак</strong><br />\r\n			Возвращение в Ташкент.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Обед</strong><br />\r\n			По пути в Ташкент в Чиназе мы посетим ферму по разведению племенных конец, а также крупного рогатого скота.<br />\r\n			<strong>Прощальный ужин</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>ДЕНЬ 12</td>\r\n			<td>Ташкент</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Завтрак</strong><br />\r\n			Трансфер в аэропорт, вылет домой</p>\r\n			Окончание наших услуг<strong><em>… </em></strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em>Все экскурсии проводятся с помощью аудио гида</em></strong></p>\r\n\r\n<p><strong><em>На протяжение всего маршрута доступен бесплатный Wi-Fi</em></strong></p>\r\n', '29', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('de', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n	<tbody>\r\n		<tr>\r\n			<th style=\"width:10%;\">TAGE</th>\r\n			<th style=\"width:14%;\">REISEROUTE</th>\r\n			<th style=\"width:8%;\">ZEIT</th>\r\n			<th>THEMEN DER REISEROUTE</th>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">Tag1</td>\r\n			<td rowspan=\"2\">Frankfurt - Taschkent</td>\r\n			<td>AM</td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Ankunft in Taschkent um 20:25 Uhr von Frankfurt mit </strong><strong>НУ</strong><strong>-232. </strong></p>\r\n\r\n			<p>Unterkunft im Hotel.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">Tag2</td>\r\n			<td rowspan=\"2\">Taschkent - Urgentsch - Chiwa&nbsp;</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Frühstuck</strong><br />\r\n			Stadtrundfahrt in Taschkent: Kukeldash Madrasah, Kaffal Shashi Mausoleum, Barak Chan Madrasah, Moschee in der Chast Imam Street, Besuch des östlichen Marktes von Chorsu (Inspektion von Obst- und Gemüsereihen und anderen landwirtschaftlichen Produkten).</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Mittagsessen</strong><br />\r\n			Ein Ausflug nach Parkent, Besichtigung von Weinbergen verschiedener Speise- und Weinsorten. Rückkehr nach Taschkent, Transfer zum Hotel.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">Tag3</td>\r\n			<td rowspan=\"2\">Chiwa</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Frühstuck</strong><br />\r\n			Stadtrundfahrt in Chiwa: Transfer zum Flughafen, Abfahrt nach Urgentsch um 07.25 Uhr Flug HY-011. Ankunft in Urgentsch um 08:55 Uhr.<br />\r\n			Besuch der Urgench State University, Treffen mit Universitätsprofessoren, Erörterung von Fragen der Landgewinnung, Beseitigung von Bodensalzen, Perspektiven für die landwirtschaftliche Entwicklung in Karakalpakstan und der Region Khorezm.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Mittagsessen</strong><br />\r\n			Bei einem Besuch der Mitra-Saftfabrik, der Herstellung von Tillo-Domor-Käse und Milchprodukten sowie auf dem Weg nach Chiwa können Weinberge und Reisfelder besichtigt werden.<br />\r\n			Transfer nach Chiwa, Hotelunterkunft.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">Tag4</td>\r\n			<td rowspan=\"2\">Chiwa</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Fr</strong><strong>ü</strong><strong>hstuck</strong><br />\r\n			Stadtrundfahrt in Chiwa: Kunya-Ark Festung, einschließlich des Kurinskaya Khan (17. Jahrhundert); Muhammad Amin Khan Madrasah (19. Jahrhundert), Islam Khoja Minarett; Palvan-Kari-Komplex, Abd al-Bobo-Komplex (18. Jahrhundert), Rafanek-Medresse, Mohammed Rakim Chan (19. Jahrhundert), Amir-Tur-Medresse, Sho-i-Kalandar-Bobo-Mausoleum.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Mittagsessen</strong><br />\r\n			Stadtrundfahrt in Chiwa: Sharif Masari-Medresse, Heyvak-Brunnen (10. Jahrhundert), Utch Avlioli-Mausoleum („Drei Heilige“), Dshuma-Moschee (18. Jahrhundert), Kutluk-Murad Inak-Medresse (19. Jahrhundert), Pakhlavan Mahmud-Mausoleum, Tash Hauly („Tash Hauly“) Steinhof ”) Palast, Alla Kuli Khan (18. Jahrhundert), Arabischer Mohammed Khan (18. Jahrhundert), Medresse.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">Tag5</td>\r\n			<td rowspan=\"2\">Chiwa - Buchara&nbsp;</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Fr</strong><strong>ühstuck</strong><br />\r\n			Transfer nach Buchara durch die Kyzyl-Kum-Wüste, eine kurze Pause am Ufer des Flusses Amu Darya. Die Dauer des Umzugs beträgt 8-10 Stunden.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Mittagsessen unterwegs</strong><br />\r\n			Auf dem Weg nach Buchara fahren wir in die Stadt Gazli, um zu sehen, wie Astrachaner Schafe gezüchtet werden, und besuchen die Werkstatt für die Verarbeitung von Astrachaner Wolle und Schafsfell.<br />\r\n			Ankunft in Buchara, Transfer zum Hotel, Unterbringung.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">Tag6</td>\r\n			<td rowspan=\"2\">Buchara&nbsp;</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Fr</strong><strong>ü</strong><strong>hstuck</strong><br />\r\n			Stadtrundfahrt in Buchara: Das Poi-Kalon-Ensemble, zu dem ein Minarett (12. Jahrhundert), eine Moschee (15. Jahrhundert), die derzeitige Miri-Arab-Medresse (16. Jahrhundert), die Emir-Alim-Khan-Medresse (14. Jahrhundert), Taki (Nadelkuppeln) und die Kukeldash-Medresse (16. Jahrhundert) gehören. )), Nadir Divanbegi (17. Jahrhundert), Magok-i-Attari-Moschee (16. Jahrhundert), Lyab-i-Khauz (17. Jahrhundert).</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Mittagsessen</strong><br />\r\n			Stadtrundfahrt in Buchara: Besichtigung des Registan, der Archenfestung (16. Jahrhundert) und des Bolo-Chaus-Komplexes (18. Jahrhundert), Ulugbek (15. Jahrhundert), Abdul Aziz (17. Jahrhundert), des Mausoleums von Ismail Samani (9.-10. Jahrhundert) - Das Grab der Herrscher von Buchara - Dynastie, Chashchma Mausoleums Ayuba (14. Jahrhundert).<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">Tag7</td>\r\n			<td rowspan=\"2\">Buchara - Nurata - Uchum</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Fr</strong><strong>ühstuck</strong><br />\r\n			Ein Ausflug in die Umgebung der Stadt Kagan, in der sich das malerische Naturschutzgebiet befindet - eine Zuflucht wilder Tiere wie Mufflons und Gazellen.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Mittagsessen</strong><br />\r\n			Dann fahren Sie in Richtung Nurata, vorbei am Tadakul-See, in das Dorf Ukhum. Unterkunft in einer Pension.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">Tag8</td>\r\n			<td rowspan=\"2\">Uchum <strong><em>- </em></strong>Samarkand&nbsp;</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Frühstück</strong><br />\r\n			Bevor es nach Samarkand geht, besuchen wir auf dem Weg durch 25 km die größte Kamelfarm Usbekistans sowie eine Schäferei. Hier kann man echte Kamelmilch probieren. Es wird gesagt, dass Kamelmilch viele Lebererkrankungen beim Menschen heilen kann.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Mittagsessen</strong><br />\r\n			Dann fahren wir nach Samarkand, nach der Ankunft in Samarkand, Unterkunft im Hotel.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">Tag9</td>\r\n			<td rowspan=\"2\">Samarkand</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Frühstuck</strong><br />\r\n			Stadtrundfahrt in Samarkand: Gur-Emir-Mausoleum (15. Jahrhundert), Registan-Ensemble (einschließlich Ulugbek (15. Jahrhundert), Shir Dor (17. Jahrhundert), Tilla-Kari-Medressen (17. Jahrhundert), Bibi-Chanum-Moschee Besuchen Sie den Basar in Samarkand.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Mittagsessen</strong><br />\r\n			Stadtrundfahrt in Samarkand: Shah-i-Sinda Stadt der Toten (XIV-XV Jahrhundert), \"Stars over Samarkand\": Ausflug zum Ulugbek Observatorium (XV Jahrhundert), Papiermühle<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">\r\n			<p>Tag 10</p>\r\n			</td>\r\n			<td rowspan=\"2\">Samarkand - Schachrisabz - Samarkand</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Fr</strong><strong>ühstuck</strong><br />\r\n			Ein Besuch in den Gärten von Amir Temur in der Nähe von Samarkand, im Garten von Baland besuchen wir die Plantagen von Feigenbäumen (Feigen). Als nächstes besuchen wir die Agro-Landwirtschaft in der Region Tailak, untersuchen die Plantagen von Weinbergen, Kartoffeln, Tomaten und Mais, sehen uns die Tonny-Tomatenverarbeitungsanlage an und schauen uns auch die Behälter der Bagizag\'an-Pflanze an. Als nächstes fahren wir nach Shakhrisabz.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Mittagsessen</strong><br />\r\n			Stadtrundfahrt in Schachrisabz: Ak-Saray-Palast (XIV-XV Jahrhundert); Dorut Tilovat: Kok-Moschee - Gumbaz (15. Jahrhundert); Dorus Siadat: Jahongir-Mausoleum (14c), Tamerlanes Gewölbe (15. Jahrhundert).<br />\r\n			Nach einer Tour in Shakhrisabz in Richtung Kitab besuchen wir das Dorf Varganza, das für seine Granatapfelplantagen bekannt ist.<br />\r\n			Rückkehr nach Samarkand, Transfer zum Hotel.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">Tag 11</td>\r\n			<td rowspan=\"2\">Taschkent</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Frühstuck</strong><br />\r\n			Rückkehr nach Taschkent.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Mittagsessen</strong><br />\r\n			Auf dem Weg nach Taschkent in China besuchen wir eine Farm zur Zucht von Schafen und Rindern.<br />\r\n			<strong>Abschiedsabendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Tag 12</td>\r\n			<td>Taschkent</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Frühere Frühstück.</strong><br />\r\n			Transfer zum internationalen Flughafen in Taschkent, Abflug um 05:45 mit HY-231</p>\r\n			Ende unserer Leistungen …</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n	<p><strong><em>Alle Reiseleitungen werden mit Audioguide durchgeführt&nbsp; </em></strong></p>\r\n	<p><strong><em>Im Laufe der Reise wird kostenlose Internet via Wi-Fi zugänglich</em></strong></p>\r\n\r\n\r\n<p>&nbsp;</p>\r\n', '29', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('en', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n	<tbody>\r\n		<tr>\r\n			<th style=\"width:8%;\">Days</th>\r\n			<th style=\"width:14%;\">Waypoint</th>\r\n			<th style=\"width:8%;\">Time</th>\r\n			<th>Subjects of itinerary</th>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 1</td>\r\n			<td rowspan=\"2\">Tashkent</td>\r\n			<td>AM</td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p>Arrival to Tashkent. Transfer and accommodation in hotel.<br />\r\n			<strong>Dinner.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 2</td>\r\n			<td rowspan=\"2\">Tashkent – Urgench - Khiva</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Breakfast.</strong><br />\r\n			<em>Sightseeing tour in Tashkent:</em> The old part of the city of Tashkent.<br />\r\n			Visit to the Khast Imam complex, consisting of the Barak Khan Madrasah, Till Sheikh Mosque - the repository of the famous Koran Osman and the golden hair of the Prophet Muhammad; The Juma Mosque of Khoja Ahrar Vali.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Lunch.</strong><br />\r\n			Drive to Parkent in 45km to east from Tashkent. There we will visit grape fields and gardens, further we will visit vine chateau Hamkor located near Namdanak village.<br />\r\n			Return to Tashkent. Free time.<br />\r\n			<strong>Dinner.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 3</td>\r\n			<td rowspan=\"2\">Khiva</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Breakfast.</strong><br />\r\n			Transfer to the airport, departure to Urgench at 07.25 flight HY-011. Arrival in Urgench at 08:55.<br />\r\n			Visit to Urgench State University, meeting with university professors, discussing land reclamation issues, eliminating soil salts, prospects for agricultural development in Karakalpakstan and the Khorezm region.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Lunch.</strong><br />\r\n			A visit to the Mitra Juice Factory, the production of Tillo Domor cheese and dairy products, and on the way to Khiva, it is possible to inspect vineyards and rice fields.<br />\r\n			Transfer to Khiva, hotel accommodation.<br />\r\n			<strong>Dinner.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 4</td>\r\n			<td rowspan=\"2\">Khiva</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Breakfast.</strong><br />\r\n			<em>Sightseeing tour in Khiva:</em> Kunya-Ark fortress, including Kurinish Khana (17c); Mohammed-Amin-Khan (19c) Madrassah, Islam Khodja Minaret; Palvan-Kari complex, Abd Al Bobo complex (18c), Rafanek Madrassah, Mohammed Rakim Khan (19 c), Amir-Tur Madrassah, Sho-I-Kalandar Bobo Mausoleum.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Lunch.</strong><br />\r\n			<em>Sightseeing tour in Khiva:</em> Mazary Sharif Madrassah, Kheivak well (10c), Uch Avlioli (“three saints”) Mausoleum, Juma Mosque (18c), Kutluq-Murad Inaq (19c) Madrassah, Pakhlavan Mahmoud Mausoleum, Tash Hauli (“stone yard”) Palace, Alla-Kuli-Khan (18c), Arab Mohammed Khan (18 c) Madrassahs.<br />\r\n			<strong>Dinner with Khorezmian dance show</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 5</td>\r\n			<td rowspan=\"2\">Khiva - Bukhara</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Breakfast.</strong><br />\r\n			Transfer to Bukhara through the Kyzyl-Kum desert, a short stop on the banks of the Amu Darya river. The duration of the drive is 7-8 hours.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Lunch picnic on the way</strong><br />\r\n			On the way to Bukhara, we will drop into the city of Gazli to see how Astrakhan sheep are bred, фlso visit the workshop for the processing of Astrakhan (Karakol) wool and sheep skin.<br />\r\n			Arrival in Bukhara, transfer to hotel, accommodation.<br />\r\n			<strong>Dinner.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 6\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td rowspan=\"2\">Bukhara</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Breakfast.</strong><br />\r\n			<em>Sightseeing tour in Bukhara:</em> Visit to Registan, Ark Fortress (16 c) and Bolo-Hauz complex (18 c). Ulug Beg (15c), Abdul Aziz (17c) Madrassahs; Chor-Minor Mausoleums. Ismael Samani mausoleum (9-10c)-vault of Bukhara rulers dynasty, Chashma-Ayub Mausoleums (14 c)</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Lunch.</strong><br />\r\n			<em>Sightseeing tour in Bukhara:</em> Poi-Kalyan Ensemble including Minaret (12c) Mosque (15c), functioning Madrassah Miri-Arab (16 c), Emir-Alim-Khan Madrassah (14c), Taqi (trading domes), Kukeldash Madrassah (16c), Nadir Divanbegi (17c), Magok-i-Attari Mosque (16c), Lyab-i-Hauz (17c).<br />\r\n			<strong>Dinner with Folk Show.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 7\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td rowspan=\"2\">Bukhara – Nurata – Uhum</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Breakfast.</strong><br />\r\n			A trip to the vicinity of the city of Kagan, where the picturesque conservation area is located - a refuge of wild animals like mouflons and gazelles.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Lunch.</strong><br />\r\n			Then transfer to the direction of Nurata via Lake Tadakul, to the village of Ukhum. Accommodation in a guesthouse.<br />\r\n			<strong>Dinner.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 8\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td rowspan=\"2\">Uhum - Samarkand</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Breakfast.</strong><br />\r\n			A trip to the vicinity of the city of Kagan, where the picturesque conservation area is located - a refuge of wild animals like mouflons and gazelles.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Lunch.</strong><br />\r\n			Then transfer to the direction of Nurata via Lake Tadakul, to the village of Ukhum. Accommodation in a guesthouse.<br />\r\n			<strong>Dinner.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 9\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td rowspan=\"2\">Samarkand</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Breakfast.</strong><br />\r\n			<em>Sightseeing tour in Samarkand:</em> Gur-Emir Mausoleum (15c), Registan Ensemble (including Ulugbek (15c), Shir Dor (17c), Tillya –Kari Madrassahs (17c). &nbsp;Bibi Khanum Mosque, Visit to Samarkand bazaar.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Lunch.</strong><br />\r\n			<em>Sightseeing tour in Samarkand:</em> Shah-i-Zinda necropolis (14-15c) “Stars above Samarkand”: tour to Ulugbek observatory (15c), “Ruins of Afrosiab” State archaeological reserve (6 BC-13 AD).<br />\r\n			<strong>Dinner.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 10\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td rowspan=\"2\">Samarkand – Shakhrisabz - Samarkand</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Breakfast.</strong><br />\r\n			A visit to the gardens of Amir Temur near Samarkand, in the garden of Baland we will visit the plantations of fig trees (figs). Next, we will visit agro-farming in the Tailak region, examine the plantations of vineyards, potatoes, tomatoes and corn, see the Tonny tomato processing plant, and also look into the bins of the Bagizag`an plant. Next we head to Shakhrisabz.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Lunch.</strong><br />\r\n			<em>Sightseeing tour in Shakhrisabz:</em> Ak-Saray Palace (XIV-XV centuries); Dorut Tilovat: Kok Mosque - Gumbaz (15th century); Dorus Siadat: Jahongir Mausoleum (14c), Tamerlane\'s vault (15th century).<br />\r\n			After a tour in Shakhrisabz towards Kitab, we will visit the village of Varganza, famous for its pomegranate plantations.<br />\r\n			Return to Samarkand, transfer to the hotel.<br />\r\n			<strong>Dinner.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 11\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td rowspan=\"2\">Samarkand - Tashkent</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Breakfast.</strong><br />\r\n			Return to Tashkent.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Lunch en route</strong><br />\r\n			On the way to Tashkent in Chinaz, we will visit a breeding horse farm, as well as cattle.<br />\r\n			Farewell dinner</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DAY 12\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td>\r\n			<p><em>Tashkent</em></p>\r\n\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Breakfast.</strong><br />\r\n			Transfer to the airport, flight home<br />\r\n			<em>The end of our services ...</em></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em>• All excursions are conducted using an audio guide</em></strong><br />\r\n<strong><em>• Free Wi-Fi available throughout the tour</em></strong></p>\r\n', '29', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('it', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n	<tbody>\r\n		<tr>\r\n			<th style=\"width:8%;\">Giorni</th>\r\n			<th style=\"width:14%;\">Tappe</th>\r\n			<th style=\"width:8%;\">Time</th>\r\n			<th>Soggetti dell’itinerario</th>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">GIORNO 1</td>\r\n			<td rowspan=\"2\">Tashkent</td>\r\n			<td>AM</td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p>Arrivo a Tashkent. Trasferimento e sistemazione in albergo.<br />\r\n			<strong>Cena</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">GIORNO 2</td>\r\n			<td rowspan=\"2\">Tashkent</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Colazione</strong>.<br />\r\n			WVisiteremo lo zoo di Tashkent fondato nel 1924. Qui faremo conoscenza con la fauna selvatica dell\'Uzbekistan e del mondo.<br />\r\n			Visita al Giardino Botanico di Tashkent, fondato nel 1950. Conferenza sulla flora e la fauna dell\'Uzbekistan.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Pranzo<br />\r\n			<em>Giro turistico a Tashkent:</em></strong> TLa parte vecchia della città di Tashkent.<br />\r\n			Visita al complesso Khast Imam, composto dalla Madrasa Barak Khan, dalla Moschea Till Sheikh - il deposito del famoso Corano Osman e dai capelli d\'oro del profeta Maometto; La Moschea Juma di Khoja Ahrar Vali.<br />\r\n			<strong>Cena</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">GIORNO 3</td>\r\n			<td rowspan=\"2\">Tashkent – Nukus - Muinak</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Colazione</strong><br />\r\n			Trasferimento all\'aeroporto locale, volo per Nukus partenza alle 07:25 e arrivo alle 09:05. Trasferimento a Muynak, lungo il percorso sosta e panoramica del fiume. Amu Darya. Arrivo a Muynak e sistemazione in yurte.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Pranzo</strong><br />\r\n			<strong><em>Giro turistico a Muinak</em></strong>: Visita al cimitero delle navi, al Museo delle tradizioni locali, alle vecchie case dei pescatori di Muynak, alla baia di Muynak. Conferenza sul disastro dell\'Aral.<br />\r\n			<strong>Cena</strong>.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">GIORNO 4</td>\r\n			<td rowspan=\"2\">Muinak – Nukus – Khiva</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Colazione anticipata.</strong><br />\r\n			Partenza per Nukus. All\'arrivo, visita al Museo di Savitsky.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Pranzo</strong><br />\r\n			Inoltre guidiamo a Khiva, sulla strada visitiamo la Riserva Bagai-Tugai. Conoscenza della flora e della fauna, visita alla voliera (Bukhara Deer nel Libro Rosso) e osservazione degli animali allo stato brado. Gite in barca nell\'Amu Darya.<br />\r\n			Arrivo a Khiva e sistemazione.<br />\r\n			<strong>Cena</strong>.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">GIORNO 5</td>\r\n			<td rowspan=\"2\">Khiva</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Colazione<br />\r\n			<em>Giro turistico a Khiva:</em></strong> fortezza Kunya-Ark, incluso Kurinish Khana (17c); Mohammed-Amin-Khan (19c) Madrasa, minareto Islam Khodja; Complesso Palvan-Kari, complesso Abd Al Bobo (18c), Rafanek Madrassah, Mohammed Rakim Khan (19c), Amir-Tur Madrassah, Mausoleo Sho-I-Kalandar Bobo.</p>\r\n\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Pranzo<br />\r\n			<em>Giro turistico a Khiva:</em></strong> Mazary Sharif Madrassah, Kheivak well (10c), Uch Avlioli (\"tre santi\") Mausoleo, Moschea Juma (18c), Kutluq-Murad Inaq (19c) Madrassah, Mausoleo Pakhlavan Mahmoud, Tash Hauli (\"cortile di pietra ”) Palazzo, Alla-Kuli-Khan (18c), Arabo Mohammed Khan (18c) Madrasse.<br />\r\n			<strong>Cena con spettacolo di danza Khorezmian.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">GIORNO 6</td>\r\n			<td rowspan=\"2\">\r\n			<p>Khiva - Bukhara</p>\r\n			</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Colazione</strong>.<br />\r\n			Trasferimento alla stazione ferroviaria di Khiva. Treno per Bukhara alle 08:30.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Pranzo al sacco sul treno</strong><br />\r\n			Arrivo a Bukhara alle 14:45. Trasferimento all\'Eco-Centro Jeyran. Conoscenza della flora e della fauna, visita alla voliera (Dzheyrana nel libro rosso) e osservazione degli animali allo stato brado.<br />\r\n			Trasferimento a Bukhara, sistemazione in albergo.<br />\r\n			<strong>Cena</strong>.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">GIORNO 7</td>\r\n			<td rowspan=\"2\">Bukhara</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Colazione</strong><br />\r\n			<em><strong>Giro turistico a Bukhara:</strong></em> Visita al Registan, alla Fortezza dell\'Arca (16 sec) e al complesso Bolo-Hauz (18 sec). Ulug Beg (15c), Abdul Aziz (17c) Madrasse; Chor-Mausolei Minori. Mausoleo di Ismael Samani (9-10c)-volta della dinastia dei sovrani di Bukhara, Mausolei di Chashma-Ayub (14 c)</p>\r\n\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Pranzo<br />\r\n			<em>Giro turistico a Bukhara:</em></strong> Poi-Kalyan Ensemble che include Minareto (12c) Moschea (15c), Madrassah Miri-Arab funzionante (16c), Emir-Alim-Khan Madrassah (14c), Taqi (cupole commerciali), Kukeldash Madrassah (16c) , Nadir Divanbegi (17c), Moschea Magok-i-Attari (16c), Lyab-i-Hauz (17c).<br />\r\n			<strong>Cena con spettacolo folkloristico.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">GIORNO 8</td>\r\n			<td rowspan=\"2\">Bukhara – Nurata – Campo yurte</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Colazione</strong><br />\r\n			Partenza per il campo di Yurt sul lago Aydarkul. Sulla strada visita Rabat-i-Malik Karavan Serai a Karmana e le rovine della fortezza di Alessandro Magno e petroglifi e l\'antica moschea di San Chashma a Nurata.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Pranzo</strong><br />\r\n			Trasferimento più profondo nel deserto al Camel Camp, sistemazione in yurte nazionali, Camel Back a cavallo per 10-15 minuti.<br />\r\n			<strong>Cena intorno al caminetto con esibizione di akyn (cantante epico nomade).</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">GIORNO 9</td>\r\n			<td rowspan=\"2\">Campo yurte – Samarcanda</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Colazione</strong><br />\r\n			Partenza per il lago Aydarkul. Passeggiata in riva al lago. Fare il bagno a seconda del tempo e della stagione.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Pranzo pic-nic in riva al lago.</strong><br />\r\n			Pomeriggio a Samarcanda. Arrivo e sistemazione in hotel.<br />\r\n			<strong>Cena</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">GIORNO 10</td>\r\n			<td rowspan=\"2\">Samarcanda</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Colazione<br />\r\n			<em>Giro turistico a Samarcanda:</em></strong> Mausoleo Gur-Emir (15c), Registan Ensemble (incluso Ulugbek (15c), Shir Dor (17c), Tillya –Kari Madrassah (17c) Moschea Bibi Khanum, Visita al bazar di Samarcanda.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Pranzo</strong><br />\r\n			<em><strong>Giro turistico a Samarcanda:</strong></em> Necropoli di Shah-i-Zinda (14-15c) “Stelle sopra Samarcanda”: visita all\'osservatorio di Ulugbek (15c), Riserva archeologica statale “Rovine di Afrosiab” (6 aC-13 dC).<br />\r\n			<strong>Cena</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">GIORNO 11</td>\r\n			<td rowspan=\"2\">Samarcanda - Tashkent</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Colazione</strong><br />\r\n			Trasferimento a Tashkent durante la visita alla riserva di Zarafshan.<br />\r\n			Conoscenza della flora e della fauna, visita di una voliera (animali del Libro rosso dell\'Uzbekistan) e osservazione degli animali allo stato brado.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Pranzo</strong><br />\r\n			Arrivo a Tashkent, sistemazione in albergo, tempo libero.<br />\r\n			<strong>Cena di arrivederci.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">GIORNO 12</td>\r\n			<td rowspan=\"2\">Tashkent</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Colazione</strong><br />\r\n			Trasferimento all’aeroporto. Volo di rientro.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>Fine dei nostri servizi…</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '30', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('ru', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n	<tbody>\r\n		<tr>\r\n			<th style=\"width:10%;\">День</th>\r\n			<th style=\"width:14%;\">Маршрут</th>\r\n			<th style=\"width:8%;\">Время</th>\r\n			<th>Программа</th>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">ДЕНЬ 1</td>\r\n			<td rowspan=\"2\">Ташкент</td>\r\n			<td>AM</td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p>Прибытие в Ташкент. Трансфер в гостиницу, размещение.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">ДЕНЬ 2</td>\r\n			<td rowspan=\"2\">Ташкент</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Завтрак.</strong><br />\r\n			Мы посетим Ташкентский Зоопарк основанный в 1924г. Здесь мы ознакомимся с животным миром Узбекистана и Мира.<br />\r\n			Посещение Ташкентского Ботанического Сада, основанного в 1950г. Лекция на тему флоры и фауны Узбекистана.<br />\r\n			<strong>Обед.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Экскурсия по Ташкенту:</strong> Старая часть города Ташкента.<br />\r\n			Посещение комплекса Хаст Имам, состоящего из Медресе Барак-Хана, мечети Тилля-Шейха - хранилище знаменитого Корана Османа и золотого волоса пророка Мухаммада; Джума-мечеть Ходжа Ахрар Вали.<br />\r\n			<strong>Ужин.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">ДЕНЬ 3</td>\r\n			<td rowspan=\"2\">Ташкент – Нукус – Муйнак</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Завтрак.</strong><br />\r\n			Переезд в местный аэропорт, рейс в Нукус НУ-011 вылет в 07.25 прибытие в 09.05.<br />\r\n			Переезд в Муйнак, по дороге остановка и обзор р. Амударья. Прибытие в Муйнак и размещение в юртах.<br />\r\n			<strong>Обед </strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Экскурсия по Муйнаку:</strong><br />\r\n			Посещение Кладбища кораблей, Краеведческого музея, старых домов муйнакских рыбаков, муйнакский залив. Лекция об Аральской катастрофе.<br />\r\n			<strong>Ужин.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">ДЕНЬ 4</td>\r\n			<td rowspan=\"2\">Муйнак – Нукус – Хива</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Ранний завтрак.</strong><br />\r\n			Выезд в Нукус, прибытие и посещение Музея Савитского.<br />\r\n			<strong>Обед.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p>Далее мы едем в Хиву, по дороге посещаем Багай-Тугайский заповедник. Ознакомление с флорой и фауной, посещение вольера (Бухарский Олень в красной книге) и наблюдение за животными в диких условиях. Катание на лодках по Амударье.<br />\r\n			Прибытие в Хиву и размещение.<br />\r\n			<strong>Ужин.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">ДЕНЬ 5</td>\r\n			<td rowspan=\"2\">Хива</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Завтрак.<br />\r\n			Экскурсия по Хиве. </strong>Город-крепость Куня-Арк, включая Куриниш хана (17 век), Медресе Амин Хана (19 век), Минарет Ислам Ходжи, комплексы Палван-Кары, Абд Аль Бобо (18 век), Медресе Рафанек, Мохамад Рахим Хан (19 век), Медресе Амир-Тур, Мавзолей Шо-и –Каландар Бобо<br />\r\n			<strong>Обед.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p>Медресе Мазари Шариф, древняя стена Хейвах (10 век), Мавзолей «Уч Авлие», Пятничная мечеть (18 век), Медресе Кутлуг-Мурад Инака (19 век), Мавзолей Пахлаван Махмуда, Дворец «Таш Хавли», Медресе Алла-Кули Хана (18 век), Араб Мухаммед Хана (18 век).<br />\r\n			<strong>Ужин.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">ДЕНЬ 6</td>\r\n			<td rowspan=\"2\">\r\n			<p>Хива – Бухара</p>\r\n			</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Завтрак.</strong><br />\r\n			Переезд на Хивинский вокзал, поезд до Бухары в 08.30, Прибытие в Бухару в 14.45.<br />\r\n			<strong>Обед в пути (ланч боксы).</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p>Переезд в Эко-Центр Джейран. Ознакомление с флорой и фауной, посещение вольера (Джейраны в красной книге) и наблюдение за животными в диких условиях.<br />\r\n			Переезд в Бухару, размещение в гостинице.<br />\r\n			<strong>Ужин.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">ДЕНЬ 7</td>\r\n			<td rowspan=\"2\">Бухара</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Завтрак.</strong><br />\r\n			<em><strong>Экскурсия по Бухаре</strong></em>: Мавзолей Саманидов (9-10 века), Мавзолей Чашма-Аюб (14 век), Мечеть Боло-Хауз (18 век), Зимняя резиденция Бухарского эмира «АРК» (16 век).</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><em><strong>Экскурсия по Бухаре:</strong></em> Архитектурный ансамбль По-и Колон (включая Минарет (12 век), Мечеть (15 век), действующую медресе Мири-Араб (16 век), Медресе Алим-Хана (14 век), Таки (торговые палаты), Медресе Кукельдаш (16 век), Надир Диванбеги (17век), Мечеть Магок-и Атари (16 век), Ляб-и Хауз (17 век), мечеть Чор-минор (Четыре минарета).<br />\r\n			<strong>Ужин.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">ДЕНЬ 8</td>\r\n			<td rowspan=\"2\">Бухара – Нурата – Юртовый Лагерь</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Завтрак.</strong><br />\r\n			Переезд в Нурату на автобусе, по дороге посещение Сардобу и древний Караван Сарай в Малик Рабат.<br />\r\n			Прибытие в Нурота, Посещение святого источника «Чашма», развалин крепости Александра Македонского.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Обед.</strong><br />\r\n			Переезд в юрточный лагерь, прибытие и размещение в юрточном лагере.<br />\r\n			Катание на верблюдах.<br />\r\n			<strong>Ужин у костра с выступлением акына (фольклорный певец). Ночь в юртовом лагере.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">ДЕНЬ 9</td>\r\n			<td rowspan=\"2\">Юртовый Лагерь – Самарканд</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Завтрак.</strong><br />\r\n			Выезд к озеру Айдаркуль.<br />\r\n			По прибытию на озеро, купание и обед пик ник.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Обед.</strong><br />\r\n			Переезд в Самарканд, прибытие и размещение в гостинице<br />\r\n			<strong>Ужин</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">ДЕНЬ 10</td>\r\n			<td rowspan=\"2\">Самарканд</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Завтрак.</strong><br />\r\n			<em><strong>Экскурсия по Самарканду:</strong></em> Мавзолей Гур-Эмир (15век), Архитектурный ансамбль Регистан (включая Медресе Улугбека (15 век), Шер-Дор (17 век), Тилля-Кари (17 век)), Мечеть Биби-Ханум (15 век). Посещение Самаркандского базара, Ансамбль Шахи-Зинда (14-15 век)</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Обед.</strong><br />\r\n			<em><strong>Экскурсия по Самарканду:</strong></em> Обсерватория Улугбека (15 век), Святыня - захоронениеПророка Даниила, Музей археологических раскопок «Руины Афросиаба».<br />\r\n			<strong>Ужин</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">ДЕНЬ 11</td>\r\n			<td rowspan=\"2\">Самарканд – Ташкент</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Завтрак.</strong><br />\r\n			Переезд в Ташкент по дороге посещение Зарафшанского заповедника.<br />\r\n			Ознакомление с флорой и фауной, посещение вольера (животные из красной книге Узбекистана) и наблюдение за животными в диких условиях.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Обед.</strong><br />\r\n			Прибытие в Ташкент, размещение в гостинице, свободное время<br />\r\n			<strong>Прощальный Ужин.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">ДЕНЬ 12</td>\r\n			<td rowspan=\"2\">Ташкент</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Завтрак</strong><br />\r\n			Трансфер в аэропорт, вылет домой.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>Окончание наших услуг<strong><em>… </em></strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><strong><em>Все экскурсии проводятся с помощью аудио гида</em></strong></p>\r\n\r\n<p><strong><em>На протяжение всего маршрута доступен бесплатный Wi-Fi</em></strong></p>\r\n', '30', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('de', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n	<tbody>\r\n		<tr>\r\n			<th style=\"width:10%;\">TAGE</th>\r\n			<th style=\"width:14%;\">REISEROUTE</th>\r\n			<th style=\"width:8%;\">ZEIT</th>\r\n			<th>THEMEN DER REISEROUTE</th>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">Tag1</td>\r\n			<td rowspan=\"2\">Frankfurt - Taschkent</td>\r\n			<td>AM</td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Ankunft in Taschkent um 20:25 Uhr von Frankfurt mit </strong><strong>НУ</strong><strong>-232. </strong></p>\r\n\r\n			<p>Unterkunft im Hotel.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">Tag 2</td>\r\n			<td rowspan=\"2\">Taschkent</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Frühstück.</strong><br />\r\n			Wir werden den 1924 gegründeten Tashkent Zoo besuchen. Hier lernen wir die Tierwelt Usbekistans und der Welt kennen.<br />\r\n			Besuch des 1950 gegründeten Botanischen Gartens von Taschkent. Vortrag über die Flora und Fauna Usbekistans.<br />\r\n			<strong>Mittagsessen</strong><strong>.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p>Stadtrundfahrt in Taschkent<strong>:</strong> Die Altstadt von Taschkent.<br />\r\n			Besuch des Khast Imam Komplexes, bestehend aus der Barak Khan Madrasah, der Till Sheikh Moschee - dem Aufbewahrungsort des berühmten Koran Osman und den goldenen Haaren des Propheten Muhammad; Die Juma-Moschee von Khoja Ahrar Vali.<br />\r\n			<strong>Abendessen</strong><strong>.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">Tag 3</td>\r\n			<td rowspan=\"2\">Taschkent – Nukus – Muynak</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Frühstück.</strong><br />\r\n			Transfer zum lokalen Flughafen, Flug nach Nukus NU-011 Abfahrt um 07.25 Uhr Ankunft um 09.05 Uhr.<br />\r\n			Transfer nach Muynak, unterwegs Zwischenstopp und Flussblick. Amu Darya. Ankunft in Muynak und Unterbringung in Jurten.<br />\r\n			<strong>Mittagsessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p>Stadtrundfahrt in Muynak: Besuch des Schiffsfriedhofs, des Heimatmuseums, der alten Häuser der Muynak-Fischer, der Muynak-Bucht. Vortrag über die Aral-Katastrophe.<br />\r\n			<strong>Abendessen.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">Tag 4</td>\r\n			<td rowspan=\"2\">Muynak <strong><em>– Nukus – Chiwa </em></strong></td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Frühere Frühstück.</strong><br />\r\n			Abfahrt nach Nukus, Ankunft und Besuch des Savite-Museums.<br />\r\n			<strong>Mittagsessen</strong><strong>.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p>Dann fahren wir nach Chiwa und besuchen unterwegs das Bagai-Tugai-Reservat. Bekanntschaft mit Flora und Fauna, Besuch der Voliere (Buchara-Rotwild) und Beobachtung von Tieren in freier Wildbahn. Bootfahren in der Amu Darya.<br />\r\n			Ankunft in Chiwa und Unterbringung.<br />\r\n			<strong>Abendessen</strong><strong>.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">Tag 5</td>\r\n			<td rowspan=\"2\">Chiwa</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Fr</strong><strong>ü</strong><strong>hst</strong><strong>ü</strong><strong>ck</strong><strong>.</strong><br />\r\n			Stadtrundfahrt in Chiwa: Die befestigte Stadt Kunya-Ark, einschließlich Kurinish Khan (17. Jahrhundert), Amin Khan Madrasah (19. Jahrhundert), Islam Khoja Minarett, Palvan Kara Komplexe, Abd Al Bobo (18. Jahrhundert), Rafanek Madrasah, Mohamad Rahim Khan (19. Jahrhundert) , Amir Tours Madrasah, Sho-i-Mausoleum - Kalandar Bobo<br />\r\n			<strong>Mittagsessen</strong><strong>.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p>Stadtrundfahrt in in Chiwa: Mazar-i-Sharif-Medresse, alte Mauer von Heyvah (10. Jahrhundert), Uch Avliy-Mausoleum, Freitagsmoschee (18. Jahrhundert), Kutlug-Murad Inaka-Medresse (19. Jahrhundert), Pakhlavan Mahmud-Mausoleum, Tash Khavli-Palast, Alla Kuli Khan-Medresse (18. Jahrhundert), Araber Mohammed Khan (18. Jahrhundert).<br />\r\n			<strong>Abendessen</strong><strong>.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">Tag6</td>\r\n			<td rowspan=\"2\">\r\n			<p>Chiwa – Buchara</p>\r\n			</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Fr</strong><strong>ü</strong><strong>hst</strong><strong>ü</strong><strong>ck.</strong><br />\r\n			Transfer zum Bahnhof in Chiwa, Zugfahrt nach Buchara um 08.30 Uhr, Ankunft in Buchara um 14:45 Uhr.<br />\r\n			<strong>Mittagsessen unterwegs (Lunchboxen).</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p>Transfer zum Eco-Center Jeyran. Bekanntschaft mit Flora und Fauna, Besuch der Voliere (Dzheyrana im Roten Buch) und Beobachtung von Tieren in freier Wildbahn.<br />\r\n			Transfer nach Buchara, Hotelunterkunft.<br />\r\n			<strong>Abendessen.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">Tag7</td>\r\n			<td rowspan=\"2\">Buchara</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Frühstück.</strong><br />\r\n			Stadtrundfahrt in<em> Buchara</em>: Mausoleum der Samaniden (9.-10. Jahrhundert), Mausoleum von Chashma-Ayub (14. Jahrhundert), Bolo-Khauz-Moschee (18. Jahrhundert), Winterresidenz des Buchara-Emirs \"ARK\" (16. Jahrhundert).</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Mittagsessen<br />\r\n			Stadtrundfahrt in<em> Buchara</em></strong><em><strong>е</strong></em><em><strong>:</strong></em> Das architektonische Ensemble Po-I Colon (einschließlich des Minaretts (12. Jahrhundert), der Moschee (15. Jahrhundert), der bestehenden miriarabischen Medresse (16. Jahrhundert), der Alim Khan-Medresse (14. Jahrhundert), der Taki-Medresse (Handelskammer) und der Kukeldash-Medresse (16. Jahrhundert) ), Nadir Divanbegi (17. Jahrhundert), Magok-i Atari-Moschee (16. Jahrhundert), Lyab-i Khauz (17. Jahrhundert), Chor-Minor-Moschee (Vier Minarette).<br />\r\n			<strong>Abendessen</strong><strong>.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">Tag8</td>\r\n			<td rowspan=\"2\">Buchara – Nurata – Jurtenlager</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Fr</strong><strong>ü</strong><strong>hst</strong><strong>ü</strong><strong>ck.</strong><br />\r\n			Fahrt nach Nurata mit dem Bus, unterwegs besuchen wir Sardoba und uralte Karavan Saray in Malik Rabat.<br />\r\n			Ankunft in Nurota, Besichtigung der heiligen Quelle \"Chashma\", der Ruinen der Festung Alexanders des Großen.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Mittagsessen.</strong><br />\r\n			Transfer zum Jurtenlager, Ankunft und Unterbringung im Jurtenlager.<br />\r\n			Kamelreiten.<br />\r\n			<strong>Abendessen an dem Feuer mit Akin (Folkloresänger). Übernachtung im Jurtenlager.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">Tag9</td>\r\n			<td rowspan=\"2\">Jurtenlager <strong><em>– Samarkand </em></strong></td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Frühstück.</strong><br />\r\n			Abfahrt zum Aydarkul See.<br />\r\n			Bei der Ankunft am See können Sie schwimmen und ein Picknick machen.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Mittagsessen.</strong><br />\r\n			Transfer nach Samarkand, Ankunft und Hotelübernachtung<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">Tag10</td>\r\n			<td rowspan=\"2\">Samarkand</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Frühstück.</strong><br />\r\n			Stadtrundfahrt in<em> Samarkand:</em> Gur-Emir-Mausoleum (15. Jahrhundert), Registan-Architekturensemble (einschließlich Ulugbek-Medresse (15. Jahrhundert), Sher-Dor (17. Jahrhundert), Tilla-Kari (17. Jahrhundert)), Bibi-Khanum-Moschee (15. Jahrhundert). Besuch des Samarkand Basars, Shahi Zinda Ensemble (14.-15. Jahrhundert)</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Mittagsessen</strong><strong>.</strong><br />\r\n			Stadtrundfahrt in<em> Samarkand:</em> Ulugbek Observatorium (15. Jahrhundert), Heiligtum - Grabstätte des Propheten Daniel, Museum der archäologischen Stätte „Ruinen von Afrosiab“.<br />\r\n			<strong>Abendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">Tag11</td>\r\n			<td rowspan=\"2\">Samarkand – Taschkent</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Frühstück.</strong><br />\r\n			Transfer nach Taschkent auf dem Weg zum Zarafshan Reservat.<br />\r\n			Bekanntschaft mit Flora und Fauna, Besuch der Voliere (Tiere aus dem Roten Buch Usbekistans) und Beobachtung von Wildtieren.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Mittagsessen.</strong><br />\r\n			Ankunft in Taschkent, Hotelübernachtung, Freizeit<br />\r\n			<strong>Abschiedsabendessen</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">Tag12</td>\r\n			<td rowspan=\"2\">Taschkent - Frankfurt</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Fr</strong><strong>ü</strong><strong>here</strong> <strong>Fr</strong><strong>ü</strong><strong>hst</strong><strong>ü</strong><strong>ck.</strong><br />\r\n			Transfer zum internationalen Flughafen in Taschkent, Abflug um 05:45 mit HY-231</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>Ende unserer Leistungen …</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '30', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('en', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n	<tbody>\r\n		<tr>\r\n			<th style=\"width:8%;\">Days</th>\r\n			<th style=\"width:14%;\">Waypoint</th>\r\n			<th style=\"width:8%;\">Time</th>\r\n			<th>Subjects of itinerary</th>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 1</td>\r\n			<td rowspan=\"2\">Tashkent</td>\r\n			<td>AM</td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p>Arrival to Tashkent. Transfer and accommodation in hotel.<br />\r\n			<strong>Dinner</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 2</td>\r\n			<td rowspan=\"2\">Tashkent</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong>.<br />\r\n			We will visit the Tashkent Zoo founded in 1924. Here we will get acquainted with the wildlife of Uzbekistan and the World.<br />\r\n			Visit to the Tashkent Botanical Garden, founded in 1950. Lecture on the flora and fauna of Uzbekistan.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Lunch<br />\r\n			<em>Sightseeing tour in Tashkent:</em></strong> The old part of the city of Tashkent.<br />\r\n			Visit to the Khast Imam complex, consisting of the Barak Khan Madrasah, Till Sheikh Mosque - the repository of the famous Koran Osman and the golden hair of the Prophet Muhammad; The Juma Mosque of Khoja Ahrar Vali.<br />\r\n			<strong>Dinner</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 3</td>\r\n			<td rowspan=\"2\">Tashkent – Nukus - Muinak</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong><br />\r\n			Transfer to the local airport, flight to Nukus departure at 07.25 arrival at 09.05. Transfer to Muynak, on the way a stop and an overview of the river. Amu Darya. Arrival in Muynak and accommodation in yurts.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Lunch</strong><br />\r\n			<strong><em>Sightseeing tour in Muinak</em></strong>: Visiting the Ship Cemetery, the Museum of Local Lore, the old houses of the Muynak fishermen, the Muynak Bay. Lecture on the Aral disaster.<br />\r\n			<strong>Dinner</strong>.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 4</td>\r\n			<td rowspan=\"2\">Muinak – Nukus – Khiva</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Early breakfast.</strong><br />\r\n			Departure to Nukus. Upon arrival, visit the Museum of Savitsky.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Lunch</strong><br />\r\n			Further we drive to Khiva, on the way we visit the Bagai-Tugai Reserve. Acquaintance with flora and fauna, visiting the aviary (Bukhara Deer in the Red Book) and observing animals in the wild. Boating in the Amu Darya.<br />\r\n			Arrival in Khiva and accommodation.<br />\r\n			<strong>Dinner</strong>.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 5</td>\r\n			<td rowspan=\"2\">Khiva</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Breakfast<br />\r\n			<em>Sightseeing tour in Khiva:</em></strong> Kunya-Ark fortress, including Kurinish Khana (17c); Mohammed-Amin-Khan (19c) Madrassah, Islam Khodja Minaret; Palvan-Kari complex, Abd Al Bobo complex (18c), Rafanek Madrassah, Mohammed Rakim Khan (19 c), Amir-Tur Madrassah, Sho-I-Kalandar Bobo Mausoleum.</p>\r\n\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Lunch<br />\r\n			<em>Sightseeing tour in Khiva:</em></strong> Mazary Sharif Madrassah, Kheivak well (10c), Uch Avlioli (“three saints”) Mausoleum, Juma Mosque (18c), Kutluq-Murad Inaq (19c) Madrassah, Pakhlavan Mahmoud Mausoleum, Tash Hauli (“stone yard”) Palace, Alla-Kuli-Khan (18c), Arab Mohammed Khan (18 c) Madrassahs.<br />\r\n			<strong>Dinner with Khorezmian dance show</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 6</td>\r\n			<td rowspan=\"2\">\r\n			<p>Khiva - Bukhara</p>\r\n			</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong>.<br />\r\n			Transfer to Khiva train station. Train to Bukhara at 08.30.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Lunch in train in lunch boxes</strong><br />\r\n			Arrival in Bukhara at 14.45.&nbsp; Transfer to the Eco-Center Jeyran. Acquaintance with flora and fauna, a visit to the aviary (Dzheyrana in the red book) and observation of animals in the wild.<br />\r\n			Transfer to Bukhara, hotel accommodation.<br />\r\n			<strong>Dinner</strong>.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 7</td>\r\n			<td rowspan=\"2\">Bukhara</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong><br />\r\n			<em><strong>Sightseeing tour in Bukhara:</strong></em> Visit to Registan, Ark Fortress (16 c) and Bolo-Hauz complex (18 c). Ulug Beg (15c), Abdul Aziz (17c) Madrassahs; Chor-Minor Mausoleums. Ismael Samani mausoleum (9-10c)-vault of Bukhara rulers dynasty, Chashma-Ayub Mausoleums (14 c)</p>\r\n\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Lunch<br />\r\n			<em>Sightseeing tour in Bukhara:</em></strong> Poi-Kalyan Ensemble including Minaret (12c) Mosque (15c), functioning Madrassah Miri-Arab (16 c), Emir-Alim-Khan Madrassah (14c), Taqi (trading domes), Kukeldash Madrassah (16c), Nadir Divanbegi (17c), Magok-i-Attari Mosque (16c), Lyab-i-Hauz (17c).<br />\r\n			<strong>Dinner with Folk Show.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 8</td>\r\n			<td rowspan=\"2\">Bukhara – Nurata – Yurt camp</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong><br />\r\n			Drive to Aydarkul Lake Yurt camp. On the way visit Rabat-i-Malik Karavan Serai in Karmana and the ruins of Alexander the Great fortress and petroglyphs and old saint Chashma mosque in Nurata.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Lunch</strong><br />\r\n			Transfer deeper to the desert to the Camel Camp, accommodation in national Yurts, Camel Back riding for 10-15 minutes.&nbsp;&nbsp;<br />\r\n			<strong>Dinner around the fireplace with akyn (Nomadic epic singer) performance.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 9</td>\r\n			<td rowspan=\"2\">Yurt camp - Samarkand</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong><br />\r\n			Drive to Aydarkul Lake. Walk on the lakeside. Bathing depending on the weather and season.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Lunch pic-nic on the lakeside</strong><br />\r\n			Afternoon drive to Samarkand. Arrival and accommodation at hotel.<br />\r\n			<strong>Dinner</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 10</td>\r\n			<td rowspan=\"2\">Samarkand</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Breakfast<br />\r\n			<em>Sightseeing tour in Samarkand:</em></strong> Gur-Emir Mausoleum (15c), Registan Ensemble (including Ulugbek (15c), Shir Dor (17c), Tillya –Kari Madrassahs (17c). &nbsp;Bibi Khanum Mosque, Visit to Samarkand bazaar.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Lunch</strong><br />\r\n			<em><strong>Sightseeing tour in Samarkand:</strong></em> Shah-i-Zinda necropolis (14-15c) “Stars above Samarkand”: tour to Ulugbek observatory (15c), “Ruins of Afrosiab” State archaeological reserve (6 BC-13 AD).<br />\r\n			<strong>Dinner</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 11</td>\r\n			<td rowspan=\"2\">Samarkand - Tashkent</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong><br />\r\n			Transfer to Tashkent on the way visiting the Zarafshan reserve.<br />\r\n			Acquaintance with flora and fauna, visiting an aviary (animals from the Red Book of Uzbekistan) and observing animals in the wild.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>\r\n			<p><strong>Lunch</strong><br />\r\n			Arrival in Tashkent, hotel accommodation, free time<br />\r\n			<strong>Farewell dinner.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 12</td>\r\n			<td rowspan=\"2\">Tashkent</td>\r\n			<td>AM</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong><br />\r\n			Transfer to airport. Flight back home.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PM</td>\r\n			<td>End of our services…</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '30', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('it', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n   <tbody>\r\n      <tr>\r\n         <th style=\"width:8%;\">Giorni</th>\r\n         <th style=\"width:14%;\">Tappe</th>\r\n         <th>Soggetti dell’itinerario</th>\r\n      </tr>\r\n      <tr>\r\n         <td rowspan=\"2\">GIORNO 1</td>\r\n         <td rowspan=\"2\">Тashkent - Arrivo</td>\r\n         <td>&nbsp;</td>\r\n      </tr>\r\n      <tr>\r\n         <td>\r\n         <p>PM. Arrivo a Tashkent. Trasferimento e sistemazione in albergo.<br />\r\n         <strong>Cena</strong></p>\r\n         </td>\r\n      </tr>\r\n      <tr>\r\n         <td rowspan=\"2\">GIORNO 2</td>\r\n         <td rowspan=\"2\">Tashkent</td>\r\n         <td>\r\n         <p><strong>Colazione</strong>.<br />\r\n         Visiteremo il Centro Culturale dei Tedeschi dell\'Uzbekistan “Wiedergeburt” - “Rinascimento” creato nel 1990. Che oggi unisce i centri di Bukhara, Samarcanda e Ferghana. Lì faremo conoscenza con la formazione e lo sviluppo della cultura e della vita dei tedeschi dell\'Asia centrale. Il centro culturale ha vari gruppi di interesse, arte popolare, teatro e danza per i giovani.</p>\r\n         </td>\r\n      </tr>\r\n      <tr>\r\n         <td>\r\n         <p><strong>Pranzo</strong>.<br />\r\n         <strong><em>Giro turistico a Tashkent:</em></strong> La parte vecchia della città di Tashkent.<br />\r\n         Visita al complesso Khast Imam, composto dalla Madrasa Barak Khan, dalla Moschea Till Sheikh - il deposito del famoso Corano Osman e dai capelli d\'oro del profeta Maometto; La Moschea Juma di Khoja Ahrar Vali.<br />\r\n         <strong>Cena</strong>.</p>\r\n         </td>\r\n      </tr>\r\n      <tr>\r\n         <td rowspan=\"2\">GIORNO 3</td>\r\n         <td rowspan=\"2\">Tashkent - Bukhara</td>\r\n         <td>\r\n         <p><strong>Colazione</strong>.<br />\r\n         Alle 06:30 Trasferimento in stazione.<br />\r\n         Partenza per Bukhara con il treno ad alta velocità Afrasiab alle 07:30, arrivo alle 11:19. All\'arrivo, visiteremo il centro nazionale e culturale degli ebrei di Bukhara \"Shalom\" fondato nel 1990. Dove conosceremo la storia e la cultura degli ebrei.</p>\r\n         </td>\r\n      </tr>\r\n      <tr>\r\n         <td>\r\n         <p><strong>Pranzo a casa di un mercante ebreo.</strong><br />\r\n         Sistemazione in hotel dopo pranzo.<br />\r\n         <em><strong>Giro turistico a Bukhara:</strong></em> il complesso architettonico Po-I Colon (incluso il minareto (XII secolo), la moschea (XV secolo), l\'attuale Madrasa Miri-Arab (16°), Alim Khan Madrasa (14° secolo), Taki (camere di commercio ), Kukeldash Madrasah (XVI secolo), Nadir Divanbegi (17cc), Moschea Magok-i Atari (XVI secolo), Lyab-i Khauz (XVII secolo), Moschea Chor-Minor (Quattro Minareti).<br />\r\n         <strong>Cena</strong>.</p>\r\n         </td>\r\n      </tr>\r\n      <tr>\r\n         <td rowspan=\"2\">GIORNO 4</td>\r\n         <td rowspan=\"2\">Bukhara – Djeynau – Shakhrisabz</td>\r\n         <td>\r\n         <p><strong>Colazione</strong>.<br />\r\n         Partenza per l\'insediamento di tipo urbano Dzheynau situato a 140 km da Bukhara, nella regione di Kashkadarya. Più dell\'80% della popolazione è araba. Qui conosceremo la cultura, lo stile di vita e la quotidianità degli arabi che abitano questo borgo dal XIV secolo. Visiteremo anche il Centro Culturale Nazionale Arabo, fondato nel 1991. Museo Etnografico delle Tradizioni e Costumi degli Arabi.</p>\r\n         </td>\r\n      </tr>\r\n      <tr>\r\n         <td>\r\n         <p><strong>Pranzo in villaggio.</strong><br />\r\n         Inoltre continuiamo a guidare verso Shakhrisabz, la città natale di Amir Timur. All\'arrivo sistemazione in albergo e tempo libero.<br />\r\n         <strong>Cena</strong>.</p>\r\n         </td>\r\n      </tr>\r\n      <tr>\r\n         <td rowspan=\"2\">GIORNO 5</td>\r\n         <td rowspan=\"2\">Shakhrisabz - Samarcanda</td>\r\n         <td>\r\n         <p><strong>Colazione</strong>.<br />\r\n         <strong><em>Giro turistico a Shakhrisabz: </em></strong>Visita il Mausoleo di Jahongir, il complesso commemorativo Dor-Us Saodat, le rovine del Palazzo Ak-Saray, l\'ensemble Dor-Ut Tilavat con la Moschea Kok-Gumbaz e la tomba di Padre Tamerlano.</p>\r\n         </td>\r\n      </tr>\r\n      <tr>\r\n         <td>\r\n         <p><strong>Pranzo</strong>.<br />\r\n         Dopo pranzo trasferimento a Samarcanda, la perla d\'Oriente. Sistemazione in albergo. Riposo e tempo libero.<br />\r\n         <strong>Cena</strong>.</p>\r\n         </td>\r\n      </tr>\r\n      <tr>\r\n         <td rowspan=\"2\">GIORNO 6</td>\r\n         <td rowspan=\"2\">\r\n         <p>Samarcanda</p>\r\n         </td>\r\n         <td>\r\n         <p><strong>Colazione</strong>.<br />\r\n         Oggi visiteremo la Casa dell\'Amicizia dove si trova il Centro Culturale Tedesco. Gli attivisti del centro partecipano a molti eventi folkloristici. Faremo conoscenza con le attività del centro, conosceremo la storia dei tedeschi dell\'Asia centrale. Il centro svolge anche le sue attività nazionali e culturali dell\'Azerbaigian, coreano, polacco e di altre nazionalità.</p>\r\n         </td>\r\n      </tr>\r\n      <tr>\r\n         <td>\r\n         <p><strong>Pranzo</strong>.<br />\r\n         <strong><em>Giro turistico a Samarcanda: </em></strong>Mausoleo Gur-Emir (XV secolo), complesso architettonico del Registan (inclusa Madrasa Ulugbek (XV secolo), Sher-Dor (XVII secolo), Tilla-Kari (XVII secolo)), Moschea Bibi-Khanum (XV secolo) ).<br />\r\n         <strong>Cena</strong>.</p>\r\n         </td>\r\n      </tr>\r\n      <tr>\r\n         <td rowspan=\"2\">GIORNO 7</td>\r\n         <td rowspan=\"2\">Samarcanda - Tashkent</td>\r\n         <td>\r\n         <p><strong>Colazione</strong>.<br />\r\n         <strong><em>Giro turistico a Samarcanda: </em></strong>L\'ensemble di Shahi - Zinda (14-15 secolo), l\'Osservatorio di Ulugbek (15 secolo), il Santuario - la sepoltura del profeta Daniele, le rovine dell\'antico insediamento \"Afrosiab\"..</p>\r\n         </td>\r\n      </tr>\r\n      <tr>\r\n         <td>\r\n         <p><strong>Pranzo</strong> alla casa nazionale.<br />\r\n         Trasferimento a Tashkent. Sistemazione in albergo.<br />\r\n         Passeggia e fai shopping al Chorsu Oriental Bazaar..<br />\r\n         <strong>Cena</strong> al ristorante uiguro.</p>\r\n         </td>\r\n      </tr>\r\n      <tr>\r\n         <td rowspan=\"2\">GIORNO 8</td>\r\n         <td rowspan=\"2\">Tashkent - partenza</td>\r\n         <td>\r\n         <p><strong>Colazione</strong>.<br />\r\n         Trasferimento all’aeroporto. Volo di ritorno.</p>\r\n         </td>\r\n      </tr>\r\n      <tr>\r\n         <td>Fine dei nosri servizi…</td>\r\n      </tr>\r\n   </tbody>\r\n</table>\r\n', '31', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('ru', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n	<tbody>\r\n		<tr>\r\n			<th style=\"width:8%;\">ДНИ</th>\r\n			<th style=\"width:14%;\">МАРШРУТ</th>\r\n			<th>ПРОГРАММА</th>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">ДЕНЬ 1</td>\r\n			<td rowspan=\"2\">Ташкент</td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Прибытие в Ташкент. Трансфер в гостиницу, размещение.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">ДЕНЬ 2</td>\r\n			<td rowspan=\"2\">Ташкент</td>\r\n			<td>\r\n			<p><strong>Завтрак.</strong><br />\r\n			Мы посетим Куль­тур­ный центр немцев Узбекистана «<strong>Wiedergeburt</strong>» — «Возрождение» созданный в 1990 году. Который сегодня объединяет центры в Бухаре, Самарканде и Фергане. Где мы ознакомимся с формированием и развитием культуры и быта среднеазиатских немцев. При культурном центре действуют различные кружки по интересам, народного творчества, театрального и танцевального искусства для молодежи.<br />\r\n			<strong>Обед.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Экскурсия по Ташкенту:</strong> Старая часть города Ташкента.<br />\r\n			Посещение комплекса Хаст Имам, состоящего из Медресе Барак-Хана, мечети Тилля-Шейха - хранилище знаменитого Корана Османа и золотого волоса пророка Мухаммада; Джума-мечеть Ходжа Ахрар Вали.<br />\r\n			<strong>Ужин.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">ДЕНЬ 3</td>\r\n			<td rowspan=\"2\">Ташкент - Бухара</td>\r\n			<td>\r\n			<p><strong>Завтрак.<br />\r\n			06:30 Трансфер на вокзал.<br />\r\n			Отправление в Бухару скоростном поезде Афрасиаб в 07:30, прибытие в 11:19.</strong><br />\r\n			По приезду мы посетим национально-культурный центр бухарских евреев «Шалом» основанный в 1990г. Где ознакомимся с историей и культурой евреев.<br />\r\n			<strong>Обед в доме еврейского купца.<br />\r\n			Размещение в гостинице после обеда.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Экскурсия по Бухаре:</strong><br />\r\n			Архитектурный ансамбль По-и Колон (включая Минарет (12 век), Мечеть (15 век), действующую медресе Мири-Араб (16 век), Медресе Алим-Хана (14 век), Таки (торговые палаты), Медресе Кукельдаш (16 век), Надир Диванбеги (17век), Мечеть Магок-и Атари (16 век), Ляб-и Хауз (17 век), мечеть Чор-минор (Четыре минарета).<br />\r\n			<strong>Ужин.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">ДЕНЬ 4</td>\r\n			<td rowspan=\"2\">Бухара – Джейнау - Шахрисабз</td>\r\n			<td>\r\n			<p><strong>Завтрак.</strong><br />\r\n			Выезд в поселок городского типа Джейнау в 140км от Бухары, в Кашкадарьинской области. Более 80% населения составляют арабы. Здесь мы ознакомимся культурой, образом жизни и бытом арабов которые проживают в этом поселке с XIV века. Также мы здесь посетим Арабский национальный культурный центр, созданный в 1991г. Этнографический музей традиций и обычаев арабов.</p>\r\n\r\n			<p><strong>13:00 – 14:00 Обед в поселке.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Далее мы едем в Шахрисабз, родной город Амира Тимура. Размещение в гостинице и свободное время.<br />\r\n			<strong>Ужин.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">ДЕНЬ 5</td>\r\n			<td rowspan=\"2\">Шахрисабз - Самарканд</td>\r\n			<td>\r\n			<p><strong>Завтрак.<br />\r\n			<em>Экскурсия по Шахрисабзу:</em> </strong>Посещение Мавзолея Джахонгира, мемориального комплекса Дор-ус Саодат, руин дворца Ак-Сарай, ансамбля Дор-ут Тилават с мечетью Кок-Гумбаз и усыпальницей отца Тамерлана.</p>\r\n\r\n			<p><strong>Обед.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>После обеда переезд в Самарканд, жемчужину Востока. Размещение в гостинице. Отдых и свободное время.<br />\r\n			<strong>Ужин.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">ДЕНЬ 6</td>\r\n			<td rowspan=\"2\">\r\n			<p>Самарканд</p>\r\n			</td>\r\n			<td>\r\n			<p><strong>Завтрак.</strong><br />\r\n			Сегодня мы посетим Дом дружбы где расположен Немецкий культурный центр. Активисты центра учувствуют во многих народных мероприятиях. Мы ознакомимся деятельностью центра, узнаем про историю немцев Средней Азии. В центре также ведут свою национально-культурную деятельность Азербайджанский, Корейский, Польский и другие народности.<br />\r\n			<strong>Обед.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Экскурсия по Самарканду:</strong><br />\r\n			Мавзолей Гур-Эмир (15век), Архитектурный ансамбль Регистан (включая Медресе Улугбека (15 век), Шер-Дор (17 век), Тилля-Кари (17 век)), Мечеть Биби-Ханум (15 век).<br />\r\n			<strong>Ужин.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">ДЕНЬ 7</td>\r\n			<td rowspan=\"2\">Самарканд - Ташкент</td>\r\n			<td>\r\n			<p><strong>Завтрак.<br />\r\n			<em>Экскурсия по Самарканду:</em></strong>Ансамбль Шахи - Зинда (14-15 век), Обсерватория Улугбека (15 век), Святыня - захоронение Пророка Даниила, руины древнего городища «Афросиаб»<strong>.<br />\r\n			Обед в национальном доме.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Переезд в Ташкент. Размещение в отеле.<br />\r\n			Прогулка и покупки на восточном базаре Чорсу.<br />\r\n			<strong>Ужин в уйгурском ресторане.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">ДЕНЬ 8</td>\r\n			<td rowspan=\"2\">Ташкент - Франкфурт</td>\r\n			<td>\r\n			<p><strong>Завтрак</strong><br />\r\n			Трансфер в аэропорт, вылет домой.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Окончание наших услуг<strong><em>… </em></strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><strong><em>Все экскурсии проводятся с помощью аудио гида</em></strong></p>\r\n\r\n<p><strong><em>На протяжение всего маршрута доступен бесплатный Wi-Fi</em></strong></p>\r\n', '31', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('de', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n	<tbody>\r\n		<tr>\r\n			<th style=\"width:10%;\">TAGE</th>\r\n			<th style=\"width:14%;\">REISEROUTE</th>\r\n			<th>THEMEN DER REISEROUTE</th>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">Tag1</td>\r\n			<td rowspan=\"2\">Frankfurt - Taschkent</td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Ankunft in Taschkent um 20:25 Uhr von Frankfurt mit </strong><strong>НУ</strong><strong>-232. </strong></p>\r\n\r\n			<p>Unterkunft im Hotel.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">Tag 2</td>\r\n			<td rowspan=\"2\">Taschkent</td>\r\n			<td>\r\n			<p><strong>Frühstück.</strong><br />\r\n			Wir besuchen das 1990 gegründete Kulturzentrum der Deutschen Usbekistans \"Wiedergeburt\". Was heute die Zentren in Buchara, Samarkand und Ferghana vereint. Hier lernen wir die Bildung und Entwicklung von Kultur und Leben der zentralasiatischen Deutschen kennen. Das Kulturzentrum hat verschiedene Interessengruppen, Volkskunst, Theater und Tanzkunst für junge Leute.<br />\r\n			<strong>Mittagsessen</strong><strong>.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Stadtrundfahrt in Taschkent<strong>:</strong> Die Altstadt von Taschkent.<br />\r\n			Besuch des Khast Imam Komplexes, bestehend aus der Barak Khan Madrasah, der Till Sheikh Moschee - dem Aufbewahrungsort des berühmten Koran Osman und den goldenen Haaren des Propheten Muhammad; Die Juma-Moschee von Khoja Ahrar Vali.<br />\r\n			<strong>Abendessen</strong><strong>.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">Tag 3</td>\r\n			<td rowspan=\"2\">Taschkent <strong><em>- Buchara </em></strong></td>\r\n			<td>\r\n			<p><strong>Frühstück.</strong></p>\r\n\r\n			<p><strong>06:30 Transfer zum Bahnhof.</strong></p>\r\n\r\n			<p><strong>Abfahrt nach Buchara mit dem Afrosiab-Hochgeschwindigkeitszug um 07:30 Uhr, Ankunft um 11:19 Uhr.</strong></p>\r\n\r\n			<p>Nach der Ankunft besuchen wir das 1990 gegründete nationale und kulturelle Zentrum der Bukhara-Juden \"Shalom\". Hier lernen wir die Geschichte und Kultur der Juden kennen.</p>\r\n\r\n			<p><strong>Mittagsessen im Haus des Jüdischen Kaufmannes.</strong></p>\r\n\r\n			<p><strong>Unterkunft</strong> <strong>im</strong> <strong>Hotel</strong> <strong>nachmittags</strong><strong>.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><em><strong>Stadtrundfahrt in Buchara:</strong></em> Das architektonische Ensemble Po-I Colon (einschließlich des Minaretts (12. Jahrhundert), der Moschee (15. Jahrhundert), der bestehenden miriarabischen Medresse (16. Jahrhundert), der Alim Khan-Medresse (14. Jahrhundert), der Taki-Medresse (Handelskammer) und der Kukeldash-Medresse (16. Jahrhundert) ), Nadir Divanbegi (17. Jahrhundert), Magok-i Atari-Moschee (16. Jahrhundert), Lyab-i Khauz (17. Jahrhundert), Chor-Minor-Moschee (Vier Minarette).<br />\r\n			<strong>Abendessen</strong><strong>.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">Tag 4</td>\r\n			<td rowspan=\"2\">Buchara <strong><em>– Djeynau - Schachrisabz </em></strong></td>\r\n			<td>\r\n			<p><strong>Frühstück.</strong><br />\r\n			Abfahrt zur städtischen Siedlung Dzheynau, 140 km von Buchara entfernt, in der Region Kashkadarya. Über 80% der Bevölkerung sind Araber. Hier lernen wir die Kultur, den Lebensstil und den Alltag der Araber kennen, die seit dem 14. Jahrhundert in diesem Dorf leben. Wir werden auch das 1991 gegründete Arab National Cultural Center besuchen. Ethnographisches Museum für Traditionen und Bräuche der Araber.<br />\r\n			<strong>13:00 – 14:00 Mittagsessen im Dorf.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Weiter geht es nach Shakhrisabz, der Heimatstadt von Amir Timur. Hotelübernachtung und Freizeit.<br />\r\n			<strong>Abendessen</strong><strong>.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">Tag 5</td>\r\n			<td rowspan=\"2\">Schachrisabz <strong><em>- Samarkand </em></strong></td>\r\n			<td>\r\n			<p><strong>Fr</strong><strong>ü</strong><strong>st</strong><strong>ü</strong><strong>ck</strong><strong>.</strong><br />\r\n			Stadtrundfahrt in Schachrusabz: Besuchen Sie das Jahongir-Mausoleum, den Dor-Us-Saodat-Gedächtniskomplex, die Ruinen des Ak-Saray-Palastes, das Dor-Ut-Tilavat-Ensemble mit der Kok-Gumbaz-Moschee und das Grab von Pater Tamerlane.<br />\r\n			<strong>Mittagsessen</strong><strong>.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Nach dem Mittagessen Transfer nach Samarkand, der Perle des Ostens. Unterkunft im Hotel. Ruhe und Freizeit.<br />\r\n			<strong>Abendessen</strong><strong>.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">Tag 6</td>\r\n			<td rowspan=\"2\">\r\n			<p>Samarkand</p>\r\n			</td>\r\n			<td>\r\n			<p><strong>Frühstück.</strong><br />\r\n			Heute besuchen wir das Haus der Freundschaft, in dem sich das Deutsche Kulturzentrum befindet. Aktivisten des Zentrums nehmen an vielen Volksveranstaltungen teil. Wir werden die Aktivitäten des Zentrums kennenlernen, etwas über die Geschichte der Deutschen in Zentralasien erfahren. Das Zentrum führt auch seine nationalen und kulturellen Aktivitäten in Aserbaidschan, Korea, Polen und anderen Nationalitäten durch.<br />\r\n			<strong>Mittagsessen</strong><strong>.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Stadtrundfahrt in Samarkand: Gur-Emir-Mausoleum (15. Jahrhundert), Registan-Architekturensemble (einschließlich Ulugbek-Medresse (15. Jahrhundert), Sher-Dor (17. Jahrhundert), Tilla-Kari (17. Jahrhundert)), Bibi-Khanum-Moschee (15. Jahrhundert).<br />\r\n			<strong>Abendessen</strong><strong>.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">Tag 7</td>\r\n			<td rowspan=\"2\">Samarkand – Taschkent</td>\r\n			<td>\r\n			<p><strong>Fr</strong><strong>ü</strong><strong>hst</strong><strong>ü</strong><strong>ck</strong><strong>.</strong><br />\r\n			Stadtrundfahrt in Samarkand: Das Ensemble von Shahi-Zinda (14.-15. Jahrhundert), das Ulugbek Observatorium (15. Jahrhundert), das Heiligtum - das Begräbnis des Propheten Daniel, die Ruinen der antiken Siedlung \"Afrosiab\".<br />\r\n			<strong>Mittagsessen im nationalen Haus</strong><strong>.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Transfer nach Taschkent. Unterkunft im Hotel.<br />\r\n			Gehen Sie spazieren und kaufen Sie im Chorsu East Bazaar ein.<br />\r\n			<strong>Abendessen im uygurischen Restaurant.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">Tag 8</td>\r\n			<td rowspan=\"2\">Taschkent - Frankfurt</td>\r\n			<td>\r\n			<p><strong>Frühere Frühstück.</strong><br />\r\n			Transfer zum internationalen Flughafen in Taschkent, Abflug um 05:45 mit HY-231</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Ende unserer Leistungen …</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '31', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('en', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n	<tbody>\r\n		<tr>\r\n			<th style=\"width:8%;\">Days</th>\r\n			<th style=\"width:14%;\">Waypoint</th>\r\n			<th>Subjects of itinerary</th>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 1</td>\r\n			<td rowspan=\"2\">Тashkent - arrival</td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>PM. Arrival to Tashkent. Transfer and accommodation in hotel.<br />\r\n			<strong>Dinner</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 2</td>\r\n			<td rowspan=\"2\">Tashkent</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong>.<br />\r\n			We will visit the Cultural Center of the Germans of Uzbekistan “<strong>Wiedergeburt</strong>” - “Renaissance” created in 1990. Which today unites the centers in Bukhara, Samarkand and Ferghana. There we will get acquainted with the formation and development of culture and life of the Central Asian Germans. The cultural center has various interest groups, folk art, and theater and dance art for young people.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Lunch</strong>.<br />\r\n			<strong><em>Sightseeing tour in Tashkent:</em></strong> The old part of the city of Tashkent.<br />\r\n			Visit to the Khast Imam complex, consisting of the Barak Khan Madrasah, Till Sheikh Mosque - the repository of the famous Koran Osman and the golden hair of the Prophet Muhammad; The Juma Mosque of Khoja Ahrar Vali.<br />\r\n			<strong>Dinner</strong>.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 3</td>\r\n			<td rowspan=\"2\">Tashkent - Bukhara</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong>.<br />\r\n			At 06:30 Transfer to the station.<br />\r\n			Departure to Bukhara by Afrasiab high-speed train at 07:30, arrival at 11:19. Upon arrival, we will visit the national and cultural center of Bukhara Jews \"Shalom\" founded in 1990. Where we get acquainted with the history and culture of the Jews.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Lunch at the home of a Jewish merchant.</strong><br />\r\n			Accommodation at the hotel after lunch.<br />\r\n			<em><strong>Sightseeing tour in Bukhara:</strong></em> The Po-I Colon architectural ensemble (including the Minaret (12th century), Mosque (15th century), the existing Miri-Arab Madrasah (16th), Alim Khan Madrasah (14th century), Taki (chambers of commerce), Kukeldash Madrasah (16th century), Nadir Divanbegi (17cc), Magok-i Atari Mosque (16th century), Lyab-i Khauz (17th century), Chor-Minor Mosque (Four Minarets).<br />\r\n			<strong>Dinner</strong>.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 4</td>\r\n			<td rowspan=\"2\">Bukhara – Djeynau – Shakhrisabz</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong>.<br />\r\n			Departure to the urban-type settlement Dzheynau located in 140km from Bukhara, in the Kashkadarya region. More than 80% of the population are Arabs. Here we will get acquainted with the culture, lifestyle and everyday life of Arabs who have been living in this village since the XIV century. We will also visit the Arab National Cultural Center, established in 1991. Ethnographic Museum of Traditions and Customs of Arabs.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Lunch in the village.</strong><br />\r\n			Further we continue driving to Shakhrisabz, the hometown of Amir Timur. Upon arrival hotel accommodation and free time.<br />\r\n			<strong>Dinner</strong>.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 5</td>\r\n			<td rowspan=\"2\">Shakhrisabz - Samarkand</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong>.<br />\r\n			<strong><em>Sightseeing tour in Shakhrisabz: </em></strong>Visit the Jahongir Mausoleum, the Dor-Us Saodat memorial complex, the ruins of the Ak-Saray Palace, the Dor-Ut Tilavat ensemble with the Kok-Gumbaz Mosque and the tomb of Father Tamerlane.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Lunch</strong>.<br />\r\n			After lunch, transfer to Samarkand, the pearl of the East. Hotel accommodation. Rest and free time.<br />\r\n			<strong>Dinner</strong>.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 6</td>\r\n			<td rowspan=\"2\">\r\n			<p>Samarkand</p>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong>.<br />\r\n			Today we will visit the House of Friendship where the German Cultural Center is located. Activists of the center participate in many folk events. We will get acquainted with the activities of the center, learn about the history of the Germans of Central Asia. The center also conducts its national and cultural activities of Azerbaijan, Korean, Polish and other nationalities.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Lunch</strong>.<br />\r\n			<strong><em>Sightseeing tour in Samarkand: </em></strong>Gur-Emir Mausoleum (15th century), Registan architectural ensemble (including Ulugbek Madrasah (15th century), Sher-Dor (17th century), Tilla-Kari (17th century)), Bibi-Khanum Mosque (15th century).<br />\r\n			<strong>Dinner</strong>.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 7</td>\r\n			<td rowspan=\"2\">Samarkand - Tashkent</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong>.<br />\r\n			<strong><em>Sightseeing tour in Samarkand: </em></strong>The ensemble of Shahi - Zinda (14-15 century), Ulugbek Observatory (15 century), the Shrine - the burial of the Prophet Daniel, the ruins of the ancient settlement \"Afrosiab\".</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Lunch</strong> at the national house.<br />\r\n			Transfer to Tashkent. Hotel accommodation.<br />\r\n			Walk and shop at Chorsu Oriental Bazaar.<br />\r\n			<strong>Dinner</strong> at the Uyghur restaurant.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">DAY 8</td>\r\n			<td rowspan=\"2\">