INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('en', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n	<tbody>\r\n		<tr>\r\n			<th style=\"width:8%;\">Days</th>\r\n			<th style=\"width:14%;\">Waypoint</th>\r\n			<th>Subjects of itinerary</th>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>DAY 1</p>\r\n\r\n			<p>30.04</p>\r\n			</td>\r\n			<td>\r\n			<h3>Tashkent</h3>\r\n			</td>\r\n			<td>\r\n			<p>Arrival to Tashkent, start discover the Uzbek capital upon arrival.&nbsp;</p>\r\n\r\n			<p><strong><em>Sightseeing tour in Tashkent;</em></strong>&nbsp;<a class=\"in_text\" href=\"[[~744]]\" title=\"Kukeldash Madrassah\">Kukeldash Madrassah</a>,&nbsp;<a class=\"in_text\" href=\"[[~709]]\" title=\"Kaffal Shashi Mausoleum\">Kaffal Shashi Mausoleum</a>,&nbsp;<a class=\"in_text\" href=\"[[~711]]\" title=\"Barak Khan Madrassah\">Barak Khan Madrassah</a>,&nbsp;<a class=\"in_text\" href=\"[[~686]]\" title=\"Mosque on Khasty Imam Street\">Mosque on Khasty Imam Street</a>, Visit to&nbsp;<a class=\"in_text\" href=\"[[~710]]\" title=\"Chor-Su\">Chor-Su</a>&nbsp;– oriental market (handicraft bazaar)</p>\r\n\r\n			<p><strong>Lunch on self arrangement.</strong></p>\r\n\r\n			<p>Accommodation in hotel after 14:00.<br />\r\n			<strong><em>Sightseeing tour in Tashkent:</em></strong> Visit to Tashkent Metro, Catholic Polish Cathedral, and German Kirsch. Mustakillik Square (Independence Square), Amir Temur Square, Monument to Courage, Halklar Dustligi Square, Monument to Alisher Navoi.<br />\r\n			<strong>Dinner on self arrangement</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>DAY 2</p>\r\n\r\n			<p>01.05</p>\r\n			</td>\r\n			<td>\r\n			<h3>Tashkent — Samarkand</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong></p>\r\n\r\n			<p>Morning drive to Samarkand (310km, 4h drive). Start city tour upon arrival.<br />\r\n			<strong><em>Sightseeing tour in Samarkand:</em></strong>&nbsp;<a class=\"in_text\" href=\"[[~776]]\" title=\"Gur-Emir Mausoleum\">Gur-Emir Mausoleum</a>&nbsp;(15c),&nbsp;<a class=\"in_text\" href=\"[[~697]]\" title=\"Registan Ensemble\">Registan Ensemble</a>&nbsp;(including&nbsp;<a class=\"in_text\" href=\"[[~754]]\" title=\"Ulugbek\">Ulugbek</a>&nbsp;(15c),&nbsp;<a class=\"in_text\" href=\"[[~765]]\" title=\"Shir Dor\">Shir Dor</a>&nbsp;(17c),&nbsp;<a class=\"in_text\" href=\"[[~768]]\" title=\"Tillya–Kari Madrassahs\">Tillya–Kari Madrassahs</a>&nbsp;(17c),&nbsp;<a class=\"in_text\" href=\"[[~775]]\" title=\"Bibi Khanum Mosque\">Bibi Khanum Mosque</a>. Visit to Samarkand bazaar.</p>\r\n\r\n			<p><strong>Lunch on self arrangement.</strong><br />\r\n			<a class=\"in_text\" href=\"[[~764]]\" title=\"Shah-i-Zinda necropolis\">Shah-i-Zinda necropolis</a>&nbsp;(14-15c), \"Stars above Samarkand\": tour to&nbsp;<a class=\"in_text\" href=\"[[~754]]\" title=\"Ulugbek observatory\">Ulugbek observatory</a>&nbsp;(15c),&nbsp;<a class=\"in_text\" href=\"[[~595]]\" title=\"“Ruins of Afrosiab” State archaeological reserve\">“Ruins of Afrosiab” State archaeological reserve</a>&nbsp;(6 BC-13 AD) museum. Carpet factory.<br />\r\n			<strong>Dinner on self arrangement</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>DAY 3</p>\r\n\r\n			<p>02.05</p>\r\n			</td>\r\n			<td>\r\n			<h3>Samarkand — Bukhara</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast </strong></p>\r\n\r\n			<p>In the morning drive to Bukhara (260km, 4h drive). Arrival to Bukhara and accommodation in hotel.</p>\r\n\r\n			<p><strong>Lunch on self arrangement. </strong><br />\r\n			Free at leisure time.<br />\r\n			<strong>Dinner on self arrangement.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>DAY 4</p>\r\n\r\n			<p>03.05</p>\r\n			</td>\r\n			<td>\r\n			<h3>Bukhara</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong><br />\r\n			<strong><em>Sightseeing tour in Bukhara:</em></strong>&nbsp;<a class=\"in_text\" href=\"[[~769]]\" title=\"Poi-Kalyan Ensemble including Minaret\">Poi-Kalyan Ensemble including Minaret</a>&nbsp;(12c) Mosque (15c), functioning&nbsp;<a class=\"in_text\" href=\"[[~766]]\" title=\"Madrassah Miri-Arab\">Madrassah Miri-Arab</a>&nbsp;(16 c),&nbsp;<a class=\"in_text\" href=\"[[~779]]\" title=\"Emir-Alim-Khan Madrassah\">Emir-Alim-Khan Madrassah</a>&nbsp;(14c), Taqi (trading domes),&nbsp;<a class=\"in_text\" href=\"[[~744]]\" title=\"Kukeldash Madrassah\">Kukeldash Madrassah</a>&nbsp;(16c),&nbsp;<a class=\"in_text\" href=\"[[~763]]\" title=\"Nadir Divanbegi\">Nadir Divanbegi</a>&nbsp;(17c),&nbsp;<a class=\"in_text\" href=\"[[~803]]\" title=\"Magok-i-Attari Mosque\">Magok-i-Attari Mosque</a>&nbsp;(16c),&nbsp;<a class=\"in_text\" href=\"[[~703]]\" title=\"Lyab-i-Hauz\">Lyab-i-Hauz</a>&nbsp;(17c),</p>\r\n\r\n			<p><strong>Lunch on self arrangement.</strong><br />\r\n			<strong><em>Sightseeing tour in Bukhara</em></strong>: Visit to Registan,&nbsp;<a class=\"in_text\" href=\"[[~767]]\" title=\"Ark Fortress\">Ark Fortress</a>&nbsp;(16 c) and&nbsp;<a class=\"in_text\" href=\"[[~692]]\" title=\"Bolo-Hauz complex\">Bolo-Hauz complex</a>&nbsp;(18 c).&nbsp;<a class=\"in_text\" href=\"[[~822]]\" title=\"Ulugbeg\">Ulugbeg</a>&nbsp;(15c),&nbsp;<a class=\"in_text\" href=\"[[~801]]\" title=\"Abdul Aziz\">Abdul Aziz</a>&nbsp;(17c) Madrassahs;&nbsp;<a class=\"in_text\" href=\"[[~770]]\" title=\"Chor-Minor Mausoleums\">Chor-Minor Mausoleums</a>.&nbsp;<a class=\"in_text\" href=\"[[~786]]\" title=\"Ismael Samani mausoleum\">Ismael Samani mausoleum</a>&nbsp;(9-10c)-vault of Bukhara rulers dynasty,&nbsp;<a class=\"in_text\" href=\"[[~804]]\" title=\"Chashma-Ayub Mausoleums\">Chashma-Ayub Mausoleums</a>&nbsp;(14 c)<br />\r\n			<strong>Dinner on self arrangement.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>DAY 5</p>\r\n\r\n			<p>04.05</p>\r\n			</td>\r\n			<td>\r\n			<h3>Bukhara — Khiva</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong><br />\r\n			Transfer to Khiva&nbsp;through Kyzyl-Kum desert, and stop over on bank of Amudarya river.</p>\r\n\r\n			<p><strong>Lunch on self arrangement.</strong><br />\r\n			Arrival to Bukhara and accommodation in hotel.<br />\r\n			<strong>Dinner on self arrangement.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>DAY 6</p>\r\n\r\n			<p>05.05</p>\r\n			</td>\r\n			<td>\r\n			<h3>Khiva</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong><br />\r\n			<strong><em>Sightseeing tour in Khiva:&nbsp;</em></strong><a class=\"in_text\" href=\"[[~716]]\" title=\"Kunya-Ark fortress\">Kunya-Ark fortress</a>, including Kurinish Khana (17c);&nbsp;<a class=\"in_text\" href=\"[[~830]]\" title=\"Mohammed-Amin-Khan\">Mohammed-Amin-Khan</a>(19c) Madrassah,&nbsp;<a class=\"in_text\" href=\"[[~761]]\" title=\"Islam Khodja Minaret\">Islam Khodja Minaret</a>;&nbsp;<a class=\"in_text\" href=\"[[~2160]]\" title=\"Palvan-Kari complex\">Palvan-Kari complex</a>,&nbsp;<a class=\"in_text\" href=\"[[~827]]\" title=\"Abd Al Bobo complex\">Abd Al Bobo complex</a>(18c),&nbsp;<a class=\"in_text\" href=\"[[~2165]]\" title=\"Rafanek Madrassah\">Rafanek Madrassah</a>,&nbsp;<a class=\"in_text\" href=\"[[~833]]\" title=\"Mohammed Rakim Khan\">Mohammed Rakim Khan</a>&nbsp;(19 c), Amir-Tur Madrassah,&nbsp;<a class=\"in_text\" href=\"[[~828]]\" title=\"Sho-I-Kalandar Bobo Mausoleum\">Sho-I-Kalandar Bobo Mausoleum</a>.</p>\r\n\r\n			<p><strong>Lunch on self arrangement.</strong><br />\r\n			<strong><em>Sightseeing tour in Khiva</em></strong>:&nbsp;<a class=\"in_text\" href=\"[[~831]]\" title=\"Mazary Sharif Madrassah\">Mazary Sharif Madrassah</a>, Kheivak well (10c), Uch Avlioli (“three saints”) Mausoleum,&nbsp;<a class=\"in_text\" href=\"[[~821]]\" title=\"Juma Mosque\">Juma Mosque</a>&nbsp;(18c),&nbsp;<a class=\"in_text\" href=\"[[~826]]\" title=\"Kutluq-Murad Inaq\">Kutluq-Murad Inaq</a>&nbsp;(19c) Madrassah,&nbsp;<a class=\"in_text\" href=\"[[~789]]\" title=\"Pakhlavan Mahmoud Mausoleum\">Pakhlavan Mahmoud Mausoleum</a>,&nbsp;<a class=\"in_text\" href=\"[[~772]]\" title=\"Tash Hauli (“stone yard”) Palace\">Tash Hauli (“stone yard”) Palace</a>,&nbsp;<a class=\"in_text\" href=\"[[~784]]\" title=\"Alla-Kuli-Khan\">Alla-Kuli-Khan</a>&nbsp;(18c),&nbsp;<a class=\"in_text\" href=\"[[~774]]\" title=\"Arab Mohammed Khan\">Arab Mohammed Khan</a>(18 c) Madrassahs.<br />\r\n			<strong>Dinner on self arrangement.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>DAY 7</p>\r\n\r\n			<p>06.05</p>\r\n			</td>\r\n			<td>\r\n			<h3>Khiva —&nbsp; &nbsp; Nukus —&nbsp;&nbsp;&nbsp; Muynak</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong><br />\r\n			Drive to Nukus (200km, 3h). Visit famous Savitsky museum on own. The second biggest collection of Russian avant-garde paintings collection after Hermitage in Saint-Petersburg.&nbsp;&nbsp;</p>\r\n\r\n			<p><strong>Lunch on self arrangement.</strong><br />\r\n			In the afternoon drive to Muynak (200km, 3h). Arrival late afternoon and accommodation in yurts.<br />\r\n			<strong>Dinner on self arrangement.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>DAY 8</p>\r\n\r\n			<p>07.05</p>\r\n			</td>\r\n			<td>\r\n			<h3>Muynak</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong></p>\r\n\r\n			<p>Participation in Stihia festival of Electronic Music.&nbsp;</p>\r\n\r\n			<p><strong>Lunch on self arrangement.</strong><br />\r\n			Participation in Stihia festival of Electronic Music.&nbsp;<br />\r\n			<strong>Dinner on self arrangement.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>DAY 9</p>\r\n\r\n			<p>08.05</p>\r\n			</td>\r\n			<td>\r\n			<h3>Muynak&nbsp;</h3>\r\n			</td>\r\n			<td>\r\n			<p><strong>Breakfast</strong></p>\r\n\r\n			<p>Participation in Stihia festival of Electronic Music.&nbsp;</p>\r\n\r\n			<p><strong>Lunch on self arrangement.</strong><br />\r\n			Participation in Stihia festival of Electronic Music.&nbsp;<br />\r\n			<strong>Dinner on self arrangement.</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>DAY 10&nbsp;</p>\r\n\r\n			<p>09.05</p>\r\n			</td>\r\n			<td>\r\n			<h3>Muynak —&nbsp; Urgench</h3>\r\n			</td>\r\n			<td>\r\n			<p>Night at 01:00 drive to Urgench airport (480km, 6h). Flight back home at 08:20 via Moscow.</p>\r\n\r\n			<p><strong><em>End of our service …</em></strong></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '38', 'App\Models\Tour');
INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('ru', 'route_lines', '<table class=\"table tour-table-rounded\">\r\n	<tbody>\r\n		<tr>\r\n			<th style=\"width:8%;\">День</th>\r\n			<th style=\"width:14%;\">Маршрут</th>\r\n			<th>Программа</th>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>ДЕНЬ 1</p>\r\n\r\n			<p>30.04</p>\r\n			</td>\r\n			<td>\r\n			<h3>Ташкент</h3>\r\n			</td>\r\n			<td>\r\n			<p>Прибытие в Ташкент и начало экскурсии.&nbsp;</p>\r\n\r\n			<p><strong style=\"box-sizing: border-box; color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">Экскурсия по Ташкенту:</strong><span style=\"color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">&nbsp;Старая часть города Ташкента: Посещение комплекса Хаст Имам, состоящего из Медресе Барак-Хана, мечети Тилля-шейха - хранилище знаменитого Корана Османа и золотого волоса пророка Мухаммада; Джума-мечеть Ходжа Ахрар Вали.</span><br style=\"box-sizing: border-box; color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\" />\r\n			<span style=\"color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">Посещение старогородского рынка «Чорсу».</span></p>\r\n\r\n			<p><strong style=\"box-sizing: border-box; color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">Свободный обед</strong><strong>.</strong></p>\r\n\r\n			<p>Размещение в гостинице после 14:00.<br />\r\n			<strong style=\"box-sizing: border-box; color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">Экскурсия по Ташкенту:&nbsp;</strong><span style=\"color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">Посещение музея искусств и музея прикладного искусства Узбекистана.</span></p>\r\n\r\n			<p><strong style=\"box-sizing: border-box; color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);\">Свободный ужин</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>ДЕНЬ 2</p>\r\n\r\n			<p>01.05</p>\r\n			</td>\r\n			<td>\r\n			<h3>Ташкент —&nbsp;Самарканд</h3>\r\n			</td>\r\n			<td>\r\n			<p style=\"box-sizing: border-box; margin: 0px 0px 9.5px; color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 15px;\"><strong style=\"box-sizing: border-box;\">Завтрак</strong></p>\r\n\r\n			<p style=\"box-sizing: border-box; margin: 0px 0px 9.5px; color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 15px;\">Переезд в Самарканд (310км, 4 часа езды).</p>\r\n\r\n			<p style=\"box-sizing: border-box; margin: 0px 0px 9.5px; color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 15px;\"><strong style=\"box-sizing: border-box;\">Экскурсия по Самарканду:</strong></p>\r\n\r\n			<p style=\"box-sizing: border-box; margin: 0px 0px 9.5px; color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 15px;\">Мавзолей Гур-Эмир (15век), Архитектурный ансамбль Регистан (включая Медресе Улугбека (15 век), Шер-Дор (17 век), Тилля-Кари (17 век)), Мечеть Биби-Ханум (15 век).</p>\r\n\r\n			<p style=\"box-sizing: border-box; margin: 0px 0px 9.5px; color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 15px;\">Размещение в гостинице после обеда.</p>\r\n\r\n			<p style=\"box-sizing: border-box; margin: 0px 0px 9.5px; color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 15px;\"><strong style=\"box-sizing: border-box;\">Свободный обед</strong></p>\r\n\r\n			<p style=\"box-sizing: border-box; margin: 0px 0px 9.5px; color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 15px;\">Обсерватория Улугбека (15 век), Святыня - захоронение&nbsp; Пророка Даниила, Музей археологических раскопок «Руины Афросиаба».</p>\r\n\r\n			<p style=\"box-sizing: border-box; margin: 0px 0px 9.5px; color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 15px;\"><strong style=\"box-sizing: border-box;\">Свободный ужин</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>ДЕНЬ&nbsp;3</p>\r\n\r\n			<p>02.05</p>\r\n			</td>\r\n			<td>\r\n			<h3>Самарканд —&nbsp;Бухара</h3>\r\n			</td>\r\n			<td>\r\n			<p style=\"box-sizing: border-box; margin: 0px 0px 9.5px; color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 15px;\"><strong style=\"box-sizing: border-box;\">Завтрак</strong></p>\r\n\r\n			<p style=\"box-sizing: border-box; margin: 0px 0px 9.5px; color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 15px;\"><span style=\"color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 15px;\">Переезд в Бухару</span><span style=\"color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 15px;\">&nbsp;(260 км, 4 часа в пути), Прибытие, размещение в гостинице.</span></p>\r\n\r\n			<p style=\"box-sizing: border-box; margin: 0px 0px 9.5px; color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 15px;\"><strong style=\"box-sizing: border-box;\">Свободный обед</strong></p>\r\n\r\n			<p style=\"box-sizing: border-box; margin: 0px 0px 9.5px; color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 15px;\"><span style=\"color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 15px;\">Свободное время.</span></p>\r\n\r\n			<p style=\"box-sizing: border-box; margin: 0px 0px 9.5px; color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 15px;\"><strong style=\"box-sizing: border-box;\">Свободный ужин</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>ДЕНЬ&nbsp;4</p>\r\n\r\n			<p>03.05</p>\r\n			</td>\r\n			<td>\r\n			<h3>Бухара</h3>\r\n			</td>\r\n			<td>\r\n			<p style=\"box-sizing: border-box; margin: 0px 0px 9.5px; color: rgb(51, 51, 51); font-family: Arial, sans-serif; fon