INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('es', 'description', '<p>Este viaje por tren por Uzbekistán ofrece una oportunidadincreíblepara descubrirtresgemas de Asia Central, como Tachkent, Bujaray Samarcanda consu ubicación geográfica ventajosaen el crucede la cultura medieval. En el caminode estastresciudades antiguasse desarrolló no sólo el comercio, sino había tambiénun activo intercambio deideas,culturas, tradiciones, religiones, artes y tecnologías.</p>', '2', 'App\\Models\\Tour');
