INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('fr', 'description', '\r\n<p>C\'est un circuit en train qui vous offre une chance incroyable de découvrir les perles de l\'Asie Centrale, tels que Tachkent, Boukhara et Samarcande avec leur situation géographique&nbsp;avantageuse au carrefour des cultures médiévales. Sur la route de ces trois villes antiques développé non seulement le commerce, mais aussi il y avait un échange actif des idées, de la culture, des traditions, des religions, de l\'artisanat et des technologies.</p>', '2', 'App\\Models\\Tour');
