INSERT INTO translations (locale, column_name, value, translatable_id, translatable_type) VALUES ('fr', 'description', '<p>Randonnée à dos de chameau en Ouzbékistan est l’un de voyage plus demandé et préfère par les voyageurs. Voyage à dos de chameau en Ouzbékistan vous permet de vous sentir étant abandonné pour revenir il y a plusieurs centaines d’années, l’équitation d’une caravane de chameaux sous le soleil chaud. Dans les déserts de l\'Ouzbékistan vous aurez l\'expérience mystérieuse et inoubliable de trekking l\'océan de sable pur avec des ombres de chameaux sur elle dans le coucher du soleil.</p>', '11', 'App\\Models\\Tour');
